INSERT INTO `php_plugin_model_task_result` VALUES (917, 1654, '{\"model\": 8, \"assistant\": 7, \"form_data\": {\"prompt\": \"中近景固定镜头。秦彻从跪拜的姿态缓缓起身，站定在青石阶上。他微微侧身，目光投向远处晨雾中若隐若现的玄清观山门，凝视片刻。山风吹动他额前的发丝和道袍的衣角。整个画面笼罩在清晨的薄雾和静谧的氛围中。\", \"last_image\": \"https://renloong-dev.oss-cn-chengdu.aliyuncs.com/uploads/storyboard_image/20251229/60918af698b87a8ba07ae107ca41f764_6951f718a48f9.png\", \"notify_url\": \"http://short-play.local.renloong.com/app/model/Notify/video\", \"first_image\": \"https://renloong-dev.oss-cn-chengdu.aliyuncs.com/uploads/storyboard_image/20251229/caecfa22fa0e0323f92e98cdbe750599_6951f4cacf6ff.png\", \"aspect_ratio\": \"9:16\", \"negative_prompt\": \"\"}}', '{\"url\": \"https://dashscope-result-sh.oss-cn-shanghai.aliyuncs.com/1d/9d/20251229/161b96d2/d469e58e-5fa0-4bd7-b5fc-866d221e565e.mp4?Expires=1767065822&OSSAccessKeyId=LTAI5tKPD3TMqf2Lna1fASuh&Signature=a8O6imqbt0bqBvApH7y%2BlqwWp8M%3D\", \"status\": \"success\", \"message\": null, \"task_id\": \"1000345\"}', NULL, 'https://dashscope-result-sh.oss-cn-shanghai.aliyuncs.com/1d/9d/20251229/161b96d2/d469e58e-5fa0-4bd7-b5fc-866d221e565e.mp4?Expires=1767065822&OSSAccessKeyId=LTAI5tKPD3TMqf2Lna1fASuh&Signature=a8O6imqbt0bqBvApH7y%2BlqwWp8M%3D', NULL, 'uploads/storyboard_video/20251229/97b8e0416cbf9d5eb8591007ffff9861_6951f7868dea1.mp4', NULL, NULL, NULL);
