INSERT INTO `php_plugin_model_task_result` VALUES (922, 1654, '{\"model\": 8, \"assistant\": 7, \"form_data\": {\"prompt\": \"中景，仰拍视角，固定镜头。青年道士秦彻站在老旧公寓楼前，他微微仰头，目光从楼底缓慢扫视至楼顶，仿佛在评估和寻找着什么。他的表情平静，但眼神专注。傍晚的微风吹动他道袍的下摆和束起的长发。背景中，远处商业区的霓虹灯开始亮起，与前景阴郁的公寓楼形成对比。\", \"last_image\": \"https://renloong-dev.oss-cn-chengdu.aliyuncs.com/uploads/storyboard_image/20251229/55be68a4dfad81e70ef8aac4410bccfe_6951f36cda976.png\", \"notify_url\": \"http://short-play.local.renloong.com/app/model/Notify/video\", \"first_image\": \"https://renloong-dev.oss-cn-chengdu.aliyuncs.com/uploads/storyboard_image/20251229/11b73090a93d3bbb1266b0ece17c95a6_6951f327bde70.png\", \"aspect_ratio\": \"9:16\", \"negative_prompt\": \"\"}}', '{\"url\": \"https://dashscope-result-sh.oss-cn-shanghai.aliyuncs.com/1d/d0/20251229/161b96d2/01fa6593-da6c-484d-8c91-3deff7c431eb.mp4?Expires=1767066301&OSSAccessKeyId=LTAI5tKPD3TMqf2Lna1fASuh&Signature=WkYAu8YMvx96FNvXfr1ow3kvKJ8%3D\", \"status\": \"success\", \"message\": null, \"task_id\": \"1000350\"}', NULL, 'https://dashscope-result-sh.oss-cn-shanghai.aliyuncs.com/1d/d0/20251229/161b96d2/01fa6593-da6c-484d-8c91-3deff7c431eb.mp4?Expires=1767066301&OSSAccessKeyId=LTAI5tKPD3TMqf2Lna1fASuh&Signature=WkYAu8YMvx96FNvXfr1ow3kvKJ8%3D', NULL, 'uploads/storyboard_video/20251229/1566466985676943e7b08cff564ab104_6951f9611b5a7.mp4', NULL, NULL, NULL);
