INSERT INTO `php_plugin_model_task_result` VALUES (970, 1654, '{\"model\": 8, \"assistant\": 7, \"form_data\": {\"prompt\": \"老旧公寓楼三楼楼道内景，傍晚，顶灯闪烁。镜头固定在中景。公寓门从内侧打开，柳如烟（妆容精致，身着米白色西装套裙，手拿名牌包）从门内走出，她看到站在楼道里的秦彻（身着藏青色道袍，背桃木剑），动作和表情明显停顿了一下，带着审视和好奇上下打量秦彻。秦彻停下脚步，平静地回视柳如烟，目光在她脸上短暂停留。两人进行简短对话。对话结束后，柳如烟侧身从秦彻身边走过，走向楼梯方向。秦彻的目光跟随她移动片刻，然后重新转向走廊深处。\", \"last_image\": \"https://renloong-dev.oss-cn-chengdu.aliyuncs.com/uploads/storyboard_image/20251229/8d2f9f1d25314e48f1779de0286ce362_6952014650de0.png\", \"notify_url\": \"http://short-play.local.renloong.com/app/model/Notify/video\", \"first_image\": \"https://renloong-dev.oss-cn-chengdu.aliyuncs.com/uploads/storyboard_image/20251229/196c8f04c19b722e5cbc4bbbf50c07f3_695201011270c.png\", \"aspect_ratio\": \"9:16\", \"negative_prompt\": \"\"}}', '{\"url\": \"\", \"status\": \"fail\", \"message\": \"\", \"task_id\": \"1000354\"}', NULL, NULL, NULL, NULL, NULL, NULL, '');
