INSERT INTO `php_plugin_model_task_result` VALUES (972, 1654, '{\"model\": 8, \"assistant\": 7, \"form_data\": {\"prompt\": \"中近景，平视，固定镜头。秦彻站在老旧公寓楼昏暗的楼道中，他微微侧身，目光从柳如烟离开的方向收回，转向走廊深处。他的眼神变得锐利而专注，凝视着尽头那扇贴着褪色福字的房门。楼道里寂静无声，只有远处隐约传来的城市噪音。\", \"last_image\": \"\", \"notify_url\": \"http://short-play.local.renloong.com/app/model/Notify/video\", \"first_image\": \"https://renloong-dev.oss-cn-chengdu.aliyuncs.com/uploads/storyboard_image/20251229/e9035fa8b3f87860e01594a97e4f2808_6951fb825cccb.png\", \"aspect_ratio\": \"9:16\", \"negative_prompt\": \"\"}}', '{\"url\": \"\", \"status\": \"fail\", \"message\": \"\", \"task_id\": \"1000356\"}', NULL, NULL, NULL, NULL, NULL, NULL, '');
INSERT INTO `php_plugin_model_task_result` VALUES (973, 1654, '{\"model\": 8, \"assistant\": 7, \"form_data\": {\"prompt\": \"中景侧跟拍镜头，跟随秦彻在狭窄昏暗的楼道中缓慢行走。他左手托着罗盘，指针持续微微颤动。他的目光在罗盘与前方走廊之间来回移动，神情专注。头顶的声控灯闪烁不定，光线随之明暗变化，营造出不安的氛围。秦彻的脚步沉稳而谨慎，道袍下摆随着步伐轻轻摆动。镜头平稳地跟随他的侧后方移动，经过几扇紧闭的、老旧的房门。\", \"last_image\": \"https://renloong-dev.oss-cn-chengdu.aliyuncs.com/uploads/storyboard_image/20251229/196c8f04c19b722e5cbc4bbbf50c07f3_695201011270c.png\", \"notify_url\": \"http://short-play.local.renloong.com/app/model/Notify/video\", \"first_image\": \"https://renloong-dev.oss-cn-chengdu.aliyuncs.com/uploads/storyboard_image/20251229/7111e544778bf6139b8456142f9864ab_6951f9a2c07da.png\", \"aspect_ratio\": \"9:16\", \"negative_prompt\": \"\"}}', '{\"url\": \"https://dashscope-result-sh.oss-cn-shanghai.aliyuncs.com/1d/67/20251229/841c8ebf/ed05ba06-70c1-4be9-a743-5bacc6e8294a.mp4?Expires=1767075585&OSSAccessKeyId=LTAI5tKPD3TMqf2Lna1fASuh&Signature=a6ssPQwEl5aaTBBUs5cPi741m9c%3D\", \"status\": \"success\", \"message\": null, \"task_id\": \"1000357\"}', NULL, 'https://dashscope-result-sh.oss-cn-shanghai.aliyuncs.com/1d/67/20251229/841c8ebf/ed05ba06-70c1-4be9-a743-5bacc6e8294a.mp4?Expires=1767075585&OSSAccessKeyId=LTAI5tKPD3TMqf2Lna1fASuh&Signature=a6ssPQwEl5aaTBBUs5cPi741m9c%3D', NULL, 'uploads/storyboard_video/20251229/84ebc2310b3727b73a699cfc47f4fb33_69521d967c63c.mp4', NULL, NULL, NULL);
