INSERT INTO `php_plugin_model_task_result` VALUES (977, 1654, '{\"model\": 4, \"assistant\": 6, \"form_data\": {\"images\": [\"https://renloong-dev.oss-cn-chengdu.aliyuncs.com/uploads/scene_image/20251229/474436ff7be77d8cb7f9fd6b1e737940_6951f0fd50bdb.png\", \"https://renloong-dev.oss-cn-chengdu.aliyuncs.com/uploads/actor_costume/20251229/5070c7634748b864ae2ba6f02d0bd535_6951ef4354dad.png\", \"https://renloong-dev.oss-cn-chengdu.aliyuncs.com/uploads/prop_image/20251226/9ed6b78094203f7dd185c1d5e7353e6e_694e5c5764d1b.png\", \"https://renloong-dev.oss-cn-chengdu.aliyuncs.com/uploads/prop_image/20251226/803100b745b019bb6f3f26d81a8c6cfc_694e5c7698832.png\"], \"prompt\": \"中景，平视镜头，固定机位。一位身穿洗白发旧藏青色道袍的青年道士（秦彻）跪在湿滑的门前的青石台阶上，他身后斜背着一把桃木剑，腰间系着一个鼓囊囊的黄色布褡裢。他正对着前方紧闭的、古朴肃穆的山门叩首。背景是晨雾缭绕的山林和一座若隐若现的古道观（玄清观）。清晨光线柔和，薄雾弥漫。\", \"notify_url\": \"http://short-play.local.renloong.com/app/model/Notify/draw\", \"aspect_ratio\": \"9:16\"}}', '{\"url\": \"https://file83.aitohumanize.com/file/5fbb051baaa44776a83d021868e50e03.png\", \"status\": \"success\", \"message\": null, \"task_id\": \"1000754\"}', 'https://file83.aitohumanize.com/file/5fbb051baaa44776a83d021868e50e03.png', NULL, NULL, NULL, 'uploads/storyboard_image/20251231/185336857f1a94011a03976d758ac55c_6954c174a7c87.png', NULL, NULL);
