INSERT INTO `php_plugin_model_task_result` VALUES (1177, 1654, '{\"model\": 8, \"assistant\": 7, \"form_data\": {\"prompt\": \"特写镜头，模拟柳如烟苏醒瞬间的主观视角。画面从一片模糊的、晃动的光斑和色块开始，逐渐聚焦。一个穿着青色道袍的年轻男子（秦彻）的侧脸轮廓在画面中变得清晰，他正专注地看着手中的银针。银针的针尾反射着阳光，泛出微弱的金色光晕。随着秦彻的动作，银针被缓缓从画面（眉心）抽离。背景是公园的树木和天空，光线柔和。整个镜头保持轻微的呼吸感和晃动感，模拟人刚恢复意识时的视觉状态。\", \"last_image\": \"https://renloong-dev.oss-cn-chengdu.aliyuncs.com/uploads/storyboard_image/20260106/62d62c5023cc32dd5c713a0b0a49402d_695c88bd7baa4.png\", \"notify_url\": \"http://short-play.local.renloong.com/app/model/Notify/video\", \"first_image\": \"https://renloong-dev.oss-cn-chengdu.aliyuncs.com/uploads/storyboard_image/20260106/3559f5798206780ffb31e5055b02f7f3_695c8913a6d7a.png\", \"aspect_ratio\": \"9:16\", \"negative_prompt\": \"\"}}', '{\"url\": \"https://dashscope-result-sh.oss-cn-shanghai.aliyuncs.com/1d/36/20260106/841c8ebf/a4b0ae93-36c9-4a23-af54-6acc346fceac.mp4?Expires=1767758897&OSSAccessKeyId=LTAI5tKPD3TMqf2Lna1fASuh&Signature=etqQmJwefaFvisaJgMz8O250nfE%3D\", \"status\": \"success\", \"message\": null, \"task_id\": \"1000379\"}', NULL, 'https://dashscope-result-sh.oss-cn-shanghai.aliyuncs.com/1d/36/20260106/841c8ebf/a4b0ae93-36c9-4a23-af54-6acc346fceac.mp4?Expires=1767758897&OSSAccessKeyId=LTAI5tKPD3TMqf2Lna1fASuh&Signature=etqQmJwefaFvisaJgMz8O250nfE%3D', NULL, 'uploads/storyboard_video/20260106/8415191be1c080b21d497fa2a65c1fec_695c8aca6b52f.mp4', NULL, NULL, NULL);
