INSERT INTO `php_plugin_model_task_result` VALUES (1508, 1654, '{\"model\": 4, \"assistant\": 2, \"form_data\": {\"images\": [null, null, null, null], \"prompt\": \"剧名：灵泉小农女：王爷的替身王妃;\\n简介：现代码农豌豆加班猝死，穿越古代成农家宠女，得灵泉空间。她救下失忆美男王爷，利用现代知识经商致富，却不知自己只是王爷白月光的替身。当阴谋与背叛接踵而至，她心死复仇，却发现穿越的使命竟是阻止他黑化。最终真相揭开，跨越时空的爱能否重来？;\\n画风：古风国漫;\\n\", \"notify_url\": \"http://short-play.local.renloong.com/app/model/Notify/draw\", \"aspect_ratio\": \"2:3\"}}', '{\"url\": \"\", \"status\": \"fail\", \"message\": null, \"task_id\": \"1001149\"}', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `php_plugin_model_task_result` VALUES (1509, 1654, '{\"model\": 4, \"assistant\": 2, \"form_data\": {\"images\": [null, null, null, null], \"prompt\": \"剧名：灵泉小农女：王爷的替身王妃;\\n简介：现代码农豌豆加班猝死，穿越古代成农家宠女，得灵泉空间。她救下失忆美男王爷，利用现代知识经商致富，却不知自己只是王爷白月光的替身。当阴谋与背叛接踵而至，她心死复仇，却发现穿越的使命竟是阻止他黑化。最终真相揭开，跨越时空的爱能否重来？;\\n画风：古风国漫;\\n\", \"notify_url\": \"http://short-play.local.renloong.com/app/model/Notify/draw\", \"aspect_ratio\": \"2:3\"}}', '{\"url\": \"\", \"status\": \"fail\", \"message\": null, \"task_id\": \"1001150\"}', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `php_plugin_model_task_result` VALUES (1510, 1654, '{\"uid\": \"1655\", \"params\": {\"model\": 2, \"assistant\": 4, \"form_data\": {\"actors\": \"林薇{695cc3317ca3b},林薇{695cc3511ae5e},林薇{695cc3682eb4c},林薇{695cc36830917},柳如烟{6933ec11cb698},芝士{692e5db0a3ddb}\", \"prompt\": \"【第1集】\\n\\n时间：晚上9点30分\\n地点：都市CBD写字楼、地铁车厢\\n\\n1. 写字楼外景 夜\\n\\n摩天大楼玻璃幕墙反射着城市的霓虹。大部分楼层的灯光已熄灭，只有零星几扇窗还亮着。\\n\\n2. 写字楼内走廊 夜\\n\\n高跟鞋敲击大理石地面的声音在空旷的走廊回响。\\n\\n林薇（28岁，互联网公司视觉设计师）背着通勤包，揉着酸痛的脖子，从一扇玻璃门后走出。她是这一层最后离开的人。\\n\\n她脸上带着明显的倦容，眼妆有些晕开，但眼神依然清澈。经过前台时，她顺手关掉了区域的照明开关。\\n\\n3. 电梯间 夜\\n\\n电梯门打开，林薇走进去，按下B1层（停车场）按钮。电梯下行时，她靠在轿厢壁上，闭上眼睛深吸一口气。\\n\\n手机在包里震动。\\n\\n她掏出手机，屏幕亮起——是母亲发来的微信消息。\\n\\n【特写】手机屏幕：\\n“薇薇，睡了吗？妈妈同事李阿姨给你介绍了个男孩子，在国企工作，32岁，有房有车，照片发你了，你看看？”\\n下方附着一张男性照片。\\n\\n林薇盯着屏幕看了三秒，拇指悬在键盘上方，最终没有回复。\\n\\n她按下锁屏键，屏幕暗下去，映出她疲惫的脸。\\n\\n4. 地铁站台 夜\\n\\n林薇从扶梯走下，站台上乘客稀少。晚风穿过通道，吹起她肩上的发丝。\\n\\n列车进站，车门打开。\\n\\n5. 地铁车厢 夜\\n\\n车厢里只有零星几个乘客：一个戴着耳机看视频的年轻人，一对依偎着打盹的情侣，一个抱着公文包打哈欠的中年男人。\\n\\n林薇走到车厢连接处，靠着栏杆。\\n\\n窗外，城市的流光飞速向后掠去——广告牌、路灯、高架桥上的车灯，连成一片流动的光带。\\n\\n玻璃窗上倒映出她的侧影：精致的职业装，一丝不苟的妆容，但眼神空洞，望着窗外不知在想什么。\\n\\n她握紧手机，指节微微发白。\\n\\n【画外音】（林薇内心独白，轻声）：\\n“又是这样的一天……明天还要交第三版方案。”\\n\\n列车轻微晃动，她调整了一下站姿，将脸转向窗外。\\n\\n【镜头缓缓推进】\\n车窗倒影中，她的眼角似乎有些湿润，但她很快眨了眨眼，恢复了平静。\\n\\n列车广播响起：“下一站，幸福里站，请准备下车的乘客……”\\n\\n林薇抬起头，整理了一下衣领，准备下车。\\n\\n【本集完】\", \"episode_no\": 1, \"scene_list\": \"外景·都市CBD写字楼外·深夜·晴朗,内景·写字楼内部走廊·深夜·无,内景·写字楼电梯轿厢内·深夜·无,内景·地铁站站台·深夜·无,内景·地铁列车车厢内·深夜·无\", \"description\": \"车厢里只有零星几个乘客。林薇走到车厢连接处，靠着栏杆。窗外，城市的流光飞速向后掠去。玻璃窗上倒映出她的侧影。列车轻微晃动。列车广播响起：“下一站，幸福里站，请准备下车的乘客……”林薇抬起头，整理了一下衣领，准备下车。\", \"episode_num\": 2, \"storyboard_num\": 5, \"storyboard_sum\": 5, \"episode_duration\": 60, \"next_description\": \"\", \"previous_dialogues\": \"\", \"next_episode_content\": \"【第2集】\\n\\n时间：晚上10点15分\\n地点：老小区楼道、林薇租住的公寓\\n\\n1. 小区外景 夜\\n\\n镜头从远处推进：一片建于上世纪90年代的老式居民楼，外墙有些斑驳，但家家户户窗户透出温暖的灯光。\\n\\n楼下的绿化带里，几只流浪猫在垃圾桶旁觅食。远处传来隐约的电视声和炒菜声。\\n\\n2. 单元楼道 夜\\n\\n林薇背着通勤包走进单元门。\\n\\n楼道里光线昏暗，声控灯反应迟钝。她踩了跺脚，灯亮了——是那种老式的黄色灯泡，光线昏黄。\\n\\n【特写】她的高跟鞋踩在水泥台阶上，发出清脆的“嗒嗒”声。\\n\\n走到二楼时，灯灭了。她又踩脚，灯再次亮起，但这次闪烁了几下才稳定。\\n\\n墙上贴着各种小广告：疏通管道、宽带办理、房屋出租……还有社区通知栏里褪色的防疫宣传海报。\\n\\n3. 三楼走廊 夜\\n\\n林薇走到301室门前。\\n\\n门上贴着一个褪色的福字，边缘已经卷起。她从包里翻找钥匙，钥匙串上挂着公司门禁卡、一个小兔子挂件和两把钥匙。\\n\\n【特写】她的手有些颤抖——不是害怕，而是疲惫导致的肌肉疲劳。\\n\\n钥匙插入锁孔，转动。\\n\\n“咔哒”一声，门开了。\\n\\n4. 公寓玄关 夜\\n\\n门内一片漆黑。\\n\\n林薇伸手在墙边摸索，找到开关，“啪”地按下去。\\n\\n暖黄色的灯光瞬间充满玄关。\\n\\n这是一个小小的空间：右手边是鞋柜，上面放着一个藤编收纳篮，里面装着钥匙、零钱等杂物；左手边墙上挂着一面全身镜，镜框是原木色的。\\n\\n5. 公寓客厅 夜\\n\\n林薇走进客厅，随手将通勤包扔在沙发上——那是一个米白色的布艺沙发，上面散落着几个几何图案的抱枕。\\n\\n她弯腰，用一只手扶着鞋柜，另一只手解开高跟鞋的搭扣。\\n\\n【特写】脚后跟有一处被磨红的痕迹——那是新鞋留下的印记。\\n\\n她将两只高跟鞋并排放在鞋柜下层，光脚踩在木地板上。\\n\\n冰凉的地板让她轻轻吸了口气，但随即，一种解放感涌上来。\\n\\n她站直身体，长长地、深深地舒了一口气——那口气仿佛憋了一整天。\\n\\n肩膀放松下来，背脊也不再挺得那么直。\\n\\n6. 客厅全景 夜\\n\\n镜头缓缓扫过这个一室一厅的小公寓：\\n\\n- 客厅不大，但布置得温馨整洁\\n- 沙发对面是一个原木色的电视柜，上面摆着一台55寸电视，旁边放着几盆绿萝和龟背竹\\n- 墙上挂着几幅她自己设计的装饰画：抽象的城市天际线、色块拼接的几何图案\\n- 阳台的落地窗前，白色的纱帘轻轻飘动——窗户开了一条缝，夜风吹进来\\n- 小餐桌上铺着格子桌布，上面放着一个玻璃花瓶，插着几支干花\\n\\n林薇光脚走到餐桌旁，拿起遥控器，打开了电视。\\n\\n电视里正在播放晚间新闻，声音不大，刚好让房间不那么寂静。\\n\\n她走到冰箱前，打开门——冷光照亮她的脸。\\n\\n冰箱里东西不多：几瓶矿泉水、酸奶、鸡蛋、一些蔬菜。她拿出一瓶水，拧开喝了一大口。\\n\\n7. 林薇特写 夜\\n\\n她靠在冰箱旁，环顾这个完全属于自己的小空间。\\n\\n脸上的疲惫依然明显，但眼神里多了一丝柔软——那是回到家才有的放松。\\n\\n窗外传来远处汽车的鸣笛声，楼下有邻居回家的关门声，但这些声音都隔着一层，像是背景音。\\n\\n她将水瓶放在餐桌上，走到沙发边，整个人陷进去。\\n\\n闭上眼睛，三秒钟。\\n\\n然后睁开，目光落在茶几上——那里放着一本翻到一半的设计杂志，还有一支用了一半的护手霜。\\n\\n【画外音】（林薇内心独白，轻声）：\\n“终于……回来了。”\\n\\n她伸手拿起护手霜，挤了一些在手背上，慢慢揉开。\\n\\n8. 公寓全景 夜（固定镜头）\\n\\n林薇坐在沙发上，电视的光在她脸上明明灭灭。\\n\\n她保持着那个姿势，一动不动，像是在积蓄力量，又像是在放空自己。\\n\\n窗外的城市灯火依旧璀璨，但这间小屋里，时间仿佛慢了下来。\\n\\n【镜头缓缓拉远】\\n透过窗户，可以看到她小小的身影，在偌大的城市里，拥有这一方属于自己的天地。\\n\\n【本集完】\", \"previous_description\": \"林薇从扶梯走下，站台上乘客稀少。晚风穿过通道，吹起她肩上的发丝。列车进站，车门打开。\"}}, \"task_id\": \"local-697188ae7a77c\", \"drama_id\": \"1000011\", \"episode_id\": \"1000130\", \"channels_uid\": 1654}', '{\"prop\": [{\"id\": \"\", \"name\": \"地铁车厢连接处栏杆\", \"description\": \"地铁车厢连接处的不锈钢栏杆，表面光滑，反射着车厢内的灯光。\"}, {\"id\": \"\", \"name\": \"地铁车窗\", \"description\": \"干净的地铁车窗，窗外是飞速掠过的城市夜景光带，窗上倒映出车厢内乘客的侧影。\"}], \"actor\": [{\"name\": \"林薇\", \"actor_id\": \"695cc3317ca3b\", \"description\": null}], \"duration\": \"10000\", \"dialogues\": [{\"actor\": {\"name\": \"列车广播\", \"actor_id\": \"\", \"description\": \"无具体形象，为系统提示音。\"}, \"content\": \"下一站，幸福里站，请准备下车的乘客……\", \"emotion\": \"neutral\", \"end_time\": \"9000\", \"start_time\": \"7000\", \"prosody_speed\": \"1.0\", \"prosody_volume\": \"50\", \"inner_monologue\": false}], \"narration\": \"\", \"shot_type\": \"中景\", \"shot_angle\": \"侧拍\", \"shot_motion\": \"固定\", \"image_prompt\": \"一位穿着精致职业装的亚洲女性（林薇）侧身靠在地铁车厢连接处的不锈钢栏杆上，她望向车窗外，车窗上倒映出她疲惫而精致的侧脸。车厢内光线柔和，只有零星几个乘客。窗外是飞速向后流动的城市夜景，广告牌、路灯和高架桥的车灯连成一片模糊的光带。构图稳定，焦点在林薇和车窗倒影上。\", \"video_prompt\": \"固定中景镜头，侧拍角度。林薇（28岁，互联网公司视觉设计师）穿着职业装，背靠地铁车厢连接处的栏杆，望着窗外。窗外城市的流光（广告牌、路灯、车灯）持续高速向后飞掠，形成动态的光带。车窗玻璃上清晰倒映出林薇的侧影。列车轻微晃动，林薇的身体随之有轻微的起伏。几秒钟后，列车广播响起，林薇闻声抬起头，整理了一下自己的衣领，准备下车。\"}', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
