INSERT INTO `php_plugin_shortplay_actor` VALUES (1, 1654, NULL, NULL, NULL, '秦彻', '692e5d7f68110', 'human', 'male', 'young', '23岁，大学刚毕业，外形阳光干净的理工科男生。身材修长，穿着简单清爽，笑容明朗，给人可靠亲近的第一印象。', 'uploads/actor_image/20251218/6da2b73b9b7593bdc626f57061ba6cd7_69436edba9953.png', 'uploads/actor_three_view_image/20251218/572f4527a00971902ee4e6a0c804931e_69436efe854d2.png', 'generated', '2025-12-02 11:23:10', '2025-12-18 11:03:27', NULL);
INSERT INTO `php_plugin_shortplay_actor` VALUES (4, 1654, NULL, NULL, NULL, '芝士', '692e5db0a3ddb', 'animal', 'female', 'teenager', '美国短毛猫，3岁，母猫。体型匀称偏圆，毛色经典虎斑，纹路清晰，对比度高，眼睛大而明亮。', 'uploads/actor_image/20251223/3f2fee446c2cb886a2dcc802ec630dff_694a5753efd70.png', 'uploads/actor_three_view_image/20251223/78b3c981bb9c2e35b1f5ab3210a49c35_694a5790d5020.png', 'generated', '2025-12-02 11:26:41', '2025-12-23 16:49:22', NULL);
INSERT INTO `php_plugin_shortplay_actor` VALUES (5, 1654, NULL, NULL, NULL, '柳如烟', '6933ec11cb698', 'human', 'female', 'young', '22岁，刚从重点大学毕业的校花级人物。外形清秀明艳，五官柔和却极具辨识度，长发自然垂落，气质干净纯粹。性格外柔内韧，看似温和安静，实则自尊心强，有清晰的自我边界。', 'uploads/actor_image/20251223/b20cee6e3a189d0c6190d55617959e14_694a493f72038.jpg', 'uploads/actor_three_view_image/20251223/8b73760e4944ae487255b81f760e3adc_694a499deb1ac.png', 'generated', '2025-12-06 16:40:37', '2025-12-23 15:49:51', NULL);
INSERT INTO `php_plugin_shortplay_actor` VALUES (427, 1654, 1653, NULL, NULL, '倩女', '694a5be81b004', 'ghost', 'female', 'young', '鹅蛋脸，眉峰清晰的“小倩眉”，深邃的杏眼（下眼睑微红），高鼻梁，饱满的“琉光唇”。乌黑长发如瀑，两侧束部分头发，常以白丝带或素银簪固定，几缕散发随风飘动。主体为白色或浅青色的古装长裙，外层罩半透明轻纱，衣袂与飘带极具动态。身形修长，姿态如风中垂柳；神情常带忧伤，但眼波流转间有勾魂摄魄的瞬间。', 'uploads/actor_image/20251223/76fb6d88370f4a706a49cd5dc545a28d_694a5c1cc6e00.png', 'uploads/actor_three_view_image/20251223/3c8f8066f4ee3b7d17d9b0f61857296c_694a5c58881b4.png', 'generated', '2025-12-23 17:07:52', '2025-12-23 17:09:45', NULL);
INSERT INTO `php_plugin_shortplay_actor` VALUES (696, 1654, 1655, 1000011, NULL, '烧烤摊老板老王', '695b9ac78ca91', 'human', 'male', 'middle_age', '中年男性，体型微胖，面容和善常带笑容。穿着沾有些许油渍的白色汗衫和深色宽松短裤，系着一条深色围裙。头发短而稀疏，额头有汗。在烧烤炉前忙碌时，动作熟练，火光映照着他红润的脸庞和专注的眼神。与顾客交谈时语气热情，带有市井的亲切感。', NULL, NULL, 'initializing', '2026-01-05 19:04:40', '2026-01-05 19:04:40', NULL);
INSERT INTO `php_plugin_shortplay_actor` VALUES (697, 1654, 1658, 1000012, NULL, '豌豆', '6968d2b36ce2e', 'human', 'female', 'young', '面容清秀，眼神灵动坚韧，常带一丝现代人的疏离与审视。初期穿着打补丁的粗布衣裙，难掩憔悴；后期随着事业发展，衣着从素雅棉麻渐变为精致绸缎，发髻简单利落，点缀少量珠翠。表情多变，思考时微蹙眉，反击时眼神锐利，伤心时强忍泪水。姿态挺拔，带有不自觉的现代人仪态。', 'uploads/actor_image/20260105/3b3bfdb191a829201014ae8055ab6c8d_695b9e21c7ea6.png', 'uploads/actor_three_view_image/20260105/f70e371c7ad9ce806da5e0e9a3d26efb_695b9e58ab6ae.png', 'generated', '2026-01-05 19:06:49', '2026-01-21 12:02:40', NULL);
INSERT INTO `php_plugin_shortplay_actor` VALUES (698, 1654, 1658, 1000012, NULL, '萧绝/阿绝1', '695b9b48c239a', 'human', 'male', 'young', '容貌绝世，眉如墨画，眼若寒星，鼻梁高挺，唇色偏淡。受伤时脸色苍白，脆弱感十足；正常时气质清冷矜贵，不怒自威。衣着从初遇时的破烂血衣，到山村时期的粗布素衣，难掩风华；恢复身份后，多着玄色或月白色锦袍，腰束玉带，身形挺拔如松。眼神深邃，看豌豆时初期带有算计与恍惚（透过她看别人），后期充满挣扎与深情。', 'uploads/actor_image/20260105/4be27b5a2ebc9649dc6c8a6bb53fe076_695b9eb7998a8.png', 'uploads/actor_three_view_image/20260105/1570f470d5627502be763b887f39dacc_695b9efe32842.png', 'generated', '2026-01-05 19:06:49', '2026-01-21 16:39:56', NULL);
INSERT INTO `php_plugin_shortplay_actor` VALUES (699, 1654, 1658, 1000012, NULL, '苏映月', '695b9b48c2ce3', 'human', 'female', 'young', '瑞王妃，萧绝的白月光。容貌与豌豆有五六分相似，但更柔美娇弱，柳叶眉，秋水眸，肤白如瓷，唇点朱丹。常着淡雅宫装，举止端庄温婉，说话轻声细语，眼神却偶尔流露出偏执与哀怨。手指纤细，喜抚琴，袖中常藏一方绣着“绝”字的旧帕。', NULL, NULL, 'initializing', '2026-01-05 19:06:49', '2026-01-05 19:06:49', NULL);
INSERT INTO `php_plugin_shortplay_actor` VALUES (700, 1654, 1658, 1000012, NULL, '嘉仪县主', '695b9b48c3686', 'human', 'female', 'teenager', '长公主之女，骄纵跋扈。圆脸杏眼，妆容艳丽，喜着大红、宝蓝等鲜艳宫装，满头珠翠。表情丰富，得意时挑眉撇嘴，生气时跺脚瞪眼，看豌豆时充满不屑与嫉妒。行为张扬，身边总跟着一群丫鬟婆子。', NULL, NULL, 'initializing', '2026-01-05 19:06:49', '2026-01-05 19:06:49', NULL);
