INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (79, 1654, 1653, 1000013, 1000164, '秦彻告别师父下山', '外景', '终南山道观外山道', '清晨', '薄雾', '一条蜿蜒的古老山道，石阶被晨雾笼罩，两旁是苍翠的松柏。一座古朴的道观在山道尽头若隐若现。整体环境清幽、空灵，充满出世之感。', NULL, 1, 'uploads/scene_image/20260106/d448a95605f5fd7ae44f9c0737c73d08_695c7397030b7.png', '2026-01-06 10:26:54', '2026-01-13 15:30:07');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (80, 1654, 1653, 1000013, 1000164, '秦彻初入都市公园', '外景', '都市公园入口及周边', '下午', '晴天', '现代都市公园的入口，背景是车水马龙的街道和高耸的玻璃幕墙写字楼。公园内绿树成荫，有步道和草坪。穿着时尚的人群来来往往，环境嘈杂而充满现代都市气息。', NULL, 3, 'uploads/scene_image/20260106/0bcb3a1124452d0b36f28d0d89851243_695c7392bf2e1.png', '2026-01-06 10:26:54', '2026-01-06 10:29:40');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (81, 1654, 1653, 1000013, 1000164, '秦彻公园长椅施救橘猫', '外景', '公园内长椅及灌木丛区域', '下午', '晴天', '公园内一处安静角落，有一条老旧木质长椅，椅子前是人行横道，橡胶跑道，旁边是茂密的灌木丛。阳光透过树叶洒下斑驳光影，环境相对僻静。', NULL, 4, 'uploads/scene_image/20260122/52c3d8c3cfe31c03655449adf1c70e63_69718b072a8f0.png', '2026-01-06 10:26:54', '2026-01-22 10:29:25');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (82, 1654, 1653, 1000013, 1000164, '柳如烟遭术士暗算', '外景', '公园林荫道', '下午', '晴天', '公园内一条宽阔的林荫道，两旁是高大的行道树，形成一条绿色长廊。道路整洁，有行人漫步。一个穿黑色唐装的男人躲在远处一棵树后。', NULL, 5, 'uploads/scene_image/20260106/efeb30c91588f2daa8dbaee93e132b71_695c73921bd6d.png', '2026-01-06 10:26:54', '2026-01-06 10:29:39');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (83, 1654, 1653, 1000013, 1000164, '秦彻施针救人后逃离', '外景', '柳如烟倒地的林荫道位置', '下午', '晴天', '林荫道上的一个具体点位，柳如烟瘫软在地。两名黑衣保镖从远处跑来。周围有稀疏的行人。树后术士的位置。', NULL, 6, 'uploads/scene_image/20260106/7b3b8c6c6f76af95134c50f5205f9235_695c7397a8f49.png', '2026-01-06 10:26:54', '2026-01-06 10:29:45');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (84, 1654, 1653, 1000013, 1000164, '秦彻天桥下摆摊', '外景', '城市天桥下人行道', '傍晚', '傍晚天色', '一座钢筋混凝土天桥下方的人行道，桥体投下阴影。地面铺着一张写着“卜卦问事”的粗布，旁边摆着几枚铜钱。行人匆匆走过，背景是都市傍晚的车流和霓虹初上的景象。', NULL, 7, 'uploads/scene_image/20260113/1d567326d4f4c6bd7daf93d1fc2dadba_69660592ecd07.png', '2026-01-06 10:26:54', '2026-01-13 16:43:00');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (107, 1654, 1658, 1000012, 1000123, '豌豆山洞发现灵泉催生能力', '外景', '后山隐蔽山洞外的小溪边', '白天', '晴天', '山林深处，一处隐蔽山洞外的清澈溪流旁。周围是茂密的植被和岩石，阳光透过树叶缝隙洒下斑驳光影，环境静谧。', NULL, 1, 'uploads/scene_image/20260106/ec178d227afeee6b68e612ac2d3819f7_695cf3a3b1330.png', '2026-01-06 19:29:57', '2026-01-06 19:36:05');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (108, 1654, 1658, 1000012, 1000123, '豌豆家晚餐与家人团聚', '外景', '豌豆家破旧的小院', '傍晚', '晴天', '一个破旧但整洁的农家小院，中央摆放着一张旧木桌。桌上摆着几碗稠粥和一碟鲜嫩的野菜。夕阳余晖为院子镀上一层暖色，气氛温馨。', NULL, 2, 'uploads/scene_image/20260106/843226766201f0f5dec17d847497ccdf_695cf39987d79.png', '2026-01-06 19:29:57', '2026-01-06 19:35:54');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (109, 1654, 1658, 1000012, 1000123, '后山采药偶遇伤者', '外景', '后山郁郁葱葱的山林', '白天', '晴天', '一片茂密的山林，植被繁盛，阳光透过树冠形成光柱。豌豆背着竹篓在其中穿行采药。', NULL, 3, 'uploads/scene_image/20260113/80bdcac7bfd84f13a372835686a8b430_69662f965361d.png', '2026-01-06 19:29:57', '2026-01-13 19:42:15');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (110, 1654, 1658, 1000012, 1000123, '斜坡下发现重伤的萧绝', '外景', '山林中的一处斜坡下', '白天', '晴天', '一处山林斜坡的底部，周围散落着乱石。斜坡上长有杂草和灌木。萧绝蜷缩在地，锦衣破烂，身上有血迹。', NULL, 4, 'uploads/scene_image/20260106/122ddeb4906537ebc66a400c44669f43_695cf39e325f1.png', '2026-01-06 19:29:57', '2026-01-06 19:35:59');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (111, 1654, 1658, 1000012, 1000123, '豌豆救助并喂水给萧绝', '外景', '斜坡下的乱石堆旁', '白天', '晴天', '斜坡底部靠近萧绝倒地的具体位置。地面不平，有碎石和泥土。萧绝躺在地上，脸色苍白。', NULL, 5, 'uploads/scene_image/20260113/79d6e0a841ae48df45ed1ffc38d258e6_69662feb316b0.png', '2026-01-06 19:29:57', '2026-01-13 19:43:40');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (112, 1654, 1658, 1000012, 1000123, '萧绝抓住豌豆衣角求助', '外景', '斜坡下的乱石堆旁', '傍晚', '晴天，夕阳余晖', '同上一场景地点，时间推移至傍晚。夕阳的橙红色光芒斜照进林间，光线变得柔和。萧绝仍躺在地上。', NULL, 6, 'uploads/scene_image/20260113/bdba5f59fc164ec346908e081f31004c_69662fb3da0d6.png', '2026-01-06 19:29:57', '2026-01-13 19:42:45');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (113, 1654, 1658, 1000012, 1000123, '山洞内安置萧绝并生火', '内景', '后山一处隐蔽的山洞内部', '夜晚', '晴朗的夜晚', '一个天然形成的山洞内部，空间不大。洞壁是粗糙的岩石，地面相对平整。洞内生着一小堆篝火，火光摇曳，照亮洞壁和靠在石壁上的萧绝。竹筒水和几个野果放在他身边。', NULL, 7, 'uploads/scene_image/20260106/69632c5333095171fc0a82bb9c04371e_695cf3ac6d2ea.png', '2026-01-06 19:29:57', '2026-01-06 19:36:13');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (114, 1654, 1658, 1000012, 1000123, '豌豆月夜归家心神不宁', '外景', '从后山返回豌豆家途中的山林小路', '夜晚', '晴朗的月夜', '一条蜿蜒穿过山林的小路。月光清冷，洒在路面和周围的树木上，投下斑驳的树影。环境安静，只有虫鸣。', NULL, 8, 'uploads/scene_image/20260106/a878bb0a848c345f456eba73da839c57_695cf38a6494e.png', '2026-01-06 19:29:57', '2026-01-06 19:35:39');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (115, 1654, 1653, 1000013, 1000164, '秦彻施针救人后逃离', '外景', '柳如烟倒地的林荫道位置', '下午', '晴天', '林荫道上的一个具体点位，柳如烟瘫软在地。两名黑衣保镖从远处跑来。周围有稀疏的行人。树后术士的位置。', NULL, 2, 'uploads/scene_image/20260106/7b3b8c6c6f76af95134c50f5205f9235_695c7397a8f49.png', '2026-01-07 15:02:55', '2026-01-07 15:02:55');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (121, 1654, 1653, 1000013, 1000165, '秦彻天桥下摆摊为张伟解困', '外景', '城市天桥下', '夜晚', '晴朗', '城市天桥下的一角，光线昏暗，主要光源来自远处路灯的散射光。秦彻盘腿坐在一块铺开的粗布上，布上放着几枚铜钱和一个旧布包。他身穿洗得发白的道袍。摊位前冷清，旁边有零星路人经过。整体氛围安静而略带萧瑟。', NULL, 1, 'uploads/scene_image/20260107/5249d5e54bb5946e4fd3ba5d229c042d_695e3393dcc22.png', '2026-01-07 17:14:25', '2026-01-07 18:21:09');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (122, 1654, 1653, 1000013, 1000165, '张伟办公室风水摆件特写', '内景', '写字楼办公室', '白天', '晴朗', '一个现代风格的办公室工位。工位西北角摆放着一个深蓝色的陶瓷招财流水摆件，水声潺潺。办公室内光线明亮，环境整洁。', NULL, 2, 'uploads/scene_image/20260121/083c425a24a781e43915a5de72a8d72d_6970624e73e41.png', '2026-01-07 17:14:25', '2026-01-21 13:21:19');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (123, 1654, 1653, 1000013, 1000165, '柳如烟在书房研究老宅异状', '内景', '柳家别墅书房', '夜晚', '晴朗', '一间宽敞、装修典雅的书房。柳如烟穿着家居服坐在书桌前，桌上摊开着老宅的建筑图纸和几张显示水渍、墙皮剥落的照片。室内灯光柔和，气氛凝重。', NULL, 3, 'uploads/scene_image/20260121/eeb9cd11542c6d845f61b72c5752a023_6970625d26333.png', '2026-01-07 17:14:25', '2026-01-21 13:21:34');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (124, 1654, 1653, 1000013, 1000165, '柳如烟办公室获知秦彻消息', '内景', '柳如烟办公室', '白天', '晴朗', '一间现代化、视野开阔的CEO办公室。柳如烟坐在办公桌后，助理林薇站在桌前汇报。办公室内光线充足，陈设简洁大气。', NULL, 4, 'uploads/scene_image/20260121/37d337dc967153bd29d20c9ea9f96175_697062dfa7101.png', '2026-01-07 17:14:25', '2026-01-21 13:23:44');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (125, 1654, 1653, 1000013, 1000165, '柳如烟在天桥下发现秦彻', '外景', '城市天桥下', '傍晚', '晴朗', '同一天桥下地点，时间变为傍晚，天色渐暗，华灯初上。秦彻的摊位前围着五六个人，他正在为一位老太太看手相。摊位粗布上多了一小袋馒头。整体氛围比夜晚热闹一些。', NULL, 5, 'uploads/scene_image/20260121/37ad6cffdb3f89fb839113af3cab6b92_6970624f424e3.png', '2026-01-07 17:14:25', '2026-01-21 13:21:20');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (129, 1654, 1658, 1000012, 1000122, '豌豆深夜加班突发意外', '内景', '现代写字楼工位', '深夜', '无', '一个现代风格的开放式办公室，深夜时分，只有一盏台灯照亮一个凌乱的工位。工位上有一台笔记本电脑，屏幕亮着，显示着代码界面。桌面上散落着文件、空咖啡杯和零食包装。整体环境安静、压抑，光线昏暗，只有屏幕光和台灯光源。', NULL, 1, 'uploads/scene_image/20260115/b4daa971b3600161b622b94d5f01d046_6968cfb09ca03.png', '2026-01-12 19:06:06', '2026-01-15 19:29:53');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (130, 1654, 1658, 1000012, 1000122, '豌豆在古代农家土屋苏醒', '内景', '古代农家土屋内', '清晨', '无', '一间简陋的古代农家土屋内部。茅草屋顶，土墙上有裂缝。屋内家徒四壁，只有一张破旧的木桌，两个粗糙的木凳，以及一张硬板床。床上铺着打补丁的粗布被褥。清晨的光线从门或小窗透入，照亮空气中的微尘。整体环境贫穷、陈旧。', NULL, 2, 'uploads/scene_image/20260115/a69ece4d72e7853943be9e4055b85477_6968d04becda8.png', '2026-01-12 19:06:06', '2026-01-15 19:32:29');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (131, 1654, 1658, 1000012, 1000122, '农家小院家人关怀', '外景', '农家小院内', '白天', '晴天', '一个破败的古代农家小院。院子由歪斜的篱笆围成，地面是夯实的泥土。院子里有几只稀疏的鸡鸭。院中有一把唯一完好的竹椅。整体环境简陋但充满生活气息，阳光明媚。', NULL, 3, 'uploads/scene_image/20260115/ecff614b376da406257118b90404cec8_6968cfb146f04.png', '2026-01-12 19:06:06', '2026-01-15 19:29:54');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (132, 1654, 1658, 1000012, 1000122, '后山树林发现异能', '外景', '后山树林', '午后', '晴天', '一片茂密的古代山林，树木高大，阳光透过树叶缝隙洒下斑驳的光点。地面上有杂草、灌木和散落的石子。环境宁静，充满自然气息。', NULL, 4, 'uploads/scene_image/20260115/98ac82b49b44a945da6ba0642b748708_6968cfb64d141.png', '2026-01-12 19:06:06', '2026-01-15 19:29:59');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (133, 1654, 1658, 1000012, 1000122, '山林深处遇神秘伤者', '外景', '山林深处', '傍晚', '晴天', '山林更深处，灌木丛生，光线因傍晚而变得柔和，染上金色。夕阳西下，暮色开始笼罩。', NULL, 5, 'uploads/scene_image/20260115/2284a52351d5fcb9566dd81bfc4e7866_6968d0b9e6200.png', '2026-01-12 19:06:06', '2026-01-15 19:34:19');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (134, 1654, 1653, 1000013, 1000164, '天桥下', '外景', '城市天桥下人行横道', '晚上', '晴', '一座钢筋混凝土天桥下方的人行道，桥体投下阴影。地面铺着一张写着“卜卦问事”的粗布，旁边摆着几枚铜钱。行人匆匆走过，背景是都市傍晚的车流和霓虹初上的景象。', '', 8, 'uploads/scene_image/20260113/3540701a7e47bf883bc1f629595df306_696604ca7f641.png', '2026-01-13 15:32:05', '2026-01-13 16:39:39');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (136, 1654, 1654, 1000014, 1000188, '机房服务器过载警报', '内景', '现代数据中心机房', '深夜', '无', '一个充满现代科技感的服务器机房内部。背景是成排的黑色服务器机柜，机柜上密布着闪烁的蓝色和绿色指示灯。房间光线昏暗，主要光源来自屏幕和设备指示灯。中央控制台前，多块大尺寸显示屏上滚动着复杂的代码和数据流。空气中弥漫着低沉的机器嗡鸣声。', NULL, 1, 'uploads/scene_image/20260113/85b6e6c8c59c8ef9641f63bbffd67708_69662f5ede160.png', '2026-01-13 19:32:03', '2026-01-13 19:41:19');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (137, 1654, 1654, 1000014, 1000188, '破庙内唐僧苏醒', '内景', '荒山破败寺庙内部', '白天', '晴天（光线从破损处射入）', '一座古代荒废寺庙的内部空间。空间破败，屋顶的木质横梁裸露，挂着残破的蛛网。墙壁斑驳，彩绘脱落。地面铺着干草堆。几缕明亮的阳光从屋顶的破洞和墙壁的裂缝中射入，形成明显的光柱，照亮空气中漂浮的尘埃。整体环境古老、寂静且略带神秘。', NULL, 2, 'uploads/scene_image/20260113/8a002afb32b928bb65f476cfd4e97542_69662f95aba09.png', '2026-01-13 19:32:03', '2026-01-13 19:42:14');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (138, 1654, 1654, 1000014, 1000188, '破庙外荒山野岭', '外景', '荒山破败寺庙外部', '白天', '晴天', '一座位于荒山野岭之中的破败古庙外部全景。寺庙建筑老旧，瓦片残缺，墙壁倾颓。周围是连绵的荒山和稀疏的草木，一条小路蜿蜒通向远方。天空晴朗，但空气中隐约有极光般闪烁的、半透明的数据流特效，与现实自然景观形成微妙的不协调感。环境空旷、原始且带有超现实的数字感。', NULL, 3, 'uploads/scene_image/20260119/be45d83c3647d3d48783999c50ff2ced_696e034683087.png', '2026-01-13 19:32:03', '2026-01-19 18:11:20');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (139, 1654, 1653, 1000013, 1000187, '城市公园傍晚秦彻喂猫', '外景', '城市公园长椅区域', '傍晚', '晴天', '城市公园的一角，一条长椅孤零零地立在草坪旁。傍晚的夕阳为公园的树木和草坪镀上一层温暖的金色。几只流浪猫从附近的灌木丛中钻出，其中一只橘猫格外显眼。远处路边，一辆黑色轿车静静停泊。整体环境宁静，光线柔和，带有暮色渐临的氛围。', NULL, 1, NULL, '2026-01-20 19:24:52', '2026-01-20 19:24:52');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (140, 1654, 1653, 1000013, 1000187, '公园长椅旁二人初次交谈', '外景', '城市公园长椅旁', '傍晚', '晴天', '公园长椅旁的空地，长椅一端坐着秦彻，另一端坐着柳如烟，两人保持着礼貌的距离。暮色渐浓，公园路灯尚未亮起，环境光线比之前稍暗。橘猫在秦彻脚边舔着爪子。整体氛围从宁静转向略带紧张和审视的交谈状态。', NULL, 2, NULL, '2026-01-20 19:24:52', '2026-01-20 19:24:52');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (141, 1654, 1653, 1000013, 1000187, '柳如烟车内回忆老宅异状', '内景', '柳如烟的黑色轿车内', '夜', '晴天', '豪华轿车内部，空间宽敞，内饰精致。车窗外的城市夜景流光溢彩。车内光线主要来自仪表盘和中控屏幕的微光，以及偶尔掠过的路灯光影。整体氛围私密、安静，带有思考与回忆的凝重感。', NULL, 3, NULL, '2026-01-20 19:24:52', '2026-01-20 19:24:52');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (142, 1654, 1653, 1000013, 1000187, '秦彻简陋出租屋夜读占卜', '内景', '秦彻的临时出租屋', '夜', '晴天', '一间极其简陋的出租屋内部，空间狭小。屋内有一张硬板床，床上摊开一本泛黄的线装古书和三枚铜钱。一张旧桌子，上面有一盏未点燃的油灯。墙壁斑驳，窗户破旧。月光从窗户斜射进来，是屋内主要的光源，勾勒出简单家具的轮廓。整体环境清贫、安静，带有一种与世隔绝的修行气息。窗外隐约传来城市的喧嚣声。', NULL, 4, NULL, '2026-01-20 19:24:52', '2026-01-20 19:24:52');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (143, 1654, 1653, 1000013, 1000208, '秦彻天桥下摆摊问卜', '外景', '城市天桥下', '清晨', '晴天', '城市天桥下，人来人往。桥墩旁铺着一块洗得发白的蓝布，上面用墨笔写着“问卜解签，趋吉避凶”八个端正楷字。秦彻盘膝坐在蓝布后，身边放着一个布袋，神色平静。桥下车流不息，清晨的阳光斜照下来。', NULL, 1, NULL, '2026-01-20 19:27:22', '2026-01-20 19:27:22');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (144, 1654, 1653, 1000013, 1000208, '张伟向秦彻问卜工作', '外景', '城市天桥下', '白天', '晴天', '城市天桥下，秦彻的摊位前。穿着廉价西装的张伟蹲在摊位前，神色焦虑。秦彻正在查看张伟的手相。背景是桥下的行人和车流。', NULL, 2, NULL, '2026-01-20 19:27:22', '2026-01-20 19:27:22');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (145, 1654, 1653, 1000013, 1000208, '柳如烟车内观察秦彻', '外景', '天桥另一侧路边', '白天', '晴天', '天桥另一侧路边，停着一辆不起眼的灰色轿车。柳如烟坐在后座，戴着墨镜，透过车窗观察着远处天桥下的秦彻摊位。她手中拿着手机。', NULL, 3, NULL, '2026-01-20 19:27:22', '2026-01-20 19:27:22');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (146, 1654, 1653, 1000013, 1000208, '买菜大妈路过嘲讽', '外景', '城市天桥下', '白天', '晴天', '城市天桥下，秦彻的摊位前。一位穿着时髦、拎着名牌包的中年女人（买菜大妈）和同伴路过，她瞥了一眼秦彻的摊位，露出嗤笑的表情。秦彻闭目养神，仿佛没听见。', NULL, 4, NULL, '2026-01-20 19:27:22', '2026-01-20 19:27:22');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (147, 1654, 1653, 1000013, 1000208, '张伟返回道谢引围观', '外景', '城市天桥下', '白天', '晴天', '城市天桥下，秦彻的摊位前。张伟气喘吁吁地跑回来，满脸激动地向秦彻道谢。周围有几个路人被吸引，好奇地围了过来，开始窃窃私语。', NULL, 5, NULL, '2026-01-20 19:27:22', '2026-01-20 19:27:22');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (148, 1654, 1653, 1000013, 1000208, '柳如烟决定试探秦彻', '外景', '天桥另一侧路边', '白天', '晴天', '天桥另一侧路边，灰色轿车内。柳如烟将天桥下发生的一切看在眼里，她摘下墨镜，眼神若有所思。她对司机下达指令，车子缓缓驶离。', NULL, 6, NULL, '2026-01-20 19:27:22', '2026-01-20 19:27:22');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (149, 1654, 1653, 1000013, 1000208, '秦彻傍晚喂猫沉思', '外景', '城市天桥下', '傍晚', '晴天', '城市天桥下，夕阳西下。秦彻正在收起蓝布和零散的收入。一只橘猫蹭着他的腿。他身边放着买来的馒头和一碗清水。华灯初上，城市夜景开始显现。', NULL, 7, NULL, '2026-01-20 19:27:22', '2026-01-20 19:27:22');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (150, 1654, 1653, 1000013, 1000208, '柳如烟夜查老宅资料', '内景', '柳氏集团大厦总裁办公室', '夜晚', '晴朗', '柳氏集团大厦总裁办公室内。柳如烟站在落地窗前，俯瞰城市夜景。办公桌上放着一份用红笔圈画过的老宅平面图复印件。室内灯光明亮，陈设现代而奢华。', NULL, 8, NULL, '2026-01-20 19:27:22', '2026-01-20 19:27:22');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (152, 1654, 1655, 1000011, 1000130, '都市写字楼夜景', '外景', '都市CBD写字楼外', '深夜', '晴朗', '现代都市的夜景，一座高耸的玻璃幕墙写字楼矗立在城市中心。大楼大部分楼层的灯光已经熄灭，只有零星几扇窗户还亮着微弱的白光。玻璃幕墙反射着周围建筑的霓虹灯光和街道上的车流光带，呈现出冷色调的都市氛围。天空是深蓝色的，没有月亮和星星。', NULL, 1, 'scene/image/20260121/7ab385315989d8b0cde06421c4f7ff6b_69709bf4c897f.jpg', '2026-01-21 17:04:19', '2026-01-21 17:27:17');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (153, 1654, 1655, 1000011, 1000130, '写字楼内部走廊', '内景', '写字楼内部走廊', '深夜', '无', '一个现代、空旷的写字楼内部走廊。地面是光滑的浅色大理石，墙壁是白色或浅灰色。天花板上有规则的嵌入式筒灯，提供均匀但略显冷清的照明。走廊两侧是关闭的玻璃门办公室。环境非常安静，只有远处隐约传来的城市背景音。前台区域位于走廊一侧，简洁现代。', NULL, 2, NULL, '2026-01-21 17:04:19', '2026-01-21 17:04:19');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (154, 1654, 1655, 1000011, 1000130, '写字楼电梯间', '内景', '写字楼电梯轿厢内', '深夜', '无', '一个现代写字楼的标准电梯轿厢内部。轿厢内壁是光滑的金属或镜面材质，干净明亮。轿厢顶部有柔和的灯光照明。电梯控制面板上亮着楼层按钮的背光。环境密闭且安静，只有电梯运行的低沉机械声。', NULL, 3, NULL, '2026-01-21 17:04:19', '2026-01-21 17:04:19');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (155, 1654, 1655, 1000011, 1000130, '地铁站台', '内景', '地铁站站台', '深夜', '无', '一个现代都市的地铁站站台。站台空旷，乘客稀少。站台两侧是轨道，对面是广告灯箱。站台顶部有明亮的白色灯光照明，地面干净。有自动扶梯连接上下层。站台边缘有安全屏蔽门或黄色警示线。晚风从隧道或通道吹来。', NULL, 4, NULL, '2026-01-21 17:04:19', '2026-01-21 17:04:19');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (156, 1654, 1655, 1000011, 1000130, '地铁车厢内部', '内景', '地铁列车车厢内', '深夜', '无', '一节正在行驶的地铁列车车厢内部。车厢内灯光明亮，乘客非常少，只有零星几个人。车厢连接处有金属栏杆和扶手。车窗是干净的大玻璃窗。车窗外是飞速掠过的城市夜景，包括广告牌、路灯和高架桥上的车灯，形成流动的光带。车厢内广播系统会偶尔播报。', NULL, 5, NULL, '2026-01-21 17:04:19', '2026-01-21 17:04:19');
INSERT INTO `php_plugin_shortplay_drama_scene` VALUES (157, 1654, 1653, 1000013, 1000165, '天桥下', '外景', '城市天桥下人行横道', '晚上', '晴', '一座钢筋混凝土天桥下方的人行道，桥体投下阴影。地面铺着一张写着“卜卦问事”的粗布，旁边摆着几枚铜钱。行人匆匆走过，背景是都市傍晚的车流和霓虹初上的景象。', '', 6, 'uploads/scene_image/20260113/3540701a7e47bf883bc1f629595df306_696604ca7f641.png', '2026-01-22 18:05:44', '2026-01-22 18:05:44');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (204, 1654, 1000013, 1000164, 79, 1, '秦彻向师父行礼，师父递给他一枚铜钱', '外景，终南山道观外山道，清晨薄雾缭绕。身穿洗得发白的青色道袍的青年秦彻，恭敬地向一位白发老道（师父）行礼。师父手持拂尘，正将一枚古朴的铜钱放入秦彻摊开的手掌中。背景是古朴的道观建筑和朦胧的山林。', '清晨的薄雾在山道间缓缓流动。中景固定镜头，秦彻向师父躬身行礼，师父将一枚古朴的铜钱轻轻放在秦彻摊开的手掌上。秦彻的手微微合拢，握住铜钱。', 'uploads/storyboard_image/20260106/682f75ff2fffa3ad663e8231010cea0c_695c887d42ebd.png', 'uploads/storyboard_video/20260106/f808d4699b320844c91ecdc5ff13ad6d_695c897169ed5.mp4', '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (205, 1654, 1000013, 1000164, 79, 3, '秦彻眼神茫然，师父拂尘轻挥', '中近景，平视角度，固定镜头。清晨薄雾笼罩的山道，背景是古朴的道观。前景中，身穿洗得发白的青色道袍的年轻道士秦彻，眼神清澈但带着一丝茫然，望着师父。白发老道师父手持拂尘，面带微笑，轻轻挥动拂尘。画面色调清冷，有晨雾的朦胧感。', '中近景，平视角度，固定镜头。秦彻（青年，身穿洗得发白的青色道袍）站在师父面前，眼神从恭敬转为茫然，嘴唇微动，似乎想问什么。师父（白发老道）手持拂尘，微笑着轻轻一挥，动作舒缓而意味深长。清晨的薄雾在山道间缓缓流动。', 'uploads/storyboard_image/20260106/baca2e10f88f4ba40566570fc4cf8c86_695c88823a2da.png', 'uploads/storyboard_video/20260106/93a8aabc159400ce3729246afedb4347_695c8976066c2.mp4', '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (206, 1654, 1000013, 1000164, 80, 4, '秦彻站在公园入口，与周围人群格格不入', '一位身穿洗得发白的青色道袍的年轻道士（秦彻）站在现代都市公园入口处，周围是穿着时尚、行色匆匆的都市人群，背景是高楼大厦和车流，形成鲜明对比。道士表情茫然，与环境格格不入。', '镜头固定，拍摄秦彻站在公园入口。他身穿青色道袍，背着一个旧布包，茫然地站立着。周围是快速流动的时尚人群和车流，形成动态背景。秦彻的目光缓慢地扫视着周围陌生的环境，身体略显僵硬。', 'uploads/storyboard_image/20260106/423bd4c326ff67de34fe89b95927cac2_695c8882a6be7.png', 'uploads/storyboard_video/20260106/712a5ebd949c5ab38eaddc7eb16629bf_695c8980099f4.mp4', '中景', '平视', '固定', NULL, NULL, 5000, '秦彻来到大都市，一时间竟有些迷茫，不知何去何从', 'uploads/storyboard_narration_voice/20260116/50be26e08eff9661801b6231b9a02e91_6969b60f08b20.mp3', 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (207, 1654, 1000013, 1000164, 80, 5, '秦彻捏紧布包，眼神无措', 'A young man in a faded blue Taoist robe stands at the entrance of a bustling urban park. He clutches a worn cloth bag tightly in his hand. His expression is one of confusion and slight apprehension as he looks around at the modern city scene. The park entrance is crowded with people in contemporary fashion, creating a stark contrast with his traditional attire. The lighting is bright afternoon sunlight.', 'Close-up shot of Qin Che\'s hand tightly gripping the strap of his old cloth bag. The camera holds steady on his hand and the bag, emphasizing the tension in his grip. The background is a blur of moving people in modern clothing. The shot is static, focusing on the physical action of clutching the bag.', 'uploads/storyboard_image/20260106/6cb2b1edb3c90e17dee6a03fcf4914bc_695c887dcb2f4.png', 'uploads/storyboard_video/20260106/f17f834ba6ce4ad2b60c4b29ac6b5443_695c8985240f9.mp4', '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (208, 1654, 1000013, 1000164, 81, 6, '秦彻坐在长椅上吃馒头', '中景，平视镜头，固定拍摄。一位身穿洗得发白的青色道袍的年轻道士（秦彻）独自坐在城市公园的长椅上。他正低着头，专注而缓慢地吃着手中半个干硬的馒头。阳光透过树叶间隙洒在他身上，周围是现代化的公园环境，远处有高楼大厦。他身边放着一个旧布包。画面安静，带有一种与周围环境格格不入的孤独感。', '中景，平视，固定镜头。秦彻坐在公园长椅上，低头看着手中的半个馒头。他拿起馒头，小口地、缓慢地咀嚼着，动作带着一种与世无争的平静。偶尔有行人从背景中走过，但他并未抬头。整个动作节奏舒缓，强调他此刻的专注与饥饿感。', 'uploads/storyboard_image/20260106/78163ab2b6e8cc412915b4e4b12f1c69_695c8a26ec917.png', 'uploads/storyboard_video/20260106/3e32dabfe692b0687b386b6af2b7074f_695c8a857a78c.mp4', '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (209, 1654, 1000013, 1000164, 81, 7, '秦彻听到猫叫，看向灌木丛', '中景，平视，固定镜头。身穿洗白青色道袍的年轻道士秦彻坐在公园长椅上，手里拿着半个馒头，正准备吃。他微微侧头，目光被灌木丛方向的声音吸引，表情专注。背景是公园的树木和远处的行人。', '中景，平视，固定镜头。秦彻坐在公园长椅上，手里拿着半个馒头。他正要咬下去，动作突然停顿，耳朵微动，目光转向画面右侧的灌木丛方向，脸上露出倾听和疑惑的表情。', 'uploads/storyboard_image/20260106/f841fce6cc316ddc95f27e7b334f2164_695c88a5ab801.png', 'uploads/storyboard_video/20260106/46b7e0cdf06086611f298f03942cfab8_695c8a3e394fd.mp4', '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (210, 1654, 1000013, 1000164, 81, 8, '秦彻蹲下为橘猫画符驱邪', 'A young man in a faded blue Daoist robe (Qin Che) squats on the ground in a city park. He holds a small white porcelain bottle in one hand, his index finger dipped in the liquid inside. He is drawing a simple glowing talisman in the air in front of a thin, weak orange tabby cat. The cat looks up at him. A few faint, wispy strands of gray-black energy are dissipating from the cat\'s body. The background is a park with green bushes and a bench in the distance. The lighting is soft afternoon sunlight.', 'A medium close-up, fixed shot from a slightly high angle. Qin Che squats down, takes out a small porcelain bottle from his cloth bag, and opens it. He dips his index finger into the bottle, then raises his hand and draws a simple, glowing talisman in the air in front of the orange cat. As his finger moves, faint golden light traces the pattern. The gray-black energy strands缠绕 around the cat begin to writhe and dissipate like smoke meeting boiling water. The cat watches the process, its body relaxing slightly. The shot holds on Qin Che\'s focused expression and the dissipating energy.', 'uploads/storyboard_image/20260106/ffefae6543381fa3a6d5aa3a868917e2_695c888b5f264.png', 'uploads/storyboard_video/20260106/eff0de63cd4d8527446b77d9bfa41019_695c8a4332018.mp4', '中近景', '俯拍', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (211, 1654, 1000013, 1000164, 81, 9, '橘猫蹭秦彻手指后跑开', '近景，平视，固定镜头。一只瘦弱的橘猫，在公园灌木丛旁，用头轻轻蹭着一位身穿洗得发白的青色道袍的年轻道士（秦彻）的手指。秦彻蹲着，脸上带着一丝温和的微笑。背景是公园的绿植和远处模糊的长椅。', '近景，平视，固定镜头。一只瘦弱的橘猫，在公园灌木丛旁，用头轻轻蹭着一位身穿洗得发白的青色道袍的年轻道士（秦彻）的手指。秦彻蹲着，脸上带着一丝温和的微笑。背景是公园的绿植和远处模糊的长椅。橘猫蹭了两下手指后，转身轻盈地跑开，消失在灌木丛中。', 'uploads/storyboard_image/20260106/eaf782432d2313b93027049474e42595_695c88907843e.png', 'uploads/storyboard_video/20260106/0ea38ca05558bf3c0d5ed5aeafccdcab_695c8a4d1d4ea.mp4', '近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (212, 1654, 1000013, 1000164, 81, 10, '秦彻肚子叫，摸空瘪的布包叹气', '中近景，平视角度，固定镜头。身穿洗得发白的青色道袍的年轻道士秦彻，坐在公园长椅上，他微微低头，一只手轻轻按在自己腹部，另一只手则放在身旁一个看起来空瘪的旧布包上。他脸上带着一丝无奈和轻微的叹息表情。背景是公园的灌木丛和远处的行人，阳光明媚。', '中近景，固定镜头，平视。秦彻坐在长椅上，脸上露出完成救助橘猫后的短暂微笑，随即笑容收敛，眉头微蹙。他下意识地用手摸了摸腹部，然后转头看向身旁的旧布包，伸手捏了捏布包，确认其空瘪的状态，最后轻轻叹了口气，目光望向远处。', 'uploads/storyboard_image/20260106/bd33ba09d95ab510c549c12a24117240_695c88a0934b9.png', 'uploads/storyboard_video/20260106/e1e5a7dbca5f2349f548ef08539e373e_695c8a52000ab.mp4', '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (213, 1654, 1000013, 1000164, 82, 11, '柳如烟在林荫道打电话', '一位面容姣好但脸色苍白的年轻女性（柳如烟），穿着精致的米白色套装，站在公园的林荫道旁。她正将手机举在耳边，神情专注而略显急促。背景是公园的树木和远处模糊的都市高楼。阳光透过树叶缝隙洒下斑驳光影。', '镜头固定，拍摄柳如烟在林荫道旁打电话。她语速较快，表情随着通话内容变化，从专注到逐渐显出焦虑和急促。她的另一只手不自觉地握紧。背景中偶尔有行人或车辆经过，但焦点始终在柳如烟身上。', 'uploads/storyboard_image/20260106/01ac3f7ab1837112d5db4dbd8ff5ef62_695c88aa7cc4a.png', 'uploads/storyboard_video/20260106/204833bae40dd7e405d4764023e5a869_695c8a5bd7134.mp4', '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (214, 1654, 1000013, 1000164, 82, 12, '柳如烟突然身体摇晃，手机掉落', '一位穿着精致米白色套装的年轻女性（柳如烟）站在公园林荫道上，面容姣好但脸色苍白。她正拿着手机打电话，但身体突然失去平衡，开始摇晃，眼神涣散。手机从她手中滑落，即将掉在地上。背景是公园的树木和远处的城市高楼，阳光透过树叶洒下斑驳光影。', '镜头固定，拍摄柳如烟中近景。她正在打电话，表情专注而急促。突然，她的身体猛地一晃，眼神瞬间变得空洞，拿着手机的手无力地松开，手机从画面中向下掉落。她的身体继续轻微摇晃，仿佛随时会倒下。', 'uploads/storyboard_image/20260106/c19201ae570b19de6c4bcfacb7f25e98_695c89643568f.png', 'uploads/storyboard_video/20260106/d5a8fa67a83fbe830e7bdea81ee949c0_695c8a66c09f6.mp4', '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (215, 1654, 1000013, 1000164, 82, 13, '树后术士捏着草人施法', 'A medium close-up shot of a middle-aged man with a sinister expression, wearing a black Tang suit, hiding behind a tree in a park. He holds a straw doll with a yellow paper talisman attached to it in his hand, his fingers are tightly pinching the doll\'s chest area. The lighting is dappled through the leaves, creating a tense and mysterious atmosphere.', 'A static medium close-up shot of a sinister-looking man in a black Tang suit. He is partially hidden behind a tree trunk. His eyes are focused intently on the straw doll in his hands. His fingers exert pressure, pinching the chest area of the doll. A faint, malevolent energy seems to emanate from his grip. The background is slightly out-of-focus, showing the greenery of the park.', 'uploads/storyboard_image/20260106/6a2bf3b9139e6a27b127d1860b0c3603_695c88a61b65c.png', 'uploads/storyboard_video/20260106/905b8b9e720db685f8d3ae74c92ab31a_695c8a7011240.mp4', '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (216, 1654, 1000013, 1000164, 83, 14, '秦彻冲过来挡开保镖', '中景镜头，跟拍。都市公园林荫道，下午晴天。穿着洗白青色道袍的年轻道士秦彻从画面右侧快速冲入，挡在瘫倒在地、穿着米白色套装的年轻女性柳如烟身前。一名穿着黑色西装、身材魁梧的保镖（保镖甲）正弯腰伸手准备扶起柳如烟，秦彻伸出右手臂，果断地格挡开保镖的手臂。保镖脸上露出惊愕和警惕的表情。背景是公园树木和远处的其他行人。', '镜头从秦彻奔跑的侧后方跟拍，他快速冲入画面中央，动作果断地挡开保镖伸向柳如烟的手。保镖被格挡后，身体微微后倾，表情从焦急转为惊愕和警惕。秦彻挡在柳如烟身前，眼神坚定地看向保镖。', 'uploads/storyboard_image/20260106/7551b9cbf20b860d835dc65df36e5a6c_695c88afacf31.png', 'uploads/storyboard_video/20260106/bda92722f9fe30586ef3e51e0c1587dc_695c8df998277.mp4', '中景', '平视', '跟拍', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (217, 1654, 1000013, 1000164, 83, 15, '秦彻用银针扎向柳如烟眉心', '特写镜头，平视角度。画面中心是秦彻的手，手指捏着一根细长的银针，针尖精准地刺向柳如烟的眉心（印堂穴）。柳如烟闭着眼，脸色苍白，瘫软在地。背景虚化，隐约可见惊慌的保镖身影。针尾泛起极淡的金色光晕。', '固定特写镜头。秦彻的手稳定而迅速地向前刺出，银针针尖接触柳如烟眉心的瞬间，针身微微震颤，针尾的金色光晕短暂地明亮了一瞬。柳如烟的身体随之轻微一震。', 'uploads/storyboard_image/20260106/13d58a8e6f541d6e7778e8ddb02ffa50_695c8d7735b82.png', 'uploads/storyboard_video/20260106/cee8e24c9dc4f3a0d37be464d19de10f_695c8dfdcecd0.mp4', '特写', '平视', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (218, 1654, 1000013, 1000164, 83, 16, '树后术士手中草人冒烟，嘴角溢血后隐匿', '一个面容阴鸷、穿着黑色唐装的中年男人（黑衣术士）躲在公园的树后，他低头看着自己的手，手中一个贴着黄纸的草人正冒出一股青烟，术士嘴角溢出一丝鲜血，脸上露出惊骇的表情，他迅速抬头警惕地看向秦彻的方向。', '镜头固定，对准躲在树后的黑衣术士。他手中的草人突然“噗”地冒出一股青烟，术士身体一震，闷哼一声，嘴角溢出一缕鲜血。他脸上露出难以置信和惊骇的表情，迅速抬头，目光越过树丛，看向远处秦彻和柳如烟的方向。他眼神闪烁，带着一丝恐惧和怨毒，随即果断地将冒烟的草人丢弃，身体向后一缩，迅速转身，隐匿于公园的树木和阴影之中。', 'uploads/storyboard_image/20260106/5f803ad7603fab9b0786a466bdee50ba_695c89b766102.png', 'uploads/storyboard_video/20260106/2cca4e2db002c49fa38670e678cee147_695c8e088b5ef.mp4', '中近景', '侧拍', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (219, 1654, 1000013, 1000164, 83, 17, '柳如烟睁眼看到秦彻侧影', '特写镜头，模拟柳如烟刚刚苏醒的模糊、晃动的第一人称视角。画面焦点在近处，一个穿着洗得发白的青色道袍的年轻男子侧脸，他的手指正捏着一根闪亮的银针从画面中抽离。背景是公园林荫道的树叶和天空，光线透过树叶形成光斑。画面整体带有轻微的虚焦和晃动感，色彩饱和度略低，以表现人物虚弱和视线模糊的状态。', '特写镜头，模拟柳如烟苏醒瞬间的主观视角。画面从一片模糊的、晃动的光斑和色块开始，逐渐聚焦。一个穿着青色道袍的年轻男子（秦彻）的侧脸轮廓在画面中变得清晰，他正专注地看着手中的银针。银针的针尾反射着阳光，泛出微弱的金色光晕。随着秦彻的动作，银针被缓缓从画面（眉心）抽离。背景是公园的树木和天空，光线柔和。整个镜头保持轻微的呼吸感和晃动感，模拟人刚恢复意识时的视觉状态。', 'uploads/storyboard_image/20260106/3559f5798206780ffb31e5055b02f7f3_695c8913a6d7a.png', 'uploads/storyboard_video/20260106/8415191be1c080b21d497fa2a65c1fec_695c8aca6b52f.mp4', '特写', '柳如烟主观视角（模糊，晃动）', '固定（模拟人眼聚焦）', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (220, 1654, 1000013, 1000164, 83, 18, '保镖抓住秦彻，秦彻挣脱逃离', '中景，平视角度，固定镜头。保镖甲（身穿黑色西装，表情愤怒）用右手紧紧抓住秦彻（身穿洗得发白的青色道袍，表情冷静但警惕）的左臂。秦彻的旧布包斜挎在身上。背景是公园林荫道，有树木和远处模糊的人群。', '中景，平视，固定镜头。保镖甲的手紧紧抓住秦彻的道袍手臂，秦彻身体微微后撤，试图挣脱。保镖甲表情愤怒，秦彻表情冷静但眼神警惕。背景中，另一名保镖正在扶起柳如烟，人群有些骚动。', 'uploads/storyboard_image/20260106/62d62c5023cc32dd5c713a0b0a49402d_695c88bd7baa4.png', 'uploads/storyboard_video/20260106/52ccd3eb175c77b064cca8175065ee31_695c8abbc8b83.mp4', '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (221, 1654, 1000013, 1000164, 83, 19, '柳如烟被扶起，看向秦彻消失方向', '中近景，平视，固定镜头。画面中，柳如烟（面容姣好，脸色苍白，穿着精致的米白色套装）被一名黑衣保镖搀扶着，勉强站立。她的眼神迷茫而虚弱，视线越过保镖的肩膀，望向画面左侧（秦彻消失的方向）。背景是公园的林荫道，树木和远处模糊的人群。光线为下午的自然光。', '中近景，平视，固定镜头。柳如烟被保镖扶起，她身体微晃，眼神从涣散逐渐聚焦，带着困惑和一丝探寻，缓慢而坚定地转向秦彻逃离的方向。保镖关切地看着她。画面保持静止，焦点在柳如烟的脸上。', 'uploads/storyboard_image/20260106/8379e8cddce36b17a99ada57d11b0a41_695c88b49df98.png', 'uploads/storyboard_video/20260106/8e77cb4912a9aceac2503f2366062b84_695c8ac163410.mp4', '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (222, 1654, 1000013, 1000164, 84, 20, '秦彻蹲在路边，面前铺着卜卦粗布', '中景镜头，平视角度，固定机位。秦彻（青年，身穿洗得发白的青色道袍）蹲在城市天桥下的人行道上。他面前的地上铺着一块写着“卜卦问事”的粗布，布上散落着几枚铜钱。他的旧布包放在脚边。傍晚的天色，光线昏黄，背景是匆匆走过的行人和天桥结构。', '中景镜头，平视角度，固定机位。秦彻蹲在天桥下，面前铺着卜卦粗布。他静静地蹲着，目光低垂看着面前的粗布和铜钱，偶尔抬眼看向前方匆匆走过的行人。傍晚的光线，行人脚步匆匆，画面氛围冷清。', 'uploads/storyboard_image/20260115/3dd42e58ed8fd173ab2170bd1c0e492b_69688c77eeb2b.png', 'uploads/storyboard_video/20260115/192dfcbca7ad686d45686404a2777896_6968adcc9ee54.mp4', '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (223, 1654, 1000013, 1000164, 84, 21, '秦彻摸肚子，看着行人，一脸苦恼', '中近景，平视角度，固定镜头。一位身穿洗得发白的青色道袍的年轻道士（秦彻）蹲在城市天桥下的人行道上。他面前的地上铺着一张写着“卜卦问事”的深色粗布，上面散落着几枚铜钱。秦彻一只手按在自己空瘪的腹部，眉头微蹙，眼神略带苦恼地望向镜头外匆匆走过的行人。傍晚的天色，光线昏黄。', '中近景，固定镜头。秦彻蹲在天桥下，一只手按着肚子，脸上露出苦恼的表情。他的视线缓慢地从左移到右，扫视着镜头外川流不息的行人。他轻轻叹了口气，肚子又发出一声轻微的咕噜声。傍晚的光线在他脸上投下阴影。', 'uploads/storyboard_image/20260115/7ade32322f49f76111be1fe8502f1eba_69688c9071b7f.png', 'uploads/storyboard_video/20260115/a9f5487e84df4098376f184846a58c72_6968adf90fe66.mp4', '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (311, 1654, 1000012, 1000123, 107, 2, '豌豆蹲在溪边，将干瘪野果放入掌心。', '近景，平视镜头，固定拍摄。一位穿着朴素古代农家服饰的年轻女子（豌豆）蹲在一条清澈的小溪边。她神情专注，小心翼翼地将一颗干瘪的野果放在自己摊开的掌心上。背景是溪流和山石，光线明亮，天气晴朗。', '近景，平视，固定镜头。画面中，豌豆蹲在溪边，她低头看着自己的手掌，小心翼翼地将一颗干瘪的野果放入掌心。她的动作轻柔而专注。背景是流动的溪水和山石。', NULL, NULL, '近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (312, 1654, 1000012, 1000123, 107, 3, '豌豆掌心出现灵泉水浸润野果，野果迅速变得饱满红润。', '特写镜头，俯视角度。一位少女（豌豆）蹲在溪边，她摊开的掌心中，一颗干瘪的野果正被一小捧凭空出现的清澈泉水浸润。泉水泛着微光，野果正以肉眼可见的速度从干瘪变得饱满、颜色从暗淡转为红润。背景是溪边的泥土和石头。', '固定机位特写镜头，俯拍少女掌心。镜头聚焦于掌心的野果。画面开始时，野果干瘪暗淡。随后，一小捧清澈的灵泉水凭空出现在掌心，缓缓浸润野果。在镜头快放下，野果迅速吸水膨胀，表皮变得光滑饱满，颜色转为鲜艳的红润，并散发出柔和的光泽。整个过程持续5秒。', NULL, NULL, '特写', '俯拍', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (313, 1654, 1000012, 1000123, 107, 6, '豌豆喝下灵泉水，神情振奋。', '特写镜头，一位穿着朴素古装、面容清秀的年轻女子（豌豆）正仰头喝下竹筒中的水。她眼神明亮，神情专注而振奋。背景是模糊的山洞石壁和溪流。光线从洞口斜射进来，照亮了她手中的竹筒和部分脸庞。', '特写镜头，固定拍摄。画面中心是年轻女子豌豆，她双手捧着一个竹筒，仰头将筒中的水一饮而尽。喝完后，她放下竹筒，眼睛瞬间亮了起来，脸上疲惫的神色一扫而空，取而代之的是一种充满希望和力量的振奋表情。她深吸一口气，嘴角微微上扬，眼神变得异常坚定。', NULL, NULL, '特写', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (314, 1654, 1000012, 1000123, 108, 7, '木桌上摆着稠粥和野菜，家人围坐面面相觑。', '中景，平视，固定镜头。在一个破旧农家小院的堂屋内，傍晚时分，光线柔和。一张旧木桌上摆放着几碗浓稠的米粥和一碟鲜嫩的野菜。豌豆爹娘和四个哥哥围坐在桌旁，他们穿着朴素的粗布衣服，脸上带着惊讶和困惑的表情，面面相觑，看着桌上不寻常的丰盛饭菜。背景是简陋的土墙和木窗。', '中景，固定镜头，傍晚光线。镜头聚焦于破旧木桌上的稠粥和野菜，以及围坐的家人。豌豆爹娘和四个哥哥的目光在彼此和饭菜之间来回移动，脸上露出困惑和担忧的神情。豌豆娘嘴唇微动，欲言又止。整个场景气氛略显凝重，与桌上食物的丰盛形成对比。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (315, 1654, 1000012, 1000123, 108, 8, '豌豆向娘亲解释食物来源。', '中近景，平视，固定镜头。傍晚，豌豆家破旧的小院内。少女豌豆面带微笑，眼神清澈，正对着身旁面露担忧的母亲说话。母亲穿着打补丁的粗布衣服，眉头微蹙。背景是简陋的农家小院和堂屋门框。', '中近景，平视，固定镜头。傍晚，豌豆家小院。少女豌豆微笑着对母亲解释，表情自然诚恳。母亲听着，脸上的担忧神情逐渐缓和。光线是傍晚的柔和暖光。', NULL, NULL, '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (316, 1654, 1000012, 1000123, 108, 9, '哥哥们埋头吃饭，脸上露出满足。', '近景，平视镜头，四个年轻男子围坐在破旧木桌旁，他们穿着朴素的粗布衣服，面容朴实，正埋头吃着碗里的稠粥，脸上洋溢着满足和幸福的笑容。桌上摆着几个粗陶碗和一碟野菜。背景是简陋的农家小院一角，光线是傍晚的暖色调。', '近景，固定镜头，四个年轻男子围坐在破旧木桌旁，他们穿着朴素的粗布衣服，面容朴实，正埋头吃着碗里的稠粥，脸上洋溢着满足和幸福的笑容。桌上摆着几个粗陶碗和一碟野菜。背景是简陋的农家小院一角，光线是傍晚的暖色调。', NULL, NULL, '近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (317, 1654, 1000012, 1000123, 109, 10, '豌豆背着竹篓在林间熟练地采摘草药。', '中景镜头，平视角度，固定机位。一位穿着朴素布衣的少女（豌豆）背着一个半旧的竹编背篓，站在郁郁葱葱的山林中。她正微微弯腰，熟练地从地上采摘一株草药，放入背后的竹篓。阳光透过树叶缝隙洒下光斑，照亮她专注的侧脸和手中的草药。背景是茂密的树林和灌木。', '中景镜头，平视角度，固定机位。少女（豌豆）背着竹篓，在山林中熟练地辨认并采摘草药。她动作麻利，将一株草药放入背后的竹篓，然后目光移向另一处，继续寻找。整个过程连贯自然，背景是静谧的山林，有风吹动树叶的轻微晃动。', 'storyboard/image/20260121/1d83b51849fa8bbf5cf69b5d7c8fdc35_69709859e4b3d.jpg', NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (318, 1654, 1000012, 1000123, 109, 11, '豌豆听到微弱呻吟声，警觉地停下脚步。', '一个穿着朴素布衣的年轻女子（豌豆）背着竹篓，站在郁郁葱葱的山林中。她突然停下脚步，身体微微前倾，侧耳倾听，脸上露出警觉的表情。阳光透过树叶缝隙洒在她身上。', '镜头固定，拍摄豌豆在山林中采药。她正伸手去摘一株草药，动作突然停顿，身体转向声音来源方向，眉头微蹙，眼神变得警惕。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (319, 1654, 1000012, 1000123, 110, 12, '萧绝蜷缩在斜坡下的乱石旁，伤势严重。', '俯拍视角，一个穿着破烂玄色锦衣的年轻男子蜷缩在山林斜坡下的乱石堆旁，他脸色苍白，双眼紧闭，长睫毛上沾着尘土，嘴角和衣服上有明显的血迹，周围是散落的灰色岩石和稀疏的杂草，阳光透过树叶间隙洒下斑驳光影。', '固定俯拍镜头，画面中心是蜷缩在乱石堆旁的萧绝，他因伤势而身体微微起伏，呼吸微弱，阳光的斑驳光影在他身上和周围的地面上缓慢移动，营造出一种脆弱和孤寂的氛围。', NULL, NULL, '俯拍', '俯拍', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (320, 1654, 1000012, 1000123, 110, 13, '豌豆站在坡上，皱眉看着下方的萧绝。', 'A medium close-up shot from a high angle, looking down a forested slope. A young woman in simple, worn-out rural clothing (Pea) stands on the edge of the slope, frowning as she looks down at something below. Her expression is a mix of concern and hesitation. The setting is a forested hillside during the day under a clear sky.', 'A static, medium close-up shot from a high angle. The camera looks down a forested slope. Pea stands at the edge, her gaze fixed downwards. Her expression shifts slightly, showing a thoughtful frown. No movement except for her subtle facial changes and perhaps a slight breeze in her hair. Daytime, clear sky.', NULL, NULL, '中近景', '俯拍', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (321, 1654, 1000012, 1000123, 110, 14, '豌豆犹豫后，向斜坡下走去。', '一个穿着朴素布衣的年轻女子（豌豆）正小心翼翼地沿着一个杂草丛生的山林斜坡向下走，她脸上带着犹豫和警惕的表情，目光看向斜坡下方。阳光透过树叶缝隙洒下斑驳光影。', '镜头从豌豆站在坡上的中景开始，她犹豫片刻，然后转身，小心翼翼地迈步向斜坡下方走去。镜头跟随她的背影，以俯拍角度拍摄她踩着杂草和碎石，一步步向下挪动的过程。她的动作缓慢而谨慎，显示出内心的纠结和对下方情况的警惕。', NULL, NULL, '中景', '俯拍', '跟拍', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (322, 1654, 1000012, 1000123, 111, 15, '豌豆蹲在萧绝身边，伸手探他鼻息。', 'A close-up shot from a high angle. A young woman in simple, worn-out peasant clothes is crouching beside a man lying on the ground. She is reaching out her hand towards the man\'s face, her fingers positioned near his nose. The man is dressed in tattered, dark-colored silk robes, his face is pale and smudged with dirt and blood, his eyes are closed, and his long eyelashes are visible. The setting is at the bottom of a forest slope, with scattered rocks and fallen leaves visible in the background. The lighting is the soft, warm glow of late afternoon sunlight filtering through the trees.', 'Close-up shot, high angle, static camera. A young peasant woman crouches beside a wounded man lying on the ground. She carefully extends her hand towards his face, her fingers gently moving to check his breathing. The man remains motionless, his face pale and stained with blood and dirt. The background is a forest slope with rocks and foliage. The scene is lit by the warm, low-angle light of late afternoon.', NULL, NULL, '近景', '俯拍', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (323, 1654, 1000012, 1000123, 111, 16, '豌豆背对萧绝，假装从怀里取出竹筒倒水。', '近景，侧拍视角，固定镜头。一名身穿朴素布衣的少女（豌豆）背对着镜头，微微侧身，她的右手正伸向自己胸前的衣襟，做出从怀里取出东西的动作。她的左手掌心向上，准备接住。一个竹筒正从她衣襟处被拿出，竹筒表面光滑。背景是山林斜坡下的乱石堆，光线为傍晚的夕阳余晖。', '近景，侧拍视角，固定镜头。画面中，少女（豌豆）背对镜头，身体微微侧倾。她右手伸入自己胸前的衣襟内，做出掏取的动作，随后拿出一个竹筒。镜头焦点集中在少女的手部和竹筒上，背景虚化。整个动作过程自然流畅，带着一丝不易察觉的谨慎。', NULL, NULL, '近景', '侧拍', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (324, 1654, 1000012, 1000123, 111, 17, '豌豆扶起萧绝的头，小心喂他喝水。', '特写镜头，平视角度。画面中央，一位穿着朴素布衣的少女（豌豆）跪坐在乱石堆旁，她一手小心地扶着一位身着破烂玄色锦衣、脸色苍白、脸上沾有泥土和血污的俊美青年（萧绝）的后颈，另一只手将一个竹筒倾斜，清澈的水流缓缓流入青年微张的口中。背景是傍晚山林斜坡下的乱石和杂草，夕阳的余晖为画面染上暖色调。', '特写镜头，固定机位。豌豆小心地将竹筒中的水喂给昏迷的萧绝。水缓缓流入萧绝口中，他的喉结微微动了一下。豌豆神情专注，带着一丝紧张和怜悯。画面光线为傍晚的暖色夕阳。', NULL, NULL, '特写', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (325, 1654, 1000012, 1000123, 111, 18, '萧绝呛咳着醒来，虚弱道谢。', '特写镜头，平视角度。萧绝（阿绝）躺在乱石堆旁，俊美无俦的脸上沾着泥土和血污，长睫紧闭，嘴唇干裂。他呛咳着，虚弱地睁开眼，眼神迷茫而脆弱。背景是山林斜坡和乱石，傍晚的夕阳余晖斜照在他脸上，形成光影对比。', '特写镜头，固定机位。萧绝（阿绝）躺在乱石堆旁，脸上沾着泥土和血污。他呛咳着，身体微微颤动，长睫颤动后缓缓睁开，露出迷茫、脆弱的眼神。嘴唇微动，发出沙哑的道谢声。背景是傍晚的山林斜坡，光线柔和。', NULL, NULL, '特写', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (326, 1654, 1000012, 1000123, 111, 19, '豌豆放下萧绝，起身欲走。', '中景，平视，固定镜头。画面中，少女豌豆（穿着朴素的农家衣裳）正从半蹲姿势起身，她刚刚将臂弯中扶着的受伤男子萧绝（穿着破烂染血的玄色锦衣，脸色苍白，沾有泥土和血污）小心地放回地面。萧绝虚弱地靠在乱石旁，眼神迷茫。背景是山林斜坡下的乱石堆，傍晚的夕阳余晖斜照，光线温暖但场景凌乱。', '中景，平视，固定镜头。少女豌豆将受伤男子萧绝轻轻放回地面后，立刻站起身，动作略显急促，表现出她想要尽快离开的意图。萧绝无力地靠在乱石上，微微仰头看着她起身的动作，眼神脆弱。整个镜头保持静止，焦点在豌豆起身的动作和萧绝的反应上。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (327, 1654, 1000012, 1000123, 112, 20, '萧绝伸手无力却精准地抓住豌豆的衣角。', '特写镜头，俯拍视角。画面中心是一只沾满泥土和血迹的男性手掌，正紧紧抓住一片洗得发白的粗布衣角。背景是傍晚山林斜坡下的乱石地面，夕阳的余晖为画面染上暖色调。手的主人（萧绝）身体的其他部分和衣角的主人（豌豆）的下半身处于画面边缘，形成强烈的视觉焦点。', '特写镜头，俯拍，固定机位。一只沾满泥土和血迹的男性手掌（萧绝的手）从画面下方伸出，虚弱但精准地抓住了画面中垂落的、洗得发白的粗布衣角（豌豆的衣角），并轻轻攥紧。整个动作缓慢而无力，但意图明确。画面背景是傍晚山林斜坡下的乱石地面，光线为夕阳的暖金色。', NULL, NULL, '特写', '俯拍', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (328, 1654, 1000012, 1000123, 112, 21, '萧绝气若游丝地恳求豌豆别走，提及有狼。', '特写镜头，平视角度。萧绝（阿绝）躺在斜坡下的乱石堆旁，脸色苍白如纸，嘴唇干裂，脸上沾着泥土和血污。他长睫紧闭，眼神却带着恳求与无助，虚弱地望向画外。夕阳的余晖从侧面照亮他俊美无俦的脸庞，营造出脆弱又极具冲击力的视觉效果。', '特写镜头，固定拍摄。萧绝（阿绝）躺在斜坡下的乱石堆旁，夕阳余晖下，他脸色苍白，嘴唇干裂，脸上有泥土和血污。他虚弱地睁开眼睛，眼神迷茫、脆弱，带着恳求，嘴唇微动，气若游丝地说话。说话时，他因虚弱而轻微咳嗽，嘴角溢出一丝血迹，衬得脸色更白。他的目光直直地望向画外（豌豆的方向），眼神如水，充满无助。', NULL, NULL, '特写', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (329, 1654, 1000012, 1000123, 112, 22, '豌豆看着萧绝脆弱的脸和恳求的眼神，内心挣扎。', '特写镜头，俯拍视角。画面中，一只沾着泥土和血迹的男人的手，正虚弱地抓住一位年轻姑娘的粗布衣角。背景是傍晚山林斜坡下的乱石堆，夕阳的余晖为画面镀上一层暖金色的光晕。', '固定镜头，俯拍特写。一只沾着泥土和血迹的男人的手，正虚弱地抓着一位年轻姑娘的粗布衣角。手微微颤抖，显示出主人的虚弱和恳求。背景是傍晚的山林斜坡，夕阳余晖洒在乱石和衣角上。', NULL, NULL, '特写', '俯拍', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (330, 1654, 1000012, 1000123, 112, 23, '豌豆叹气，答应扶他去山洞。', '中景，平视，固定镜头。画面中，一位穿着朴素古代农家少女（豌豆）站在斜坡下，她微微低头，看着躺在地上的受伤男子（萧绝）。男子身着染血破旧的玄色锦衣，脸色苍白，虚弱地蜷缩在乱石旁。背景是山林斜坡和树木，夕阳的余晖透过树叶缝隙洒下，营造出傍晚的氛围。少女的表情带着一丝无奈和妥协。', '中景，平视，固定镜头。豌豆站在斜坡下，低头看着躺在地上的萧绝。她脸上闪过一丝犹豫，随后轻轻叹了口气，肩膀微微放松，做出了决定。萧绝虚弱地躺在地上，眼神恳求地望着她。夕阳的光线在画面中缓慢移动。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (331, 1654, 1000012, 1000123, 112, 24, '萧绝露出感激而脆弱的微笑。', '特写镜头，平视视角，萧绝（阿绝）俊美无俦的脸上沾着泥土和血污，他虚弱地靠在豌豆臂弯中，嘴角微微上扬，露出一个感激又脆弱的微笑，夕阳的余晖从侧面洒在他的脸上，长睫在眼睑投下阴影，眸光如水，带着恳求与无助，背景是模糊的乱石堆和山林斜坡。', '特写镜头，固定拍摄萧绝（阿绝）的脸部。他虚弱地靠在豌豆臂弯中，微微抬起头，嘴角缓缓上扬，露出一个感激又脆弱的微笑，夕阳的余晖在他脸上移动，他的眼神清澈，带着恳求与无助，长睫轻颤。', NULL, NULL, '特写', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (332, 1654, 1000012, 1000123, 113, 25, '豌豆在山洞内生起一小堆篝火。', '中景镜头，平视视角，固定机位。一个穿着朴素古代农家服饰的少女（豌豆）蹲在山洞内，正用火石点燃一小堆干柴，篝火刚刚燃起，火光照亮了她专注的脸庞和周围粗糙的石壁。山洞内部环境昏暗，只有篝火作为主要光源。', '中景镜头，固定机位，平视视角。少女（豌豆）蹲在山洞内，用火石打火点燃一小堆干柴，篝火逐渐燃起，火苗从火星变为稳定的火焰，照亮她的侧脸和周围环境。火光在她脸上和石壁上投下摇曳的光影。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (333, 1654, 1000012, 1000123, 113, 26, '豌豆将竹筒水和野果放在萧绝身边。', '内景，后山一处隐蔽的山洞内部，夜晚，晴朗的夜晚。山洞石壁粗糙，中央有一小堆篝火，火光摇曳。近景俯拍视角，画面中，一只少女的手（豌豆）正将一个竹筒和几颗野果放在一个倚靠在石壁上的受伤男子（萧绝/阿绝）身边的地面上。男子身着破烂的玄色锦衣，脸色苍白，火光映照着他沾有血污的俊美侧脸。竹筒和野果是画面的焦点。', '内景，后山一处隐蔽的山洞内部，夜晚，晴朗的夜晚。近景俯拍固定镜头。画面中，豌豆的手入画，将一个竹筒和几颗野果轻轻放在倚靠在石壁上的萧绝身边的地面上。篝火的火光在竹筒和野果表面跳跃闪烁。萧绝虚弱地靠在石壁上，目光随着豌豆的动作移动。整个动作过程平稳而短暂。', NULL, NULL, '近景', '俯拍', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (334, 1654, 1000012, 1000123, 113, 27, '豌豆板着脸对萧绝说话，准备离开。', '近景镜头，平视视角。少女豌豆（穿着朴素农家衣裳，面容清秀，表情严肃）站在山洞内，篝火照亮她的侧脸。她微微低头，目光看向靠在石壁上的受伤男子萧绝（玄色锦衣破烂，脸色苍白，但容貌俊美）。画面焦点在豌豆严肃的表情和篝火的光影上。', '固定近景镜头。少女豌豆站在山洞内，篝火在她脸上投下跳动的光影。她表情严肃，嘴唇微动，说完话后，转身准备离开。镜头保持固定，记录她转身的动作。', NULL, NULL, '近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (335, 1654, 1000012, 1000123, 113, 28, '萧绝靠在石壁上，轻声询问豌豆芳名。', '近景镜头，平视角度。萧绝（阿绝）虚弱地靠在山洞内凹凸不平的石壁上，火光映照着他沾有泥土和血污的俊美侧脸。他微微抬眼，嘴唇微张，眼神中带着一丝恳求与迷茫，望向镜头外的方向。背景是山洞内昏暗的岩石壁和地面上跳动的篝火光影。', '近景固定镜头，平视角度。萧绝（阿绝）靠在山洞石壁上，篝火的光影在他脸上轻微晃动。他虚弱地抬起头，嘴唇微动，发出询问。', NULL, NULL, '近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (336, 1654, 1000012, 1000123, 113, 29, '豌豆头也不回地快步走出山洞。', 'A close-up shot of Xiao Jue (Ah Jue) inside a dimly lit mountain cave. He is leaning against a rough stone wall, his face illuminated by the flickering light of a small campfire. His previously vulnerable and helpless expression has completely vanished, replaced by a deep, calm, and calculating look in his eyes. The shadows cast by the fire dance across his handsome but now inscrutable features. The atmosphere is tense and mysterious.', 'Close-up, static shot of Xiao Jue inside a mountain cave at night. He is leaning against a stone wall, illuminated by a campfire. His facial expression undergoes a subtle but distinct transformation: the feigned weakness and fragility melt away, replaced by a sharp, alert, and contemplative demeanor. His eyes, previously clouded with pain, now gleam with intelligence and hidden purpose as he gazes into the fire. The firelight flickers on his face, emphasizing the change. No dialogue.', NULL, NULL, '近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (337, 1654, 1000012, 1000123, 113, 30, '萧绝独自在山洞内，表情和眼神瞬间变得深邃冷静。', 'A close-up shot of a handsome man (Xiao Jue) leaning against a rough cave wall. The flickering light of a campfire illuminates his face, casting dramatic shadows. His expression has completely changed from earlier vulnerability; his eyes are now deep, sharp, and calculating, staring into the distance with cold intelligence. His clothes are tattered and bloodstained, but his posture is alert and controlled. The background is a dim cave interior with a small fire.', 'Close-up, static shot of Xiao Jue in a cave. The firelight flickers on his face. His expression instantly shifts from fragile and grateful to cold, sharp, and calculating. His gaze becomes focused and penetrating as he looks off into the darkness of the cave, deep in thought. The change is subtle but complete.', NULL, NULL, '近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (338, 1654, 1000012, 1000123, 113, 31, '萧绝拿起竹筒嗅了嗅，低声自语。', '特写镜头，侧拍视角，萧绝（阿绝）独自坐在山洞内，篝火的火光在他俊美深邃的脸上投下跳动的阴影。他手中拿着一个竹筒，正低头凑近筒口，专注地嗅闻其中的泉水。他的眼神冷静而锐利，与之前的脆弱判若两人。山洞石壁作为背景，光线昏暗，氛围神秘。', '特写镜头，固定机位侧拍萧绝。他坐在山洞的石地上，篝火的光线照亮他的侧脸。他拿起身边的竹筒，低头凑近，鼻翼微动，仔细嗅闻筒中的泉水。他的眼神从之前的迷茫脆弱转变为深邃冷静，嘴角微微勾起一丝难以察觉的、带有兴味的弧度。整个动作缓慢而专注，篝火的光影在他脸上和手上轻微晃动。', NULL, NULL, '特写', '侧拍', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (339, 1654, 1000012, 1000123, 114, 32, '豌豆独自走在月色笼罩的山林小路上。', '中近景，平视角度，一位穿着朴素古代农家服饰的年轻女子（豌豆）独自走在月色笼罩的山林小路上，月光透过稀疏的树叶在她身上投下斑驳的光影，她微微低头，脸上带着一丝沉思和不安，背景是模糊的树木和灌木丛，整体色调偏冷，以蓝黑色和银白色为主，营造出夜晚的静谧和一丝神秘感。', '镜头从侧后方跟随豌豆在山林小路上行走，月光洒在她身上和周围的地面上，形成移动的光斑，她的脚步略显迟疑，然后逐渐加快，风吹动路边的树叶和她的发丝，营造出夜晚的动态感。', NULL, NULL, '中近景', '平视', '跟拍（从侧后方跟随豌豆）', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (340, 1654, 1000012, 1000123, 114, 33, '豌豆加快脚步，身影消失在夜色中。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, NULL, NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (366, 1654, 1000013, 1000165, 157, 1, '秦彻闭目养神，肚子发出咕噜声。', '中近景，平视，固定镜头。夜晚，城市天桥下。一位身穿洗白道袍的年轻道士（秦彻）盘腿坐在一个简陋的摊位前，摊位由一张铺在地上的粗布构成，上面放着几枚铜钱。他闭着眼睛，表情平静。背景是天桥的混凝土结构和远处模糊的城市灯光。画面氛围冷清、安静。', '中近景，固定镜头。夜晚，城市天桥下。年轻道士秦彻盘腿坐在简陋的摊位前，闭目养神。画面保持静止，只有背景远处偶尔有车灯划过。突然，秦彻的腹部发出轻微的“咕噜”声，打破了宁静。', 'uploads/storyboard_image/20260122/44e61b46551987578c3b2dce62d2009b_6971f85b4a7a9.png', NULL, '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (367, 1654, 1000013, 1000165, 157, 2, '张伟蹲在摊位前，秦彻为其看相。', '城市天桥下夜晚，灯光昏暗。一个穿着洗白道袍的年轻道士（秦彻）盘腿坐在摊位后，目光清澈，正专注地看着蹲在摊位前的中年男人（张伟）。张伟穿着皱巴巴的西装，满脸愁容，伸出手掌。两人之间是铺在地上的粗布摊位，上面放着几枚铜钱。背景是城市夜晚的天桥和稀疏的行人。', '城市天桥下夜晚，灯光昏暗。镜头固定，拍摄中近景。穿着洗白道袍的年轻道士秦彻盘腿坐在摊位后，目光专注地看着蹲在摊位前、穿着皱巴巴西装、满脸愁容的张伟。秦彻示意张伟伸出手掌，并仔细地观察他的手掌和面相（印堂发暗，眉间有断纹）。张伟的表情从愁苦转为期待和一丝紧张。秦彻没有碰触铜钱，只是静静地看了几秒。', 'uploads/storyboard_image/20260122/a5ee00229a13b32a430ac5fb63a57069_6971f85bb3021.png', NULL, '中近景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (368, 1654, 1000013, 1000165, 157, 3, '秦彻取黄纸画符递给张伟。', '近景镜头，平视视角。年轻道士秦彻（面容清秀，身穿洗得发白的旧道袍）蹲在天桥下的摊位前。他咬破了自己的指尖，正用指尖的鲜血在一张摊开的黄纸上快速绘制符文。他的表情专注而平静，眉头微皱。中年男人张伟（穿着皱巴巴的西装，满脸愁容）蹲在他对面，身体前倾，紧张而期待地看着秦彻画符的动作。画面焦点在秦彻画符的手和那张逐渐成型的血色符咒上。背景是夜晚天桥下的模糊光影和零星路人。', '近景固定镜头。秦彻咬破指尖，眉头微皱，随即神情专注，用指尖血在黄纸上快速、流畅地绘制出一道复杂的符咒。张伟蹲在对面，身体微微前倾，目光紧紧跟随着秦彻画符的动作，脸上交织着紧张、惊讶和一丝希望。秦彻画完最后一笔，将符纸拿起，轻轻吹了吹，然后仔细地折叠成一个三角形，递给张伟。整个过程动作连贯，带着一种仪式感。', 'uploads/storyboard_image/20260122/e1f1a45a695609e8e4b5ccadabdeb8f3_6971f847d11d2.png', NULL, '近景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (369, 1654, 1000013, 1000165, 157, 4, '张伟付钱离开，路人围拢过来。', '城市天桥下夜晚，秦彻的算命摊位前，秦彻盘腿坐在粗布后，粗布上有一张百元钞票和几枚铜钱。几个路人（包括一个中年男人、一个年轻女人和一个提着菜篮的大妈）围拢过来，好奇地打量着秦彻。背景是夜晚的城市灯光和天桥结构。', '固定中景镜头。张伟千恩万谢地离开画面。路人甲和路人乙从画面两侧好奇地走近，看向秦彻和摊位上的钞票。买菜大妈从人群中挤到最前面，急切地看着秦彻。秦彻平静地坐着，目光扫过围拢的人群。', 'uploads/storyboard_image/20260122/7056e1be80459b7000c76a9b11ef7aac_6971f7e831d8d.png', NULL, '中景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (370, 1654, 1000013, 1000165, 122, 5, '工位西北角的蓝色流水摆件特写。', '特写镜头，一个深蓝色的陶瓷招财流水摆件，水声潺潺，摆放在现代办公室工位的西北角，光线从侧面照亮，水波粼粼，整体氛围平静中带着一丝诡异。', '特写镜头，固定拍摄一个深蓝色的陶瓷招财流水摆件，水在摆件内部循环流动，发出轻微的潺潺水声，光线在水面上反射出粼粼波光，画面静止，只有水流在动。', 'uploads/storyboard_image/20260122/8d8c41ab7d07f25ba1b6534b6c39109a_6971f7e907940.png', NULL, '特写', '平视', '固定', NULL, NULL, 5000, NULL, NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (371, 1654, 1000013, 1000165, 123, 6, '柳如烟查看老宅照片和图纸。', '内景，柳家别墅书房，夜晚，晴朗。中近景俯拍视角，柳如烟穿着家居服，坐在书桌前，眉头微蹙，专注地看着面前摊开的老宅建筑图纸和几张显示水渍、墙皮剥落的照片。灯光柔和，营造出安静而忧虑的氛围。', '内景，柳家别墅书房，夜晚，晴朗。固定镜头，中近景俯拍。柳如烟坐在书桌前，穿着家居服，神色忧虑。她低头看着面前摊开的老宅建筑图纸，手指轻轻划过图纸上的某个区域。随后，她的目光移向旁边的几张照片，拿起其中一张，仔细端详照片中墙皮剥落和水渍的细节，表情越发凝重。整个镜头氛围安静，只有纸张翻动的细微声响。', 'uploads/storyboard_image/20260122/6834a6f73e861123caf6ba0a52821a6e_6971f7ed13c69.png', NULL, '中近景', '俯拍', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (372, 1654, 1000013, 1000165, 123, 7, '柳如烟打电话询问爷爷病情。', '中近景，平视镜头，柳如烟穿着家居服，坐在书桌前，一手拿着手机贴在耳边，另一只手按在摊开的老宅照片和图纸上，表情忧虑，眼神凝重，背景是书房的书架和台灯。', '固定中近景镜头，柳如烟穿着家居服坐在书桌前，她正通过手机通话，表情从询问转为担忧，眼神看向桌上的老宅照片，手指无意识地摩挲着照片边缘。', 'uploads/storyboard_image/20260122/5419d0ee23168856c8d33dde6c015042_6971f7ed9c62a.png', NULL, '中近景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (373, 1654, 1000013, 1000165, 123, 8, '柳如烟挂断电话，疲惫地揉着太阳穴。', '中近景，平视镜头，固定拍摄。柳如烟穿着家居服，坐在书桌前，刚刚放下手机。她眉头微蹙，脸上带着疲惫和忧虑，抬起一只手，用指尖轻轻按压着自己的太阳穴。书桌上摊开着老宅的建筑图纸和几张显示异常状况的照片。背景是装饰典雅的书房，光线柔和。', '中近景，固定镜头。柳如烟放下手机，手机屏幕变暗。她闭上眼，深吸一口气，然后抬起右手，用食指和中指的指腹，缓慢而用力地按压右侧太阳穴，随后手移动到左侧太阳穴重复动作。她的眉头始终微蹙，嘴唇紧抿，表现出身体和精神的双重疲惫。', 'uploads/storyboard_image/20260122/13953f99ee0e66fbb4583a640262f450_6971f7fbe4b0d.png', NULL, '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (374, 1654, 1000013, 1000165, 124, 9, '林薇向柳如烟汇报天桥下小道士的消息。', '内景，柳如烟的办公室，白天，晴朗。中近景，柳如烟坐在办公桌后，正抬头看向站在办公桌前的助理林薇。柳如烟穿着职业套装，表情专注。林薇微微俯身，压低声音，表情谨慎。办公室环境现代、明亮、整洁。', '镜头固定，拍摄柳如烟办公室内的中近景。柳如烟坐在办公桌后处理文件，林薇敲门后进入，走到办公桌前，开始低声汇报。柳如烟停下手中的工作，抬头看向林薇，专注地倾听。林薇汇报时，表情谨慎，声音压低。', 'uploads/storyboard_image/20260122/8e5bc9f8abce31346df55862e0792630_6971f7f2e50d9.png', NULL, '中近景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (375, 1654, 1000013, 1000165, 124, 10, '柳如烟走到窗边，决定亲自前往。', '中近景，柳如烟站在办公室的落地窗前，身穿职业套装，表情严肃而果断，目光坚定地望向窗外的城市景色，窗外是傍晚的城市天际线，光线从窗外照入，勾勒出她的轮廓。', '中近景，固定镜头，柳如烟从办公桌后站起身，走到落地窗前，她停下脚步，目光投向窗外，表情从沉思转为果断，嘴唇微动，说出决定性的台词。', 'uploads/storyboard_image/20260122/354ab9cb3e3b85c1072894d86c30a5d1_6971f7f281fdd.png', NULL, '中近景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (376, 1654, 1000013, 1000165, 157, 11, '秦彻为老太太看手相，摊位前有围观者。', '城市天桥下，傍晚，晴朗天气。年轻道士秦彻盘腿坐在摊位后，正低头专注地为一位老太太看手相。老太太坐在他对面的小凳上，伸着手。摊位周围站着五六名围观的路人，目光都聚焦在秦彻和老太太身上。背景是天桥的桥墩和傍晚的城市街道。', '镜头固定，中景。秦彻（年轻道士，穿着洗白的道袍）正低头看着老太太伸出的手掌，他的手指虚点着老太太的掌纹，嘴唇微动，似乎在讲解。老太太（老年女性）认真听着，偶尔点头。周围的几名围观路人（男女老少皆有）有的抱着手臂，有的微微前倾身体，都在安静地观看。傍晚的光线柔和地洒在他们身上。', 'uploads/storyboard_image/20260122/967bb52fd2799c6f91a9eaeccea04135_6971f7f7d122c.png', NULL, '中景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (377, 1654, 1000013, 1000165, 157, 12, '秦彻收拾摊位，拿起馒头离开。', '中景镜头，平视角度，城市天桥下傍晚，光线柔和。年轻道士秦彻穿着洗白的道袍，蹲在地上，正将几枚铜钱收进一个旧布包里。他身旁铺着一块深褐色粗布，他拿起地上的一个装着馒头的塑料袋，准备起身离开。背景是桥墩和傍晚的城市街景，有零星路人经过。', '中景固定镜头。秦彻蹲在天桥下，将铜钱放入旧布包，然后拿起地上的馒头塑料袋，站起身，拍了拍道袍上的灰尘，背起布包，转身准备离开。傍晚的光线在他身上投下长长的影子。', 'uploads/storyboard_image/20260122/7227757b60f634323df7234a336d8b7a_6971f7f776cb5.png', NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (378, 1654, 1000013, 1000165, 157, 13, '远处黑色轿车内，柳如烟观察秦彻并认出他。', '中近景，侧拍视角，柳如烟坐在一辆黑色豪华轿车的后座，车窗降下一半。她戴着墨镜，目光透过车窗望向天桥下的方向，表情专注而略带审视。车内光线柔和，窗外是傍晚的城市街景和天桥的局部。', '固定镜头，中近景，侧拍视角。柳如烟坐在黑色轿车后座，车窗半降。她戴着墨镜，目光专注地望向窗外天桥下的方向。她微微侧头，似乎在确认什么。随后，她抬起手，缓缓摘下墨镜，身体微微前倾，眼神中闪过一丝惊讶和确认。整个镜头保持稳定，焦点在柳如烟的面部表情和眼神变化上。', 'uploads/storyboard_image/20260122/13371619a4076455d82b60f272fb603c_6971f7fb19864.png', NULL, '中近景', '侧拍/平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (379, 1654, 1000013, 1000165, 157, 14, '黑色轿车启动，缓慢尾随步行的秦彻。', '外景·城市街道·傍晚·晴朗，中景，侧拍角度，镜头跟随步行的秦彻。秦彻穿着洗白的道袍，背着旧布包，手里提着一袋馒头，在人行道上不紧不慢地走着。在他身后稍远处，一辆低调的黑色轿车缓慢行驶在车道上，与秦彻保持平行。轿车后车窗半开，隐约可见车内坐着的人影。街道两旁是城市建筑和稀疏的行人，天色渐暗，路灯初亮。', '镜头以中景、侧拍角度跟拍步行的秦彻。秦彻在人行道上向前走，步伐平稳，偶尔低头看看手中的馒头袋。背景中，一辆黑色轿车缓慢而平稳地行驶在平行的车道上，与秦彻保持大致相同的速度。轿车车窗半开，车内人物轮廓隐约可见。镜头跟随秦彻移动约10秒，营造出一种被暗中观察和尾随的紧张氛围。', 'uploads/storyboard_image/20260122/773c252b30e269eb11a17dc2f76f4fab_6971f7f5c5d0d.png', NULL, '中景', '侧拍', '跟拍', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (380, 1654, 1000013, 1000164, 79, 2, '秦彻向师父行礼，师父递给他一枚铜钱', '外景，终南山道观外山道，清晨薄雾缭绕。身穿洗得发白的青色道袍的青年秦彻，恭敬地向一位白发老道（师父）行礼。师父手持拂尘，正将一枚古朴的铜钱放入秦彻摊开的手掌中。背景是古朴的道观建筑和朦胧的山林。', '清晨的薄雾在山道间缓缓流动。中景固定镜头，秦彻向师父躬身行礼，师父将一枚古朴的铜钱轻轻放在秦彻摊开的手掌上。秦彻的手微微合拢，握住铜钱。', 'uploads/storyboard_image/20260106/682f75ff2fffa3ad663e8231010cea0c_695c887d42ebd.png', 'uploads/storyboard_video/20260106/f808d4699b320844c91ecdc5ff13ad6d_695c897169ed5.mp4', '中景', '平视', '固定', NULL, NULL, 5000, '', '', 'video');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (398, 1654, 1000012, 1000122, 129, 1, '特写电脑屏幕代码飞速滚动，右下角时间显示03:47', '特写镜头，电脑屏幕，屏幕上代码行飞速向上滚动，屏幕右下角显示时间03:47，背景是黑暗的办公室环境，屏幕光映照出键盘轮廓。', '特写镜头，固定拍摄电脑屏幕，屏幕上代码行持续快速向上滚动，右下角时间数字03:47保持静止。', 'uploads/storyboard_image/20260115/b4aa7899c171f2b4bd970baf34374ed3_6968d211695fe.png', NULL, '特写', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (399, 1654, 1000012, 1000122, 129, 2, '中景工位前，面容憔悴的年轻女子豌豆猛灌一口咖啡，手指在键盘上敲出残影', '中景镜头，平视角度，固定拍摄。一位面容憔悴的年轻女子（豌豆，25岁，穿着现代休闲装）坐在写字楼的工位前。她正举起一个白色马克杯喝咖啡，眼睛盯着面前的电脑屏幕。她的另一只手在键盘上快速敲击，手指几乎形成残影。背景是深夜的办公室，光线昏暗，只有屏幕光和远处几盏应急灯。', '中景镜头，固定拍摄，平视角度。画面中心是面容憔悴的年轻女子豌豆。她举起白色咖啡杯猛喝一口，然后放下杯子。她的右手在黑色键盘上以极快的速度敲击，手指动作模糊不清。她的眼睛紧盯着面前的电脑屏幕，表情专注而疲惫。背景是深夜寂静的办公室环境。', 'uploads/storyboard_image/20260121/63fd26867b64d849a965c777527e1208_6970c83b6792b.png', NULL, '中景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (400, 1654, 1000012, 1000122, 129, 3, '镜头晃动，豌豆突然捂住胸口，脸色煞白，眼前发黑', '中景镜头，平视角度，手持晃动效果。画面中，一位面容憔悴的年轻女子（豌豆，25岁，现代装束）坐在办公椅上，突然捂住胸口，脸色瞬间变得苍白，眼神失焦，身体因不适而微微前倾。背景是深夜的现代写字楼工位，光线昏暗，只有电脑屏幕的光映在她脸上。', '中景镜头，手持晃动拍摄。画面从豌豆敲击键盘的手部特写快速上摇至她的面部。她突然停下动作，表情痛苦，左手捂住胸口，右手撑在桌面上。镜头随着她的身体晃动而轻微晃动，模拟第一人称眩晕感。她的脸色在屏幕光下迅速失去血色，眼神涣散，仿佛要晕倒。', 'uploads/storyboard_image/20260121/e32abaf46bdd70b2eadc44ec2adb412b_69703bf1e0ab2.png', NULL, '中景', '平视', '手持晃动', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (401, 1654, 1000012, 1000122, 129, 4, '特写咖啡杯倾倒，褐色液体浸湿键盘', '特写镜头，俯拍视角，一个倾倒的白色陶瓷咖啡杯，深褐色的咖啡液体正从杯口流出，浸湿下方一个黑色电脑键盘，液体在黑色按键间蔓延，键盘上反射着屏幕的微光，背景是模糊的办公桌桌面。', '特写镜头，固定俯拍，一个白色陶瓷咖啡杯缓缓倾倒，深褐色的咖啡液体从杯口持续流出，液体接触到黑色电脑键盘，迅速在按键缝隙间扩散、浸湿，键盘表面反射着屏幕的微光，液体流动过程持续5秒。', 'uploads/storyboard_image/20260122/3d0011bc0a387a72abc1ce580c546f87_6971f283c60bd.png', NULL, '特写', '俯拍', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (402, 1654, 1000012, 1000122, 129, 5, '黑屏', 'A cinematic extreme close-up shot of a completely black screen, no light, no texture, pure black.', 'A static, completely black screen, no light, no movement, no texture, pure black.', 'storyboard/image/20260122/52482a8e930a7d559a5250c1d241de48_6971f42567549.png', NULL, '特写', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (403, 1654, 1000012, 1000122, 130, 6, '主观镜头视线模糊，看到茅草屋顶，土墙裂缝', '主观视角，仰视茅草屋顶和开裂的土墙，视线模糊，清晨微弱的光线从屋顶缝隙透入，营造出朦胧、陌生的氛围。', '主观视角镜头，仰视着茅草屋顶和开裂的土墙，视线从模糊逐渐变得清晰，模拟角色刚刚恢复意识时的视觉体验。', NULL, NULL, '主观镜头', '仰拍', '固定', NULL, NULL, 5000, NULL, NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (404, 1654, 1000012, 1000122, 130, 7, '镜头聚焦，豌豆猛地睁眼，发现自己躺在一张硬板床上，身上盖着打补丁的粗布被', 'A close-up shot of a young woman (Pea) lying on a hard wooden bed in a dimly lit ancient peasant hut. She is covered by a patched coarse cloth quilt. Her eyes are wide open with shock and confusion as she looks around. The setting is poor and simple, with a thatched roof and earthen walls visible in the background. The lighting is soft morning light filtering in, highlighting her bewildered expression.', 'Close-up, static shot of a young woman (Pea) lying on a hard bed in a rustic hut. She suddenly opens her eyes wide, showing shock and confusion. Her gaze darts around the unfamiliar surroundings. She is covered by a patched, coarse quilt. The scene is dimly lit by morning light.', NULL, NULL, '近景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (405, 1654, 1000012, 1000122, 130, 8, '环视家徒四壁，只有一张破桌，两个木凳', '主观视角镜头，从硬板床上环视一间极其简陋的古代农家土屋内部。画面中心是一张破旧的方形木桌和两个简陋的木凳，构成主要前景。背景是开裂的土墙和茅草屋顶，几缕清晨的阳光从屋顶缝隙和墙缝中斜射进来，形成光柱。屋内光线昏暗，充满尘埃感。构图强调空间的狭窄和家徒四壁的贫困感。', '主观视角镜头缓慢地从床铺位置开始，向左摇摄，扫过空荡的土屋内部。镜头首先经过开裂的土墙，然后停留在前景的破旧木桌和木凳上，最后继续摇摄，展示屋内再无其他像样家具的贫瘠景象。清晨的阳光光束在移动中缓慢变化，尘埃在光柱中漂浮。整个镜头运动平稳而略带沉重感，模拟角色苏醒后观察环境的视线。', NULL, NULL, '中景', '主观视角（豌豆视角）', '缓慢摇摄', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (406, 1654, 1000012, 1000122, 130, 9, '近景床边围着一对穿着粗布衣、面容愁苦的中年夫妇，以及四个身材壮实、神情憨厚的年轻男子', '中景镜头，平视角度，固定机位。画面中央是一张简陋的硬板床，床上躺着一位年轻女子（豌豆）。床边围着一对穿着粗布衣、面容愁苦的中年夫妇（豌豆父母），以及四个身材壮实、神情憨厚的年轻男子（豌豆的四个哥哥）。背景是家徒四壁的土屋，光线从简陋的窗户照入，形成清晨的氛围。', '中景镜头，平视角度，固定机位。床上，豌豆（年轻女子）睁着眼睛，眼神震惊。床边，一对愁苦的中年夫妇和四个憨厚的年轻男子关切地围着她。画面静止，焦点在豌豆和床边家人的表情上。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (407, 1654, 1000012, 1000122, 130, 10, '豌豆娘抹泪说醒了醒了，豌豆爹搓手局促地问闺女还有哪儿不舒服', '近景，平视镜头，固定拍摄。一位面容愁苦、穿着粗布衣的中年妇女（豌豆娘）正用手抹去眼角的泪水，脸上带着如释重负的表情。旁边，一位同样穿着粗布衣、神情局促的中年男子（豌豆爹）搓着手，身体微微前倾，关切地看着床上。背景是简陋的农家土屋，可以看到茅草屋顶和土墙的裂缝。床上铺着打满补丁的粗布被。', '近景，平视，固定镜头。画面中，豌豆娘抹着眼泪，嘴唇微动，说着“醒了醒了！菩萨保佑！”。紧接着，豌豆爹搓着手，身体微微前倾，局促地开口询问。两人的表情和动作连贯自然，背景是静态的简陋土屋环境。', NULL, NULL, '近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (408, 1654, 1000012, 1000122, 130, 11, '豌豆震惊，内心独白我穿越了？还是个快嫁不出去的村姑？', '近景镜头，平视视角，年轻女子豌豆（25岁，面容憔悴，现代装束）躺在古代农家硬板床上，盖着打补丁的粗布被，她双眼圆睁，表情极度震惊和困惑，背景是简陋的茅草屋顶和土墙裂缝。', '近景固定镜头，平视视角，年轻女子豌豆（25岁，面容憔悴，现代装束）躺在古代农家硬板床上，盖着打补丁的粗布被，她猛地睁开眼睛，瞳孔收缩，脸上流露出从迷茫到极度震惊的表情变化，眼神中充满困惑和难以置信。', NULL, NULL, '近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (409, 1654, 1000012, 1000122, 131, 12, '全景破败小院，篱笆歪斜，鸡鸭稀疏', 'A wide-angle, top-down view of a dilapidated and impoverished ancient Chinese farmyard. The fence is crooked and broken in several places. A few sparse chickens and ducks are scattered around the yard, pecking at the ground. The yard is made of packed earth, with no greenery or decorations, conveying a sense of poverty and desolation. The lighting is natural daylight, clear and bright.', 'A static, wide-angle, top-down shot of a poor ancient Chinese farmyard. The crooked fence and sparse chickens and ducks remain still within the frame. The lighting is consistent daylight. The overall atmosphere is quiet and desolate.', NULL, NULL, '全景', '俯拍', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (410, 1654, 1000012, 1000122, 131, 13, '中景豌豆被母亲搀扶着走到院中，四个哥哥殷勤地搬来唯一完好的竹椅', '中景镜头，平视视角，农家小院内，晴天。面容憔悴的年轻女子豌豆被一位面容愁苦的中年妇女搀扶着站在院子中央。四个身材壮实、神情憨厚的年轻男子正殷勤地将一把旧竹椅搬到豌豆面前。背景是破败的农家小院，篱笆歪斜，鸡鸭稀疏。', '中景固定镜头，画面中，豌豆娘搀扶着豌豆走到院子中央。四个哥哥（大哥、二哥、三哥、四哥）从画面两侧入画，争先恐后地将一把旧竹椅搬到豌豆面前，脸上带着憨厚和关切的表情。豌豆看着他们，表情复杂。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (411, 1654, 1000012, 1000122, 131, 14, '大哥憨笑说小妹坐，哥给你摘野果子去，二哥说我去挑水，三哥四哥也抢着干活', 'A medium shot, eye-level, static camera. In a dilapidated rural courtyard with a crooked fence and sparse chickens and ducks, a young woman (Pea) is being helped to sit in a worn but intact bamboo chair by her mother. Four sturdy, simple-looking young men (her brothers) stand around her, their faces showing concern and eagerness to help. The lighting is natural daylight on a sunny day.', 'A medium shot, eye-level, static camera. In a sunny, dilapidated rural courtyard, Pea sits in a bamboo chair. Her four brothers, with simple and honest expressions, speak to her one after another, showing their eagerness to help. The eldest brother speaks first with a silly smile, followed by the second brother, then the third and fourth brothers also gesture as if vying to do chores.', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (412, 1654, 1000012, 1000122, 131, 15, '近景豌豆看着家人毫无保留的关爱，心中复杂', 'A close-up shot of a young woman (Pea) standing in a dilapidated rural courtyard. She wears coarse, patched clothing. Her expression is complex, a mixture of warmth, guilt, and determination. In the background, slightly out of focus, are her four burly brothers busying themselves with chores. The lighting is natural daylight, casting soft shadows. The style is cinematic, realistic, with shallow depth of field.', 'Close-up, static shot of Pea\'s face. Her eyes subtly shift, reflecting a complex internal struggle as she observes her family\'s actions off-screen. The background remains softly blurred, with slight movement indicating her brothers\' activity. Natural daylight.', NULL, NULL, '近景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (413, 1654, 1000012, 1000122, 131, 16, '豌豆娘叹气说闺女，娘知道你不甘心，再过三月你就满十八了，按律法', '近景镜头，一位面容愁苦、穿着粗布衣的中年农妇（豌豆娘）站在破败的农家小院里，她正对着镜头方向说话，眼神充满忧虑和无奈，背景是歪斜的篱笆和稀疏的鸡鸭。', '近景固定镜头，中年农妇（豌豆娘）站在农家小院中，她看着镜头方向，嘴唇翕动，表情从担忧转为深深的叹息，眼神中流露出对女儿未来的焦虑。', NULL, NULL, '近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (414, 1654, 1000012, 1000122, 131, 17, '闪回记忆片段原主叉腰骂街、摔盆砸碗的画面', '中景镜头，平视角度，固定拍摄。画面中，年轻的农家女豌豆（25岁，面容清秀但此刻表情复杂）站在农家小院里，她正用手扶着自己的额头，眼神中流露出无奈和思索。背景是破败的农家小院，篱笆歪斜，鸡鸭稀疏。画面风格为古风写实，光线为白天的晴天光照。', '中景镜头，平视角度，固定拍摄。画面中，年轻的农家女豌豆站在农家小院里，她正用手扶着自己的额头，眼神中流露出无奈和思索。背景是破败的农家小院，篱笆歪斜，鸡鸭稀疏。画面风格为古风写实，光线为白天的晴天光照。镜头固定不动，人物有轻微的呼吸起伏和表情变化。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (415, 1654, 1000012, 1000122, 131, 18, '豌豆扶额，内心独白原主这战斗力难怪', 'A close-up shot of a young woman, Douwan, in simple ancient peasant clothing, standing in a dilapidated farmyard. She is holding her forehead with one hand, her expression is a mixture of realization and resignation. The background shows a rundown courtyard with a tilted bamboo fence and sparse chickens and ducks. The lighting is bright daytime sun.', 'Close-up, static shot of a young woman in ancient peasant clothes. She stands in a simple farmyard, raises her hand to her forehead, and holds the pose, her expression shifting subtly from thoughtfulness to a wry acceptance. The background is a static, slightly rundown courtyard.', NULL, NULL, '近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (416, 1654, 1000012, 1000122, 132, 19, '中景豌豆借口散心，独自来到后山，她想理清思绪，寻找出路', '中景镜头，一位穿着粗布古代衣裙的年轻女子（豌豆）独自站在后山的树林中，她面容带着困惑和思索，眼神望向远方，背景是茂密的树木和透过树叶的阳光。', '中景固定镜头，年轻女子豌豆站在后山树林中，她环顾四周，脸上表情从困惑转为沉思，似乎在寻找什么，风吹动她的发丝和衣角，树叶微微晃动。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (417, 1654, 1000012, 1000122, 132, 20, '豌豆对着空气，低声说系统？金手指？老爷爷？', '中近景镜头，一位穿着粗布古代衣裙的年轻女子（豌豆）站在后山树林中，她面容清秀但带着困惑和一丝期待，正对着空气低声说话，阳光透过树叶在她身上投下斑驳光影，背景是茂密的树木和灌木丛。', '中近景固定镜头，古代女子豌豆站在后山树林中，她环顾四周，然后对着空气低声说话，表情从困惑转为一丝期待，嘴唇微动，说完后停顿等待回应，周围只有风吹树叶的轻微声响。', NULL, NULL, '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (418, 1654, 1000012, 1000122, 132, 21, '无回应', 'A close-up shot of a young woman, Pea, standing alone in a lush forest in the afternoon. She is dressed in simple, patched ancient peasant clothing. Her expression is one of confusion and disappointment as she looks around expectantly but receives no response. Sunlight filters through the leaves, dappling the forest floor around her.', 'Close-up, static shot of Pea in ancient peasant clothes, standing in a sunlit forest. She looks around with an expectant expression, waiting for a response that never comes. Her expression slowly shifts from anticipation to disappointment. Gentle breeze rustles leaves.', NULL, NULL, '近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (419, 1654, 1000012, 1000122, 132, 22, '特写她沮丧地踢了一脚路边石子', 'A close-up shot from a low angle, focusing on a woman\'s foot in a worn-out cloth shoe kicking a grey-brown, rough-surfaced stone on a forest floor covered with fallen leaves and weeds. The background is a blur of green forest under a sunny afternoon sky.', 'Close-up shot, low angle, fixed camera. A woman\'s foot in a simple cloth shoe kicks a stone on the forest floor, causing it to roll a short distance. The action is quick and frustrated. The lighting is dappled sunlight filtering through leaves.', NULL, NULL, '特写', '低角度平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (420, 1654, 1000012, 1000122, 132, 23, '叮一声轻微脆响，似在脑海响起', '特写镜头，主观视角，画面中心是豌豆的侧脸，她正低头看着脚下的石子，表情沮丧。背景是模糊的树林和草地。画面中没有任何声音来源的视觉提示，但通过豌豆突然微怔的表情和眼神变化，暗示她听到了什么。', '特写镜头，固定机位，模拟豌豆的主观视角。画面中，穿着粗布衣的年轻女子（豌豆）正低头看着地面，她刚刚沮丧地踢了一脚石子。突然，她身体微微一僵，眼神从地面移开，转向虚空，表情从沮丧变为惊疑，仿佛听到了什么只有她能听到的声音。画面无声，但通过演员的表演传达出“听到声音”的瞬间。', NULL, NULL, '特写', '主观视角', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (421, 1654, 1000012, 1000122, 132, 24, '豌豆眼前突然浮现一片朦胧景象一汪清澈见底的泉水，泉边雾气氤氲，旁边似乎还有一片静止的灰蒙区域', 'A close-up, first-person perspective shot of a young woman\'s face (Pea) looking ahead with a surprised and confused expression. In front of her eyes, a translucent, shimmering, dream-like overlay appears, showing a clear, bottomless spring with mist rising from its edges. Next to the spring is a static, gray, hazy area. The background is a blurry forest. The lighting is soft, dappled sunlight filtering through leaves.', 'Close-up, static shot of a woman\'s face (Pea) in a forest. Her expression shifts from frustration to sudden surprise and confusion. A translucent, shimmering, dream-like overlay materializes in front of her eyes, showing a clear, bottomless spring with mist gently rising from its edges. Next to the spring is a static, gray, hazy area that does not move. The overlay remains stable and slightly transparent, allowing the blurred forest background to be faintly visible behind it. The lighting is soft, dappled sunlight.', NULL, NULL, '特写', '主观视角', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (422, 1654, 1000012, 1000122, 132, 25, '豌豆惊疑不定说这是灵泉？空间？', 'A close-up shot of a young woman, Douwan, standing in a forest in the afternoon. She wears simple, patched coarse cloth clothing. Her face shows a mixture of shock, confusion, and dawning realization as she stares into the middle distance, as if seeing something invisible. The forest light filters through the leaves, illuminating her expression. The background is a blur of green foliage.', 'A close-up, static shot of a young woman in ancient peasant clothing in a forest. Her eyes widen slightly, then narrow in thought. Her lips part as she whispers to herself. The focus is entirely on her face and subtle changes in her expression, from confusion to suspicion to a spark of understanding. The background is a soft, out-of-focus forest scene with dappled sunlight.', NULL, NULL, '近景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (423, 1654, 1000012, 1000122, 132, 26, '她意念微动，手中出现一颗刚才踢到的石子，再一动，石子消失', '特写镜头，平视视角，一位面容清秀但带着疲惫的年轻古代女子（豌豆）站在后山树林中，她低头凝视着自己的右手掌心，掌心上方悬浮着一颗灰色的小石子，石子周围有极其微弱、几乎不可见的淡蓝色光晕。背景是模糊的树林和午后阳光透过树叶的光斑。画面焦点集中在手掌和石子上，女子脸上带着一丝惊疑和专注。', '特写镜头，固定机位，平视。画面中心是古代女子豌豆的右手。她意念微动，一颗灰色的小石子凭空出现在她的掌心上方，悬浮片刻。紧接着，石子微微闪烁一下，瞬间消失无踪，仿佛从未出现过。整个过程伴随着轻微的、非现实的“嗡”声或空间波动音效。女子的手指在石子出现和消失时，有轻微的、下意识的收拢动作。', NULL, NULL, '特写', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (424, 1654, 1000012, 1000122, 132, 27, '豌豆眼睛亮起，握拳说有救了！虽然开局地狱难度，但有这个，至少饿不死', '近景镜头，平视视角，一位穿着古代粗布衣的年轻女子（豌豆）站在后山树林中，她眼睛突然亮起，脸上露出充满希望的表情，右手握拳，做出一个充满决心的动作。背景是郁郁葱葱的树林和草地，午后阳光透过树叶缝隙洒下斑驳光影。', '近景镜头，固定拍摄，古代农家女豌豆站在树林中，她的表情从沮丧迅速转变为充满希望和决心，眼睛发亮，右手握拳举到胸前，嘴唇微动，说出台词。背景是静态的树林和草地，午后阳光斑驳。', NULL, NULL, '近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (425, 1654, 1000012, 1000122, 132, 28, '她开始观察山林，寻找可能利用的植物', '一位穿着粗布古代衣裙的年轻女子（豌豆）站在山林中，她眼神专注，微微弯腰，正在仔细地观察周围的植物。阳光透过树叶洒在她身上，背景是茂密的树林和灌木丛。', '镜头跟随豌豆在山林中缓慢移动，她边走边停下，目光扫视着周围的树木、草丛和地面，时而蹲下查看低矮的植物，时而伸手触摸树叶，表现出在寻找和辨认可用植物的状态。', NULL, NULL, '中景', '平视', '跟拍', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (426, 1654, 1000012, 1000122, 133, 29, '远景夕阳西下，山林染金', '远景，夕阳西下，金色的阳光洒满茂密的山林，天空呈现橙红渐变色，树木被染上温暖的金色光晕，画面宁静而壮丽。', '远景镜头，夕阳缓缓沉入远山，金色的光芒逐渐变化，山林被染上温暖的色调，镜头缓慢拉远，展现山林的全貌。', NULL, NULL, '远景', '平视', '缓慢拉远', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (427, 1654, 1000012, 1000122, 133, 30, '中景豌豆发现几株野生辣椒和疑似可制皂的植物，小心采摘', '中景，平视，固定镜头。山林中，年轻的农家女豌豆（25岁，面容清秀，穿着粗布衣）正蹲在地上，小心翼翼地采摘几株野生辣椒和几片肥厚的植物叶片。她神情专注，动作轻柔，将采摘的植物放入身后的竹篓中。背景是郁郁葱葱的山林，阳光透过树叶缝隙洒下斑驳光影。', '中景，固定镜头。画面中，豌豆蹲在山林的地上，她伸出手，小心翼翼地用手指掐断一株野生辣椒的茎，然后轻轻摘下几片肥厚的植物叶片。她仔细检查了手中的植物，确认无误后，转身将它们放入身后的竹篓中。整个过程动作连贯、专注。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (428, 1654, 1000012, 1000122, 133, 31, '突然，她脚步一顿', '一位穿着粗布衣的年轻女子（豌豆）背着竹篓，在光线斑驳的山林中停下脚步，她微微低头，目光警惕地看向前方某处，表情从专注转为警觉。', '镜头固定，中景拍摄豌豆在山林中行走并突然停下的动作。她原本在小心地采摘植物，动作轻快，但突然身体一僵，脚步顿住，头部微微转向侧前方，表情瞬间变得警觉。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (429, 1654, 1000012, 1000122, 133, 32, '特写前方灌木丛中，露出一角染血的玄色锦衣', '特写镜头，画面中心是茂密的绿色灌木丛，其中一角华贵的玄色锦衣布料被勾出，布料上浸染着暗红色的新鲜血迹，血迹在光线下显得湿润，背景是模糊的森林环境。', '特写镜头，固定拍摄灌木丛中露出的染血玄色锦衣一角，光线透过树叶缝隙，在布料和血迹上形成斑驳光影，无风，画面静止。', NULL, NULL, '特写', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (430, 1654, 1000012, 1000122, 133, 33, '镜头推进一个男子面朝下倒在地上，背部一道狰狞伤口，鲜血浸透衣衫，侧脸轮廓完美', '特写镜头，俯拍角度，一个穿着染血玄色锦衣的男子面朝下倒在林间落叶上，背部有一道狰狞的伤口，鲜血浸透了衣衫。他的侧脸轮廓完美，线条分明，但双眼紧闭，处于昏迷状态。背景是模糊的灌木丛和傍晚的树林。', '特写镜头，俯拍角度，镜头从灌木丛中染血的衣角开始，缓慢向前推进，聚焦于倒在地上的男子。他面朝下，背部伤口清晰可见，鲜血在锦衣上缓慢洇开。镜头最终定格在他完美的侧脸轮廓上。傍晚的林间光线昏暗，营造出神秘和危险的氛围。', NULL, NULL, '特写', '俯拍', '推进', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (431, 1654, 1000012, 1000122, 133, 34, '豌豆警惕后退两步，内心独白路边的男人不能捡！尤其是长得这么祸水的！', '中景镜头，平视视角，固定机位。画面中心是一位穿着古代粗布衣的年轻女子（豌豆），她正警惕地后退两步，身体微微后倾，眼神充满戒备和犹豫，看向画面左下角。背景是傍晚时分的山林，光线昏暗，灌木丛茂密。', '中景镜头，平视视角，固定机位。年轻女子（豌豆）在看到灌木丛中的男子后，身体做出明显的后退动作，脚步向后挪动两步，脸上表情从惊讶转为警惕和犹豫，眼神看向画面左下角。背景是傍晚的山林，光线逐渐变暗。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (432, 1654, 1000012, 1000122, 133, 35, '她犹豫片刻，终究没上前，转身准备离开', '一个穿着粗布衣的年轻女子（豌豆）站在山林中，她背对着镜头，身体微微前倾，目光从前方地面（灌木丛方向）收回，脸上带着犹豫和警惕的表情，正准备转身离开。背景是傍晚的山林，光线昏暗。', '镜头固定，侧拍中景。画面中，豌豆站在山林小路上，她看着前方灌木丛的方向，身体语言显示出犹豫和警惕。她微微摇头，然后下定决心，身体开始转向，准备沿来路离开。整个过程持续5秒。', NULL, NULL, '中景', '侧拍', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (433, 1654, 1000012, 1000122, 133, 36, '就在她转身刹那，地上男子睫毛几不可察地颤动了一下，嘴角勾起一丝极淡的、冰冷的弧度', '特写镜头，俯视角度，一位身着染血玄色锦衣的俊美男子面朝下倒在山林灌木丛中，他的长睫毛微微颤动，嘴角勾起一丝极淡、冰冷的弧度，背景是傍晚时分的山林地面和枯叶。', '特写镜头，俯拍，固定机位。画面聚焦于倒地的男子（萧绝）侧脸。他的睫毛极其轻微地颤动了一下，嘴角勾起一丝转瞬即逝、冰冷而意味深长的弧度，随即恢复昏迷状。整个动作细微而克制。', NULL, NULL, '特写', '俯拍', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (434, 1654, 1000012, 1000122, 133, 37, '镜头拉远，暮色笼罩山林，豌豆身影消失在小路尽头，地上昏迷的男子缓缓睁开了眼，眸色深沉如夜', '全景俯拍镜头，暮色笼罩下的山林，一条蜿蜒的小路通向远方。前景中，一个穿着粗布衣的年轻女子（豌豆）的背影正沿着小路远去，身影逐渐变小。中景处，地上躺着一位身穿染血玄色锦衣的男子（萧绝），他面朝下倒在灌木丛旁。整个画面色调偏暗，以橙红和深蓝为主，营造出傍晚的静谧与神秘感。', '镜头从近景开始，缓缓向后上方拉远。画面中，豌豆的背影沿着林间小路越走越远，最终消失在暮色笼罩的树林深处。与此同时，镜头拉远至全景，展现出整个山林和躺在地上的萧绝。暮色四合，光线逐渐变暗。在镜头拉远的过程中，地上原本“昏迷”的萧绝，睫毛微微颤动，随后缓缓睁开了双眼，他的眼眸在暮色中显得深邃而锐利，完全没有了之前的脆弱感。', NULL, NULL, '全景', '俯拍', '拉远', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (439, 1654, 1000014, 1000188, 136, 1, '林墨坐在服务器阵列前的控制台椅子上，专注地看着屏幕上滚动的代码。', '中近景，侧拍视角，固定镜头。画面中，一位30岁左右、戴眼镜、面容疲惫但眼神专注的男性（林墨）坐在现代数据中心机房的服务器阵列前。他身穿程序员常见的休闲装，身体微微前倾，目光聚焦在面前控制台的多块显示屏上。屏幕上滚动着密密麻麻的、发着绿光的代码行。背景是成排的黑色服务器机柜，上面有规律闪烁的蓝色和绿色指示灯。整个场景光线偏暗，只有屏幕光和指示灯照亮他的脸和周围环境，营造出深夜工作的氛围。', '中近景，侧拍视角，固定镜头。林墨（30岁，戴眼镜，面容疲惫但专注）坐在现代数据中心机房的服务器阵列前。他身体微微前倾，目光专注地盯着面前控制台的多个显示屏。屏幕上，绿色的代码行持续不断地向上滚动。他的手指在机械键盘上快速而稳定地敲击着，发出轻微的咔嗒声。背景中，成排的黑色服务器机柜上的指示灯有规律地闪烁着蓝色和绿色的光。整个场景光线昏暗，主要光源来自屏幕和指示灯，营造出深夜工作的静谧与专注感。林墨偶尔会轻微地调整一下坐姿，但视线始终没有离开屏幕。', 'uploads/storyboard_image/20260119/e2735f92bbd0bb4ad9f3bd2d7cd38f83_696e04955fd0b.png', NULL, '中近景', '侧拍', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (440, 1654, 1000014, 1000188, 136, 2, '服务器机柜上的指示灯突然从规律的闪烁变为疯狂的、同步的红色警报闪烁。', '特写镜头，平视视角，现代数据中心服务器机柜的局部，黑色金属机架上，一排排红色LED指示灯从规律的绿色闪烁突然转变为疯狂、同步的红色警报闪烁，红光映照在金属表面和线缆上。', '特写镜头，固定拍摄服务器机柜上的一排指示灯。起初，指示灯以规律的绿色节奏闪烁。突然，所有指示灯瞬间变为红色，并以极高频率、完全同步的方式疯狂闪烁，发出刺目的红光。', 'uploads/storyboard_image/20260119/df1758832eccc6ac6bc17a93e076a4ff_696e0409e0802.png', NULL, '特写', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (441, 1654, 1000014, 1000188, 136, 3, '一道强烈的、不自然的白色光束从主服务器机柜的缝隙中射出，照亮了昏暗的机房。', '中景，平视，固定镜头。深夜的数据中心机房，光线昏暗。服务器机柜前，林墨（30岁，戴眼镜，疲惫但专注）正惊愕地看向机柜。一道强烈、不自然的白色光束从主服务器机柜的缝隙中射出，光束照亮了林墨的脸和周围弥漫的灰尘，光束边缘有细微的数据流特效闪烁。机房环境以蓝色和黑色为主色调，光束为纯白色，形成强烈对比。', '中景，平视，固定镜头。深夜的数据中心机房。服务器机柜上的红色警报灯仍在闪烁。突然，一道强烈、不自然的白色光束从主服务器机柜的缝隙中稳定射出，光束照亮了林墨惊愕的脸和周围空气中的灰尘。光束边缘有细微的、不断流动的0和1数字代码特效。整个光束持续稳定地照亮整个画面，直到镜头结束。', 'uploads/storyboard_image/20260119/5c33caf76150528e24c922ace47679bb_696e03e243006.png', NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (442, 1654, 1000014, 1000188, 137, 4, '林墨（身着破旧袈裟）躺在干草堆上，缓缓睁开眼睛，眼神迷茫。', '中近景，平视角度，固定镜头。一位年轻男子（林墨）躺在破庙内的干草堆上，他身穿一件破旧的袈裟。他缓缓睁开眼睛，眼神中充满了迷茫和困惑。背景是破败寺庙的内部，光线从破损的屋顶和墙壁射入，照亮空气中的尘埃。', '中近景，平视，固定镜头。林墨（唐僧形态）躺在干草堆上，他的眼皮微微颤动，然后缓缓睁开。镜头聚焦在他的眼睛上，展示他眼神从闭合到睁开，再到充满迷茫的整个过程。背景是破庙内部静止的环境。', 'uploads/storyboard_image/20260119/797e05147dca18f083ecd921ae343389_696e03c9ee9b3.png', NULL, '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (443, 1654, 1000014, 1000188, 137, 5, '林墨挣扎坐起，环顾破庙四周的破败环境，脸上露出困惑和震惊。', '中景，平视角度，镜头从左向右缓慢摇动。画面中心是林墨（30岁，面容困惑震惊）挣扎着从干草堆上坐起，身上穿着破旧的袈裟。他环顾四周，背景是破败的寺庙内部，可以看到布满灰尘和蛛网的木质横梁，光线从破损的屋顶和墙壁缝隙中射入，形成光束。环境昏暗，细节清晰。', '中景，平视角度，镜头从左向右缓慢摇动。林墨（面容困惑震惊）挣扎着从干草堆上坐起，环顾破庙四周的破败环境。他的目光扫过布满灰尘和蛛网的木质横梁、破损的墙壁和地面。光线从破损处射入，照亮空气中的浮尘。林墨脸上露出困惑和震惊的表情。', NULL, NULL, '中景', '平视', '摇', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (444, 1654, 1000014, 1000188, 137, 6, '林墨低头，看向自己那双不属于他的、皮肤白皙的双手。', '特写镜头，俯拍视角，一双手的特写。画面中心是一双不属于程序员林墨的手，皮肤白皙，指节修长，手指微微弯曲，掌心向上。手上沾着些许干草碎屑，背景是破庙地面杂乱的干草和尘土。光线从侧面射入，在手部形成明暗对比，突出皮肤的细腻质感与陌生感。', '特写镜头，俯拍视角，固定机位。画面中心是一双白皙、指节修长的手（林墨的意识，唐僧的身体）。手部微微颤抖，手指缓慢地张开又握紧，仿佛在确认触感和真实性。光线从画面左侧射入，随着手的细微动作，手背上的光影也产生微弱的晃动。整个镜头聚焦于手部，背景虚化，营造出角色对自身存在产生巨大怀疑的紧张氛围。', 'uploads/storyboard_image/20260119/9732133b97135742963ecfccd87a1b8c_696e04a95ac2d.png', NULL, '特写', '俯拍', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (445, 1654, 1000014, 1000188, 137, 7, '破庙门口，一个逆光的、毛脸雷公嘴的身影（孙悟空）跳了进来，蹲到林墨面前。', '内景，荒山破败寺庙内部，白天，晴天。镜头从林墨（唐僧形态，身穿袈裟，坐在干草堆上）的视角看向破庙门口。门口逆光，站着一个毛脸雷公嘴、头戴凤翅紫金冠、身穿金甲、手持金箍棒的孙悟空身影。光线从破损的屋顶和门口射入，形成强烈的明暗对比，孙悟空的轮廓在逆光中清晰而具有压迫感。', '内景，荒山破败寺庙内部，白天，晴天。镜头固定，对准破庙门口。孙悟空（毛脸雷公嘴，头戴凤翅紫金冠，身穿金甲，手持金箍棒）的身影从门口逆光中突然跳入，动作迅捷。他落地后立刻蹲下，凑到坐在地上的林墨（唐僧形态）面前。镜头轻微跟摇，捕捉孙悟空从门口移动到林墨面前的过程。光线从门口和屋顶破洞射入，灰尘在光柱中飞舞。', 'uploads/storyboard_image/20260119/7248df85c47bdfa85bc6eb6bb0f8b5e6_696e03ca23ec3.png', NULL, '中景', '平视（略带林墨主观视角）', '固定（孙悟空跳入后转为轻微跟摇）', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (446, 1654, 1000014, 1000188, 137, 8, '孙悟空凑近林墨，火眼金睛突然闪烁金光，瞳孔中浮现出数据流和扫描网格特效。', '特写镜头，孙悟空毛脸雷公嘴的猴脸凑近镜头，占据画面中心。他金色的火眼金睛突然闪烁出强烈的金光，瞳孔内部浮现出旋转的绿色数据流和半透明的蓝色扫描网格特效。背景是破庙内部模糊的木梁和干草堆。', '特写镜头，孙悟空的脸部。他的火眼金睛突然亮起，金光闪烁，瞳孔内部特效：数据流旋转，扫描网格浮现并动态扫描。镜头固定，焦点在孙悟空的眼睛上。', NULL, NULL, '特写', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (447, 1654, 1000014, 1000188, 137, 9, '孙悟空后退半步，歪着头，脸上露出疑惑和警惕的表情。', '中近景，孙悟空站在破庙内，光线从破损的屋顶和门口射入，形成逆光效果。孙悟空后退半步，歪着头，脸上露出疑惑和警惕的表情，火眼金睛微微闪烁金光。他身穿金甲，头戴凤翅紫金冠，手持金箍棒。背景是破败的寺庙内部，有蛛网和干草堆。', '孙悟空站在破败寺庙内，光线从门口和屋顶破洞射入。他后退半步，身体微微后倾，歪着头，脸上表情从之前的关切迅速转变为疑惑和警惕。他的火眼金睛持续闪烁着微弱的金光，仿佛在持续扫描和感知。他手中的金箍棒随着他的动作轻微移动。整个镜头固定不动，聚焦于孙悟空的面部表情和眼神变化。', 'uploads/storyboard_image/20260119/8013db4a350c4f6a526ff4b28d1b55a4_696e03e1e38ea.png', NULL, '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (448, 1654, 1000014, 1000188, 137, 10, '林墨强作镇定，双手合十，模仿唐僧的语气对孙悟空说话。', '中近景，平视，固定镜头。画面中，林墨（唐僧形态）身穿破旧袈裟，坐在破庙的干草堆上。他双手合十，表情强作镇定，眼神中带着一丝不易察觉的慌乱。背景是破败的寺庙内部，有木质横梁和蛛网。光线从破损的屋顶和墙壁射入，形成明暗对比。', '中近景，平视，固定镜头。林墨（唐僧形态）坐在干草堆上，他深吸一口气，努力控制表情，双手缓缓合十，嘴唇微动，开始说话。眼神闪烁，透露出内心的紧张。', NULL, NULL, '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (449, 1654, 1000014, 1000188, 137, 11, '孙悟空盯着林墨看了几秒，突然咧嘴一笑，转身将金箍棒扛上肩。', '孙悟空（毛脸雷公嘴，头戴凤翅紫金冠，身穿锁子黄金甲）站在破庙门口，逆光勾勒出他的轮廓。他盯着镜头方向看了几秒，突然咧嘴一笑，露出尖牙，随后转身将金箍棒扛上肩头，准备离开。阳光从破败的庙门外射入，照亮空气中的微尘。', '孙悟空（毛脸雷公嘴，头戴凤翅紫金冠，身穿锁子黄金甲）站在破庙门口，逆光勾勒出他的轮廓。他盯着镜头方向看了几秒，脸上闪过一丝不易察觉的探究，随即咧嘴一笑，露出标志性的猴牙笑容。他转身，动作流畅地将手中的金箍棒“唰”地一下扛上肩头，棒身与肩甲碰撞发出轻微的金属声响。他迈步准备走出破庙，阳光从门外射入，照亮他金甲上的反光和空气中的微尘。', 'uploads/storyboard_image/20260119/0ee2a0050e2768c9a188c0ace9d9353a_696e03bf090d6.png', NULL, '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (450, 1654, 1000014, 1000188, 137, 12, '林墨看着孙悟空的背影，悄悄抬手在空中做出敲击键盘的手势，随后脸上露出绝望。', '中近景，平视，固定镜头。林墨（唐僧形态，身穿红色袈裟）站在破庙内，目光望着前方孙悟空离去的背影。他脸上带着困惑和不安，右手悄悄抬起至胸前，手指在空中做出快速敲击键盘的虚拟手势。破庙内部光线昏暗，只有几束阳光从破损的屋顶和墙壁射入，照亮空气中的尘埃。背景是破败的木质结构和干草堆。', '中近景，平视，固定镜头。林墨（唐僧形态）站在破庙内，目光追随着前方孙悟空扛着金箍棒离去的背影。他脸上先是困惑，然后转为一种尝试性的专注。他悄悄抬起右手，手指在空气中做出快速、熟练但无声的敲击键盘手势。敲击几下后，他停下，看着自己的手，脸上表情逐渐变为彻底的绝望和难以置信。破庙内光线昏暗，尘埃在光束中缓慢飘浮。', 'uploads/storyboard_image/20260119/407b1b8c624255c8ac593f760ceee819_696e03bf45f18.png', NULL, '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (451, 1654, 1000014, 1000188, 138, 13, '镜头从破庙内部拉出，展现破庙外部全景及周围荒凉的野岭环境。', '大全景，俯拍视角，镜头从破败寺庙内部向外拉远，展现寺庙外部全景及周围荒凉的野岭环境。寺庙破败，周围是连绵的荒山和稀疏的树木。天空中隐约可见如极光般闪烁的彩色数据流。', '镜头从寺庙内部向外平稳拉远，展现寺庙外部全景及周围荒凉的野岭环境。镜头持续拉远，视野扩大，显示出连绵的荒山和稀疏的植被。天空中，隐约可见如极光般闪烁、流动的彩色数据流特效。', 'uploads/storyboard_image/20260119/c3bff827e3a770639148f561b589715c_696e03dc6e188.png', NULL, '大全景', '俯拍', '拉远', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (452, 1654, 1000014, 1000188, 138, 14, '（片尾定格）林墨（唐僧）站在破庙门口，脸上带着茫然；前方，孙悟空回头，火眼金睛闪烁着数据金光。', '中近景，平视镜头，固定画面。林墨（唐僧形态，面容茫然）站在破败寺庙门口，光线从他身后射入。前方几步远处，孙悟空（毛脸雷公嘴，头戴凤翅紫金冠，身穿金甲）正回头看向林墨，他的双眼闪烁着金色的数据光芒（火眼金睛特效）。背景是寺庙内部昏暗的环境和门外荒凉的野岭。画面定格，气氛充满悬念。', '中近景，平视镜头，固定画面。林墨（唐僧形态）站在破庙门口，脸上表情从困惑转为茫然，眼神失焦。前方，孙悟空扛着金箍棒，正回头看向林墨，他的双眼（火眼金睛）开始闪烁金色的数据流光，瞳孔中有细微的代码网格和扫描线特效浮现。画面在孙悟空回头、火眼金睛亮起的瞬间定格，背景中隐约有极光般的数据流在空气中微弱闪烁。', NULL, NULL, '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (453, 1654, 1000013, 1000187, 139, 1, '秦彻坐在长椅上，从布袋里拿出馒头喂猫。', 'A young man in a simple Taoist robe sits on a wooden park bench in the evening light. He is holding a worn blue cloth bag in one hand and a white steamed bun in the other. He is in the process of breaking the bun into smaller pieces. The scene is peaceful, with soft golden hour lighting filtering through trees.', 'A young Taoist practitioner, Qin Che, sits calmly on a park bench as the sun sets. He reaches into his simple cloth bag and pulls out a white steamed bun. With deliberate, gentle movements, he begins to break the bun into small pieces, his expression focused and serene. The warm evening light casts long shadows across the scene.', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (454, 1654, 1000013, 1000187, 139, 2, '远处黑色轿车内，柳如烟透过车窗观察。', '中景镜头，透过黑色轿车干净的车窗，柳如烟坐在车内，侧脸对着窗外，目光专注地望向远处公园长椅的方向。傍晚的光线透过车窗，在她脸上形成柔和的光影。', '固定中景镜头，透过黑色轿车车窗拍摄柳如烟。她坐在车内，目光透过车窗，平静而专注地观察着远处公园长椅上的秦彻。傍晚的光线在车窗上流动。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (455, 1654, 1000013, 1000187, 139, 3, '柳如烟下车，独自朝秦彻走去。', '一位身着干练职业套装的年轻女性（柳如烟）从一辆停在路边的黑色轿车后座下车，她关上车门，目光坚定地望向公园深处长椅的方向。傍晚的柔和光线勾勒出她的侧影和车辆轮廓，背景是公园的树木和草坪，营造出一种静谧而略带神秘感的氛围。', '镜头固定，对准黑色轿车后座车门。车门打开，柳如烟（身着职业套装）从车内走出，她站定，关上车门，然后目光投向公园长椅的方向。整个过程动作利落，表情平静而带有审视的意味。傍晚的光线在她身上流动。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (456, 1654, 1000013, 1000187, 140, 4, '柳如烟走到长椅旁，与秦彻对视交谈。', '城市公园傍晚，柔和夕阳光线。柳如烟（一位穿着精致、气质清冷的年轻女性）站在公园长椅旁，微微低头，目光平静而审视地看着坐在长椅上的秦彻。秦彻（一位穿着朴素道袍、面容清俊的年轻男子）抬起头，与柳如烟对视，眼神清澈而专注。背景是公园的灌木丛和远处的树木，光线在两人之间形成柔和的轮廓。', '固定镜头，中近景。傍晚的公园，光线温暖。柳如烟走到长椅旁站定，与坐在长椅上的秦彻对视。两人目光交汇，沉默片刻。柳如烟开口说话，语气平静。秦彻抬头看着她，微微怔了一下，然后仔细端详她的面容。', NULL, NULL, '中近景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (457, 1654, 1000013, 1000187, 140, 5, '柳如烟在长椅另一端坐下，递出名片。', '中景，固定镜头，平视角度。城市公园傍晚，夕阳余晖洒在长椅上。柳如烟（一位气质清冷、穿着得体的年轻女性）坐在长椅一端，身体微微前倾，手中捏着一张白色名片，正递向坐在另一端的秦彻（一位穿着朴素道袍、面容平静的年轻男子）。秦彻的目光落在柳如烟递来的名片上，并未伸手去接。背景是公园的树木和远处的城市轮廓，光线柔和。', '中景，固定镜头，平视角度。城市公园傍晚，柔和的光线。柳如烟深吸一口气，在长椅另一端坐下，与秦彻保持距离。她看向秦彻，语气平静但带着审视。她从手包中取出一张白色名片，手指捏着，递向秦彻。秦彻的目光从柳如烟脸上移到名片上，仔细看了看柳如烟的面相，然后开口说话。柳如烟在秦彻说话时，手指微微收紧。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (458, 1654, 1000013, 1000187, 140, 6, '秦彻起身，对柳如烟提出警告后离开。', '城市公园傍晚，暮色四合。秦彻（年轻道士，穿着简朴道袍）从公园长椅上站起身，拍了拍道袍下摆。柳如烟（都市精英女性，穿着干练的套装）站在长椅另一端，看着他。橘猫在秦彻脚边舔爪子。背景是公园的树木和渐暗的天空，远处有路灯初亮。构图以秦彻和柳如烟为主体，两人保持礼貌距离，气氛平静中带着一丝审视。', '镜头固定拍摄公园长椅区域。秦彻站起身，动作从容，他拍了拍道袍上可能沾到的馒头屑。他背起放在长椅上的布包。橘猫在他脚边完成舔爪子的动作。秦彻转向柳如烟，表情认真地说出台词。说完后，他转身，缓步朝画面一侧（公园深处）走去，身影逐渐融入暮色。柳如烟全程站在原地，目光跟随秦彻，表情若有所思。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (459, 1654, 1000013, 1000187, 140, 7, '柳如烟站在原地，用手机拍下秦彻背影。', '中近景，平视，固定镜头。柳如烟站在城市公园长椅旁，傍晚时分，天色渐暗。她穿着精致的商务套装，手持一部智能手机，屏幕亮着。她微微低头，目光专注地看着手机屏幕，屏幕画面中是一个穿着道袍的年轻男子（秦彻）的模糊背影，正缓步走入暮色中的公园小径。柳如烟的表情平静而若有所思，背景是公园的树木和远处模糊的城市灯光。', '固定中近景镜头。柳如烟站在公园长椅旁，傍晚。她低头看着手中的智能手机，手指在屏幕上操作，将手机镜头对准秦彻离开的方向，按下快门。手机屏幕短暂亮起模拟拍照闪光，随后画面定格在她看着屏幕上刚拍下的模糊背影照片，表情若有所思。', NULL, NULL, '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (460, 1654, 1000013, 1000187, 141, 8, '柳如烟坐在后座，回忆老宅异状（快速闪回画面）。', '中近景，平视，柳如烟独自坐在黑色轿车后座，侧脸看向窗外，眼神若有所思，手机屏幕的光映在她脸上，窗外是城市傍晚的街景，光线昏暗，车内氛围安静而略带神秘。', '中近景，固定镜头，柳如烟坐在黑色轿车后座，她侧脸看向车窗外，眼神深邃，陷入回忆。画面快速闪过几个模糊的闪回片段：老宅走廊地面上的水渍、黑暗的地下室入口、爷爷躺在病床上的虚弱面容、一张建筑图纸上被红笔圈出的诡异布局。闪回结束后，镜头切回柳如烟的脸，她微微蹙眉，手指无意识地摩挲着手机边缘。', NULL, NULL, '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (461, 1654, 1000013, 1000187, 141, 9, '柳如烟坐回车内，吩咐司机回家。', '中景，平视，固定镜头。柳如烟坐在黑色豪华轿车的后座，她刚刚关上车门，身体微微后靠，神情若有所思。透过车窗可以看到城市公园的暮色和路灯。驾驶座上，司机的背影模糊。车内光线柔和，氛围安静。', '中景，平视，固定镜头。柳如烟坐进黑色轿车后座，关上车门。她身体放松地靠向椅背，目光投向车窗外秦彻离开的方向，脸上带着沉思的表情。司机在驾驶座上等待指令。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (462, 1654, 1000013, 1000187, 141, 10, '柳如烟在车内用手机发送语音消息和照片。', '中近景，侧拍视角，柳如烟坐在豪华轿车后座，车内光线昏暗，车窗外是流动的城市夜景灯光。她低头专注地看着手中的智能手机屏幕，屏幕光照亮了她精致的侧脸和专注的神情。她正在操作手机发送消息。', '固定镜头，中近景，侧拍柳如烟坐在行驶的轿车后座。她低头操作手机，手指在屏幕上滑动、点击。手机屏幕的光映在她的脸上，窗外夜景灯光快速向后流动。她对着手机麦克风说话，然后发送消息。', NULL, NULL, '中近景', '侧拍/平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (463, 1654, 1000013, 1000187, 142, 11, '秦彻盘坐在床上，翻阅古书。', '中近景，平视视角，固定镜头。年轻道士秦彻盘腿坐在一张简陋的硬板床上，身穿朴素的深色道袍。他面前摊开一本泛黄的线装古书，书页边缘磨损。他神情专注，指尖轻轻划过书页上的文字。一盏老旧的油灯在画面一侧的简陋木桌上燃烧，发出昏黄的光，照亮他沉静的面容和古书的页面。背景是出租屋的墙壁，月光从一扇破旧的窗户斜射进来。整体氛围宁静、古朴，带有神秘感。', '中近景，平视视角，固定镜头。秦彻盘腿坐在硬板床上，专注地翻阅一本泛黄的线装古书。他的指尖在书页上缓慢移动，目光随着文字移动。油灯的火苗在画面边缘轻微摇曳，投射出跳动的光影在他脸上和书页上。月光从窗户静静洒入，形成一道光柱。整个画面安静，只有翻动书页的细微声响和油灯燃烧的噼啪声。', NULL, NULL, '中近景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (464, 1654, 1000013, 1000187, 142, 12, '秦彻掷出铜钱占卜，凝视卦象。', '特写镜头，俯拍视角，三枚古旧的圆形方孔铜钱散落在深色床单上，呈现两正一反的卦象。一只年轻男性的手悬停在铜钱上方，手指修长，背景是简陋出租屋的昏暗环境。', '固定俯拍特写镜头，画面焦点在三枚静止的铜钱上，一只男性的手从画面外伸入，指尖在铜钱上方悬停片刻，然后轻轻拂过铜钱表面，最后手收回，镜头保持静止，聚焦于卦象。', NULL, NULL, '特写', '俯拍', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (465, 1654, 1000013, 1000187, 142, 13, '秦彻吹熄油灯，在月光下静坐调息。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, NULL, NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (466, 1654, 1000013, 1000208, 143, 1, '秦彻在天桥下铺开蓝布摆摊', '一位身穿朴素布衣的年轻男子（秦彻），在天桥下的桥墩旁，正弯腰将一块写有“问卜解签，趋吉避凶”字样的蓝色布料铺在地上。清晨的城市天桥下，背景是来往的行人。', '秦彻在天桥下的桥墩旁，弯腰将一块洗得发白的蓝布铺在干净的地面上，蓝布上的“问卜解签，趋吉避凶”八个楷字清晰可见。他动作从容，神色平静。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (467, 1654, 1000013, 1000208, 143, 2, '秦彻盘膝而坐，看着桥下车流', '中景，平视，固定镜头。清晨的城市天桥下，人来人往。一位身穿朴素布衣的年轻男子（秦彻）盘膝坐在桥墩旁一块干净的蓝布上，蓝布上写着‘问卜解签，趋吉避凶’。他神色平静，目光投向桥下川流不息的车流。背景是城市街道和行人。', '中景，固定镜头。秦彻盘膝坐在天桥下的蓝布摊位前，清晨的阳光洒在他身上。他保持着平静的姿态，目光凝视着桥下持续流动的车流，偶尔有行人从他身边经过。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (468, 1654, 1000013, 1000208, 144, 3, '张伟在天桥下徘徊，看向秦彻摊位', '中景镜头，平视角度，跟拍运动。一个穿着廉价西装、神色焦虑的年轻男人（张伟）在城市天桥下的人行道上徘徊，他几次转头看向镜头外秦彻摊位的方向，表情犹豫不决。背景是清晨的天桥结构、行人和远处模糊的车流。', '中景镜头，平视角度，跟拍运动。张伟在天桥下的人行道上缓慢踱步，他显得心神不宁，不时停下脚步，目光投向画面右侧（秦彻摊位方向），嘴唇微动，似乎在自言自语。周围有稀疏的行人经过。', NULL, NULL, '中景', '平视', '跟拍', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (469, 1654, 1000013, 1000208, 144, 4, '张伟蹲在秦彻摊位前询问', '中景，平视镜头，固定拍摄。清晨的城市天桥下，人来人往。秦彻盘膝坐在桥墩旁一块干净的蓝布摊位后，神色平静。穿着廉价西装的年轻男人张伟蹲在摊位前，神色焦虑，压低声音向秦彻询问。秦彻抬眼看向张伟。背景是桥下的车流和行人。', '中景，平视镜头，固定拍摄。秦彻盘膝坐在天桥下的蓝布摊位后。张伟蹲下，靠近摊位，压低声音说话。秦彻抬眼看向张伟，观察他的面相。背景是清晨天桥下流动的行人和车流。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (470, 1654, 1000013, 1000208, 144, 5, '秦彻查看张伟的手相和面相', '俯拍视角，近景镜头。画面中心是秦彻（年轻道士，神色平静）和张伟（年轻男性，穿着廉价西装，神色焦虑）的手。秦彻的指尖正在张伟的掌心穴位处轻按，并观察其指甲颜色。背景是天桥下的地面和洗得发白的蓝布一角。', '俯拍视角，固定近景镜头。秦彻专注地查看张伟的手相和面相。他的目光从张伟的印堂、眉尾扫过，然后示意张伟伸手。张伟伸出右手，秦彻的指尖在其掌心几处穴位轻轻按压，随后又仔细看了看张伟的指甲颜色。整个过程秦彻神情专注，张伟则带着紧张和期待。', NULL, NULL, '近景', '俯拍', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (471, 1654, 1000013, 1000208, 144, 6, '秦彻为张伟画符并交代化解方法', '中景，平视镜头，固定拍摄。清晨的城市天桥下，秦彻盘膝坐在一块蓝色布摊位后，神色平静。他正用一支蘸着朱砂的毛笔在一张黄色符纸上快速画着符咒。穿着廉价西装的年轻男人张伟蹲在摊位前，身体微微前倾，神情专注而急切地看着秦彻画符的动作。背景是天桥下的人行道和模糊的车流。', '中景，平视镜头，固定拍摄。秦彻从身边的布袋中取出一张黄纸符箓，铺在蓝布上。他拿起一支细小的朱砂笔，笔尖在符纸上快速而流畅地移动，画出复杂的红色符咒线条。张伟蹲在对面，目光紧紧跟随着秦彻的笔尖，脸上带着焦虑和期待。秦彻画完最后一笔，将符纸折成一个三角形，递给张伟。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (472, 1654, 1000013, 1000208, 144, 7, '张伟付钱后离开', '中景，平视镜头。天桥下，穿着廉价西装的年轻男人张伟蹲在秦彻的算命摊位前，他正从钱包里掏出一张五十元纸币，准备放在摊开的蓝色布上。秦彻盘膝坐在蓝布后，神色平静地看着张伟的动作。蓝布上写着“问卜解签，趋吉避凶”八个字。背景是天桥的桥墩和模糊的行人。', '中景，固定镜头。张伟从钱包里掏出一张五十元纸币，小心翼翼地放在秦彻面前的蓝色布上。秦彻的目光平静地跟随着纸币的落下。张伟脸上带着感激和一丝如释重负的表情。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (473, 1654, 1000013, 1000208, 145, 8, '柳如烟坐在车内，透过车窗观察秦彻', '中近景，侧拍视角，柳如烟坐在一辆灰色轿车后座，戴着墨镜，侧脸朝向车窗外。车窗玻璃映出她冷静的面容，窗外是模糊的城市天桥和行人背景。光线从车窗外透入，在她脸上形成明暗对比。', '固定镜头，柳如烟坐在灰色轿车后座，戴着墨镜，目光透过深色车窗，静静地观察着窗外的天桥下。她的表情冷静而专注，偶尔有微小的头部转动，仿佛在追踪某个目标。车窗外，城市街景和人流在背景中模糊地移动。', NULL, NULL, '中近景', '侧拍', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (474, 1654, 1000013, 1000208, 145, 9, '柳如烟查看手机上的报告信息', 'A close-up shot from a high angle, looking down at a woman (柳如烟) sitting in the back seat of a car. She is wearing a business suit and sunglasses. Her focus is entirely on the smartphone she holds in her hands. The phone screen is visible, displaying lines of text. The car interior is dimly lit, with light filtering through the tinted windows. The atmosphere is tense and investigative.', 'Close-up shot of a woman (柳如烟) in a car, looking down at her phone. The camera is fixed, focusing on her face and the phone screen. Her eyes scan the text on the screen, her expression is serious and contemplative. The light from the phone screen illuminates her face slightly.', NULL, NULL, '近景', '俯拍/侧拍', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (475, 1654, 1000013, 1000208, 145, 10, '柳如烟若有所思地摘下墨镜', '中近景，侧拍视角，柳如烟坐在豪华轿车的后座，她正若有所思地摘下脸上的墨镜，露出冷静而审视的眼神，目光透过车窗望向远处天桥下的秦彻摊位。车内光线柔和，窗外是城市街道的模糊背景。', '中近景，固定镜头，侧拍柳如烟。她缓缓摘下墨镜，动作优雅而带着思考的停顿。她的目光从手中的墨镜移向车窗外，眼神锐利而专注，仿佛在评估和判断。窗外街景有轻微的动态模糊，暗示车辆静止或缓慢移动。', NULL, NULL, '中近景', '侧拍/平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (476, 1654, 1000013, 1000208, 146, 11, '买菜大妈路过秦彻摊位，露出嗤笑表情', '城市天桥下，白天，晴天。一位穿着时髦、拎着名牌包的中年女性（买菜大妈）和她的同伴并肩走过。买菜大妈侧头看向画面左侧秦彻的摊位方向，脸上露出明显的嗤笑表情，眼神轻蔑。她的同伴也顺着她的目光看去。背景是天桥下的行人通道，光线明亮。', '城市天桥下，白天，晴天。买菜大妈和同伴并肩从画面右侧走入中景。买菜大妈的目光被左侧吸引，她侧过头，脸上露出嗤笑的表情，嘴唇微动，对同伴说了句什么。她的同伴也转头看向同一方向。两人短暂停留后，继续向前走去，离开画面左侧。镜头固定。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (477, 1654, 1000013, 1000208, 146, 12, '秦彻闭目养神，不为所动', '中近景，平视，固定镜头。年轻男子秦彻盘膝坐在城市天桥下的桥墩旁，面前铺着一块写有‘问卜解签，趋吉避凶’的蓝布。他穿着简单的布衣，闭着眼睛，神色平静，仿佛周遭的喧嚣与他无关。阳光透过天桥的缝隙洒在他身上。', '中近景，平视，固定镜头。秦彻盘膝坐在天桥下，闭目养神。周围是模糊流动的行人背景，偶尔有车辆驶过的声音。他表情平静，呼吸均匀，对买菜大妈的嘲讽话语毫无反应，仿佛入定一般。', NULL, NULL, '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (478, 1654, 1000013, 1000208, 147, 13, '张伟激动地跑回秦彻摊位前道谢', '中景，平视镜头，固定拍摄。城市天桥下，穿着廉价西装的年轻男人张伟气喘吁吁地跑回算命摊位前，他满脸激动，正对着盘膝坐在地上的秦彻说话。秦彻神色平静地抬头看着他。背景是桥墩和来往的行人。', '中景，固定镜头。张伟从画面右侧跑入，停在秦彻的摊位前，他微微喘气，脸上带着激动和难以置信的表情，开始对秦彻说话。秦彻平静地抬头，目光与他对视。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (479, 1654, 1000013, 1000208, 147, 14, '张伟讲述公司发生的转机', '中近景，平视，固定镜头。画面中心是穿着廉价西装的张伟，他正激动地对着镜头方向说话，脸上洋溢着兴奋和难以置信的表情。他身后是秦彻的蓝布摊位，秦彻盘膝坐在摊位后，神色平静。画面边缘，路人甲和路人乙正从两侧好奇地探身围观，他们的目光聚焦在张伟身上。背景是城市天桥下的环境，有模糊的行人经过。', '中近景，平视，固定镜头。张伟站在秦彻的摊位前，情绪激动地讲述着。他说话时伴随着手势，语速较快。秦彻在摊位后保持平静的坐姿，目光淡然地看着张伟。路人甲和路人乙从画面两侧入镜，他们停下脚步，身体微微前倾，脸上带着好奇和惊讶的表情，听着张伟的讲述。背景中，其他行人以正常速度走过，形成动态模糊。', NULL, NULL, '中近景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (480, 1654, 1000013, 1000208, 147, 15, '路人被吸引，围过来观看和议论', '中景，平视镜头，固定拍摄。城市天桥下，白天，晴天。秦彻盘膝坐在桥墩旁的算命摊位后，神色平静。摊位前，路人甲、路人乙和买菜大妈等几个路人围拢过来，他们脸上带着好奇、惊讶和将信将疑的表情，互相交头接耳，窃窃私语，目光聚焦在秦彻身上和摊位的蓝布上。背景是城市天桥的桥墩和稀疏的人流。', '中景，平视，固定镜头。秦彻平静地坐在摊位后，闭目养神或淡然地看着围观者。几个路人（包括路人甲、路人乙、买菜大妈）从不同方向走近，逐渐围拢在摊位前。他们互相交换眼神，低声议论，手指偶尔指向摊位或秦彻。买菜大妈的表情从之前的嗤笑转为困惑和一丝好奇。整个场景中，路人之间有小幅度的身体语言交流，如点头、摇头、耸肩。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (481, 1654, 1000013, 1000208, 147, 16, '张伟再次付钱后离开', '中景，平视镜头。在天桥下，穿着廉价西装的年轻男人张伟，满脸激动和感激，正从钱包里掏出一张一百元纸币，弯腰放在算命先生秦彻面前铺在地上的蓝色布上。秦彻盘膝坐在蓝布后，神色平静地看着张伟的动作。周围有几个路人好奇地围观。', '固定中景镜头。张伟从钱包里快速抽出一张一百元纸币，带着感激的笑容，弯腰将钱放在秦彻面前的蓝布上。秦彻微微点头，表情淡然。张伟直起身，朝秦彻再次点头致意，然后转身，脸上带着欢快的表情，准备离开。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (482, 1654, 1000013, 1000208, 148, 17, '柳如烟在车内观察张伟道谢的场面', '中近景，平视，柳如烟坐在灰色轿车后座，戴着墨镜，侧脸望向车窗外，车窗玻璃映出她冷静观察的侧影，窗外是城市天桥下模糊的背景，光线从车窗外透入，在她脸上形成明暗对比。', '固定镜头，柳如烟坐在灰色轿车后座，戴着墨镜，目光透过车窗，冷静地观察着天桥下秦彻摊位前发生的一切。她看到张伟激动地跑回来，付钱并大声道谢，周围路人开始围观。整个过程，柳如烟的表情几乎没有变化，但眼神专注，透露出深思。车窗外的景象（张伟道谢、路人围观）作为背景虚化处理，焦点始终在柳如烟的脸上和眼神上。', NULL, NULL, '中近景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (483, 1654, 1000013, 1000208, 148, 18, '柳如烟若有所思，对司机下达指令', '中近景，侧拍视角，一位年轻干练的女性（柳如烟）坐在豪华轿车后座，她戴着墨镜，目光透过车窗望向车外，表情若有所思，嘴唇微张，正准备说话。背景是模糊的车内豪华内饰和车窗外移动的城市街景。光线从车窗外照入，在她脸上形成明暗对比。', '中近景，固定镜头，侧拍柳如烟坐在豪华轿车后座。她戴着墨镜，目光从车窗外收回，转向镜头方向（司机），嘴唇微动，下达指令。车窗外有模糊的城市街景向后移动。光线柔和。', NULL, NULL, '中近景', '侧拍/平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (484, 1654, 1000013, 1000208, 148, 19, '车子驶离，柳如烟最后看了一眼秦彻的方向', '中近景，侧拍视角，柳如烟坐在豪华轿车后座，她微微侧头，透过深色车窗玻璃望向天桥方向，表情若有所思，手中拿着墨镜。车内光线柔和，车窗外的城市街景略微模糊。', '中近景，固定镜头，柳如烟坐在轿车后座，她缓缓转过头，视线投向车窗外天桥的方向，眼神专注而若有所思，手中墨镜的镜片反射着窗外流动的光影。', NULL, NULL, '中近景', '侧拍/平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (485, 1654, 1000013, 1000208, 149, 20, '秦彻在天桥下收起蓝布和收入', '中景，俯拍视角，固定镜头。秦彻盘膝坐在城市天桥下的桥墩旁，夕阳的余晖洒在他身上。他正将一块洗得发白的蓝布折叠起来，蓝布上写着“问卜解签，趋吉避凶”的墨字。他的动作从容不迫，身边放着几张纸币和硬币。背景是傍晚的城市天桥，行人稀疏，华灯初上。', '中景，俯拍视角，固定镜头。秦彻在天桥下，夕阳西下，光线柔和。他正将写有“问卜解签，趋吉避凶”的蓝布缓慢而仔细地折叠起来，收入身边。镜头聚焦于他折叠蓝布的手部动作和散落在布上的几张纸币硬币。背景中天色渐暗，城市灯光开始亮起。', NULL, NULL, '中景', '俯拍', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (486, 1654, 1000013, 1000208, 149, 21, '橘猫出现，蹭着秦彻的腿', '近景镜头，平视角度。画面中，一只橘猫正用头和身体亲昵地蹭着秦彻的小腿。秦彻低头看着猫，表情平静温和。背景是傍晚天桥下的模糊景象，光线柔和。', '近景固定镜头。一只橘猫从画面一侧走入，靠近秦彻的腿，开始用头和身体来回蹭他的小腿，动作亲昵。秦彻低头看着猫，嘴角有细微的放松。傍晚的暖光洒在他们身上。', NULL, NULL, '近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (487, 1654, 1000013, 1000208, 149, 22, '秦彻掰开馒头喂猫，自己就着清水吃', '外景·城市天桥下·傍晚·晴天，中景，平视，固定镜头。秦彻盘膝坐在天桥下桥墩旁，穿着简单的布衣布鞋。他正将手中掰开的馒头碎屑喂给脚边的一只橘猫。橘猫低头吃着。秦彻自己手里拿着另一个完整的馒头，旁边地上放着一碗清水。背景是傍晚的城市天桥，光线昏黄，行人稀少。', '外景·城市天桥下·傍晚·晴天，中景，平视，固定镜头。秦彻盘膝坐在天桥下桥墩旁，穿着简单的布衣布鞋。他正将手中掰开的馒头碎屑喂给脚边的一只橘猫。橘猫低头吃着。秦彻自己手里拿着另一个完整的馒头，旁边地上放着一碗清水。背景是傍晚的城市天桥，光线昏黄，行人稀少。秦彻的动作缓慢而平静，橘猫安静地进食。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (488, 1654, 1000013, 1000208, 149, 23, '秦彻望着华灯初上的城市夜景', 'A young man in simple cloth clothes, Qin Che, sitting cross-legged on the ground under a city overpass at dusk. He has a calm expression and clear, determined eyes as he gazes out at the cityscape where lights are beginning to twinkle. An orange cat is curled up asleep at his feet. The background shows the urban environment transitioning from day to night.', 'A static shot of Qin Che sitting under the overpass. His gaze is steady and thoughtful as he looks out at the distant city skyline. The lights of the city gradually become brighter as dusk deepens. The orange cat at his feet remains still, sleeping. The overall atmosphere is one of quiet contemplation amidst the bustling city.', NULL, NULL, '中近景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (489, 1654, 1000013, 1000208, 150, 24, '柳如烟站在落地窗前俯瞰城市夜景', '内景，柳氏集团总裁办公室，夜晚。柳如烟（一位约28岁，面容精致但略带疲惫，穿着剪裁合体的职业套装）独自站在巨大的落地窗前，俯瞰着窗外璀璨的城市夜景。办公室内光线柔和，主要光源来自窗外的霓虹和桌上的台灯。她手中拿着一张泛黄的老照片，眼神专注而复杂，脸上映照着窗外流动的光影。', '镜头固定，拍摄柳如烟站在落地窗前的侧后方中景。窗外是流动的城市夜景，车灯和霓虹灯形成光轨。柳如烟先是低头凝视手中的老照片，手指轻轻拂过照片表面，然后抬起头，目光投向窗外深沉的夜色，表情从沉思转为凝重。整个镜头氛围安静、略带孤寂感。', NULL, NULL, '中景', '侧后方平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (490, 1654, 1000013, 1000208, 150, 25, '助理林薇敲门进入办公室汇报', '现代总裁办公室内景，柳如烟站在落地窗前，助理林薇站在办公室门口，手里拿着一叠文件，办公室门半开着，室内光线明亮，窗外是城市夜景。', '镜头固定，柳如烟站在落地窗前，背对镜头，望着窗外夜景。办公室门被轻轻敲响，然后推开，助理林薇拿着文件走进来，停在办公室中央。柳如烟闻声，缓缓转过身来。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (491, 1654, 1000013, 1000208, 150, 26, '柳如烟转身接过文件，眉头紧锁', '内景，柳氏集团总裁办公室，夜晚。柳如烟站在落地窗前，转身从助理林薇手中接过一个牛皮纸文件袋。她眉头紧锁，目光落在文件袋上，窗外是城市璀璨的夜景。林薇站在办公桌前，姿态恭敬。', '镜头固定，拍摄柳如烟转身，从林薇手中接过文件袋。她低头看了一眼文件袋，然后抬起眼帘，眉头紧锁，表情凝重。林薇保持着递交文件的姿势，随后手收回身侧。', NULL, NULL, '中近景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (492, 1654, 1000013, 1000208, 150, 27, '柳如烟交代明日安排和准备现金', '中景，平视镜头，固定拍摄。柳氏集团总裁办公室内，柳如烟站在办公桌前，手中拿着老宅平面图复印件，神色凝重。助理林薇站在办公桌对面，恭敬地等待指示。办公室背景是巨大的落地窗，窗外是城市夜景。光线以室内暖光为主，窗外冷光为辅。', '中景，固定镜头。柳如烟将手中的平面图复印件放在办公桌上，抬头看向林薇，开始交代事项。林薇专注地听着，偶尔点头。镜头主要捕捉柳如烟的正面和林薇的侧面。办公室内光线稳定，窗外夜景的灯光有微弱的流动感。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (493, 1654, 1000013, 1000208, 150, 28, '林薇退出办公室', '中景，平视，固定镜头。柳氏集团总裁办公室门口，助理林薇身穿职业套装，表情严肃，微微点头后转身，伸手握住门把手，准备退出办公室。办公室内景可见一角，光线明亮，陈设现代而奢华。', '中景，平视，固定镜头。助理林薇站在办公室门口，听完柳如烟的指示后，表情严肃地点了点头，轻声应“是”，然后转身，伸手握住门把手，拉开门，侧身退出办公室，门在她身后缓缓关上。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (494, 1654, 1000013, 1000208, 150, 29, '柳如烟拉开抽屉，看着一张泛黄的老照片', '近景，俯拍视角，柳如烟站在豪华的办公室内，她微微低头，目光专注地看着手中一张泛黄的老照片。照片上是她年幼时与爷爷、父母在老宅花园的合影。办公室窗外是城市的璀璨夜景，与照片的怀旧感形成对比。', '镜头固定，柳如烟拉开办公桌抽屉，目光落在抽屉内的一张泛黄老照片上。她伸出手指，轻轻拂过照片上父母模糊的笑脸，眼神中流露出复杂的情绪，有怀念、有悲伤，也有坚定的决心。窗外城市的霓虹灯光在她脸上投下变幻的光影。', NULL, NULL, '近景', '俯拍', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (495, 1654, 1000011, 1000130, 152, 1, '摩天大楼玻璃幕墙反射着城市的霓虹。大部分楼层的灯光已熄灭，只有零星几扇窗还亮着。', 'A wide shot of a modern city CBD at night, showing a skyscraper with glass curtain walls reflecting the neon lights of the city. Most floors are dark, with only a few windows lit up. The sky is clear.', 'A static wide shot of a modern city CBD skyscraper at night. The glass curtain walls reflect the colorful neon lights of the surrounding city. Most windows are dark, with only a few scattered lights on. The scene is quiet and still.', NULL, NULL, '大全景', '平视', '固定', NULL, NULL, 5000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (496, 1654, 1000011, 1000130, 153, 2, '高跟鞋敲击大理石地面的声音在空旷的走廊回响。林薇从一扇玻璃门后走出，经过前台时顺手关掉了区域的照明开关。', '中景，平视角度，跟拍镜头。一位28岁的亚洲女性（林薇）穿着职业装和高跟鞋，背着通勤包，正从一扇玻璃门后走出，走在空旷的写字楼走廊里。她脸上带着倦容，眼妆微晕。走廊是现代简约风格，地面是光洁的大理石，天花板有嵌入式灯带。她经过一个简约的前台，伸手关掉了墙上的照明开关。画面色调偏冷，光线主要来自剩余的几盏灯，营造出深夜办公楼的寂静感。', '中景，平视角度，跟拍镜头。林薇从一扇玻璃门后走出，高跟鞋敲击大理石地面的声音在空旷的走廊里清晰回响。镜头平稳地跟随她向前走。她脸上带着疲惫，一边走一边无意识地揉着脖子。当她经过前台时，自然地伸手按下了墙上的区域照明开关，该区域的灯光随之熄灭。整个动作流畅，镜头持续跟随她走向电梯间的方向。背景是空旷、安静的现代写字楼走廊。', NULL, NULL, '中景', '平视', '跟拍', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (497, 1654, 1000011, 1000130, 154, 3, '电梯门打开，林薇走进去，按下B1层按钮。电梯下行时，她靠在轿厢壁上，闭上眼睛深吸一口气。她掏出手机查看母亲发来的微信消息，最终没有回复。', '内景，深夜，都市写字楼电梯轿厢内。一名28岁的职业女性（林薇）独自站在电梯内，她穿着职业装，背着通勤包，身体微微后仰靠在轿厢壁上，眼睛闭着，脸上带着明显的疲惫。她一只手拿着手机，屏幕亮着。电梯楼层显示面板上，B1按钮亮着红光。电梯内光线为标准的白色冷光，环境干净但略显冰冷。', '电梯门打开，林薇走进电梯轿厢。她转身，伸手按下B1层按钮，按钮亮起红光。电梯门关闭，轿厢开始轻微震动并下行。林薇身体放松，后背靠在轿厢壁上，闭上眼睛，做了一个深呼吸。此时，她包里的手机震动。她睁开眼睛，从包里掏出手机，解锁屏幕。手机屏幕特写：显示母亲发来的微信消息，内容是关于相亲介绍，并附有一张男性照片。林薇盯着屏幕看了几秒钟，拇指悬在输入框上方，最终没有打字。她按下锁屏键，屏幕变黑，映出她疲惫而略带无奈的脸部倒影。', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (498, 1654, 1000011, 1000130, 155, 4, '林薇从扶梯走下，站台上乘客稀少。晚风穿过通道，吹起她肩上的发丝。列车进站，车门打开。', 'A medium shot of a tired but elegant woman in business attire (Lin Wei) descending an escalator in a subway station at night. The station platform is sparsely populated. Her hair is slightly lifted by a breeze coming through the tunnel. The lighting is a mix of cool fluorescent station lights and warm ambient light from the arriving train. The atmosphere is quiet and slightly melancholic.', 'A continuous medium shot of Lin Wei walking down the escalator in a subway station at night. The camera is fixed, capturing her descent. Her hair gently blows in the tunnel breeze. In the background, the nearly empty platform is visible. A subway train slowly enters the station from the right side of the frame, its lights illuminating the scene. The train doors open with a soft pneumatic sound.', NULL, NULL, '中景', '平视', '固定', NULL, NULL, 10000, '', NULL, 'image');
INSERT INTO `php_plugin_shortplay_drama_storyboard` VALUES (499, 1654, 1000011, 1000130, 156, 5, '车厢里只有零星几个乘客。林薇走到车厢连接处，靠着栏杆。窗外，城市的流光飞速向后掠去。玻璃窗上倒映出她的侧影。列车轻微晃动。列车广播响起：“下一站，幸福里站，请准备下车的乘客……”林薇抬起头，整理了一下衣领，准备下车。', '一位穿着精致职业装的亚洲女性（林薇）侧身靠在地铁车厢连接处的不锈钢栏杆上，她望向车窗外，车窗上倒映出她疲惫而精致的侧脸。车厢内光线柔和，只有零星几个乘客。窗外是飞速向后流动的城市夜景，广告牌、路灯和高架桥的车灯连成一片模糊的光带。构图稳定，焦点在林薇和车窗倒影上。', '固定中景镜头，侧拍角度。林薇（28岁，互联网公司视觉设计师）穿着职业装，背靠地铁车厢连接处的栏杆，望着窗外。窗外城市的流光（广告牌、路灯、车灯）持续高速向后飞掠，形成动态的光带。车窗玻璃上清晰倒映出林薇的侧影。列车轻微晃动，林薇的身体随之有轻微的起伏。几秒钟后，列车广播响起，林薇闻声抬起头，整理了一下自己的衣领，准备下车。', NULL, NULL, '中景', '侧拍', '固定', NULL, NULL, 10000, '', NULL, 'image');
