INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (125, 1654, 204, 759, 1.5, 50, 'neutral', 200, 8000, 0, '彻儿，你尘缘未了，命中有劫需入世化解。这枚‘通宝钱’可护你一时，记住，道法自然，心即红尘。', 'uploads/dialogue_voice/20260116/ba29ff0e1232da97019c2edd4ff74594_6969fdbbddaf9.mp3', '{\"age\": \"old\", \"name\": \"龙老伯\", \"tags\": [\"沧桑岁月爷\"], \"audios\": {\"en\": \"http://192.168.110.200:36200/uploads/save/20260106/d59e75c6430a8a2e3c1d9699a304f68c_695c86dc01fe5.mp3\", \"zh\": \"http://192.168.110.200:36200/uploads/save/20260106/7257bf7855328538d7e17d03d89e2c79_695c86da59f52.mp3\"}, \"gender\": \"male\", \"headimg\": \"\", \"age_enum\": {\"label\": \"老年\", \"value\": \"old\"}, \"emotions\": [], \"language\": [\"zh\", \"en\"], \"model_id\": 22, \"voice_id\": \"100002\", \"gender_enum\": {\"label\": \"男声\", \"props\": {\"type\": \"primary\"}, \"value\": \"male\"}, \"emotions_enum\": [], \"language_enum\": [{\"hello\": \"你好,我是{name}\", \"label\": \"中文\", \"value\": \"zh\"}, {\"hello\": \"Hello, I am {name}\", \"label\": \"英语\", \"value\": \"en\"}], \"voice_channel\": \"yidevs\", \"selected_language\": {\"label\": \"中文\", \"value\": \"zh\"}}');
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (126, 1654, 205, 1, 1, 50, 'neutral', 100, 3000, 0, '师父，山下……是何模样？', 'uploads/dialogue_voice/20260106/57f350f32bd81c4712e99538277bca4f_695c876850e5b.mp3', NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (127, 1654, 210, 1, 1, 50, 'neutral', 200, 2500, 0, '小家伙，你沾了不干净的东西。', 'uploads/dialogue_voice/20260106/96242adc413d41b3706dccf76a5eba1d_695c87806d9b8.mp3', NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (128, 1654, 213, 5, 1.2, 50, 'angry', 200, 3000, 0, '王秘书，那份并购案的文件我下午必须看到……', 'uploads/dialogue_voice/20260106/8ee4ef9d88b7135ef416c659485dc763_695c879451382.mp3', NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (130, 1654, 214, 5, 0.8, 30, 'fearful', 250, 1000, 0, '啊……', 'uploads/dialogue_voice/20260107/c41c2d2e419f5317ceefa9cbf71379d3_695e14350649e.mp3', NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (131, 1654, 216, 1, 1.2, 50, 'surprised', 0, 2500, 0, '别动她！', 'uploads/dialogue_voice/20260106/1baf39e279272a4f16a2a968c5ecaa3c_695c87ae61cd5.mp3', NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (132, 1654, 216, 1, 1, 50, 'neutral', 250, 5000, 0, '邪气入体，乱动魂就散了！', 'uploads/dialogue_voice/20260106/606ef2f770dd8e4616298b88dc556ff8_695c87b3c1216.mp3', NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (135, 1654, 220, 762, 1.2, 70, 'angry', 0, 1500, 0, '抓住他！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (136, 1654, 221, 5, 0.8, 50, 'surprised', 100, 4000, 0, '刚才……是谁？', 'uploads/dialogue_voice/20260106/65655c4fb2725b5a6f148402403d9bcf_695c87d169937.mp3', NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (163, 1654, 313, 697, 1, 50, 'happy', 0, 2500, 0, '真的可以催生！而且这水……', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (164, 1654, 313, 697, 1, 50, 'happy', 2500, 5000, 0, '好！第一步，先让家里人吃饱饭，再想办法赚钱！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (165, 1654, 314, 702, 1, 50, 'surprised', 2000, 6000, 0, '闺女，这……这米是从哪儿来的？还有这菜，后山不是早挖光了吗？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (168, 1654, 315, 697, 1, 50, 'neutral', 0, 5000, 0, '娘，我今儿去后山深处转了转，运气好，找到一小片没被人发现的野谷地，还有几窝野菜。米是我用采的草药跟村尾王货郎换的。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (169, 1654, 316, 704, 1, 50, 'happy', 0, 2000, 0, '小妹真厉害！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (170, 1654, 318, 697, 1, 50, 'surprised', 2000, 3500, 0, '谁？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (175, 1654, 320, 697, 1, 50, 'neutral', 0, 3000, 1, '怎么又是他？还没走？伤得更重了……', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (176, 1654, 320, 697, 1, 50, 'neutral', 3000, 5000, 1, '救还是不救？救了可能惹麻烦，不救……好像有点见死不救？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (178, 1654, 322, 697, 1, 0, 'neutral', 0, 0, 0, '', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (180, 1654, 323, 697, 1, 0, 'neutral', 0, 0, 0, '', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (182, 1654, 325, 698, 0.8, 50, 'weak', 2000, 8000, 0, '水……多谢姑娘……', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (184, 1654, 326, 697, 1, 50, 'neutral', 0, 2000, 0, '你醒了就好。我、我还有事，先走了！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (186, 1654, 328, 698, 0.7, 50, 'sad', 0, 8000, 0, '姑娘……别走……山林有狼……我动不了……', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (188, 1654, 329, 697, 1, 0, 'neutral', 0, 10000, 1, '美男计！这绝对是美男计！不能上当！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (189, 1654, 330, 697, 1, 50, 'neutral', 0, 2500, 0, '算了，我扶你去那边山洞避一避，给你留点水和吃的。天亮后你自己想办法。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (190, 1654, 331, 698, 0.8, 50, 'sad', 0, 4000, 0, '多谢姑娘……救命之恩……', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (193, 1654, 334, 697, 1, 50, 'neutral', 0, 4000, 0, '吃的喝的在这儿。我走了，你好自为之。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (194, 1654, 335, 698, 0.8, 50, 'neutral', 0, 3000, 0, '姑娘……可否告知芳名？日后……定当报答。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (196, 1654, 338, 698, 0.8, 30, 'neutral', 2000, 9000, 1, '有趣……凭空取物，还有这蕴含生机的‘水’……豌豆姑娘，我们很快会再见的。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (197, 1654, 339, 697, 1, 0, 'neutral', 0, 5000, 1, '那张脸……也太犯规了。不过总觉得哪里不对劲，他伤得那么重，怎么抓住我衣角时力道和位置那么准？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (198, 1654, 339, 697, 1.2, 0, 'neutral', 5000, 10000, 1, '不想了不想了，反正明天他应该就走了。赚钱！养家！这才是正事！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (227, 1654, 366, 1, 1.5, 0, 'neutral', 0, 4000, 1, '师父说入世历劫，这第一劫，莫非是饿劫？', 'uploads/dialogue_voice/20260107/f8ab4cc1d6fb9e98ca5001589d9bc8e8_695e3b130460a.mp3', NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (228, 1654, 367, 763, 2, 50, 'sad', 0, 3000, 0, '工作上的事……我负责的一个项目，眼看要成了，最近却处处不顺，总觉得……有人捣鬼。', 'uploads/dialogue_voice/20260107/52e0600a7c4036e344268a9237b816e6_695e3b6d0718e.mp3', NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (229, 1654, 367, 1, 1.5, 50, 'neutral', 3500, 5000, 0, '先生近日是否常感背后发凉，夜间多梦，且梦中多见水？', 'uploads/dialogue_voice/20260107/c44fd9139258af965be07f952aee9147_695e3b7712415.mp3', NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (230, 1654, 367, 763, 1.2, 50, 'surprised', 5500, 6500, 0, '你怎么知道？', 'uploads/dialogue_voice/20260107/0afda3e5f553601cd9fd9085dec08f1e_695e3b7be704c.mp3', NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (231, 1654, 368, 1, 1, 50, 'neutral', 2000, 6000, 0, '将此符置于摆件之下，明日午时，将摆件中的水全部倒掉，置于阳光下暴晒三日即可。此后行事，多留一分心。', 'uploads/dialogue_voice/20260107/aeab9f6977b20438e3c6a0dcb95c23a0_695e38f0bee42.mp3', NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (232, 1654, 368, 763, 1, 50, 'surprised', 6500, 8000, 0, '是李强！那个摆件就是他送的！他是我这次项目的竞争对手！小师傅，这……这怎么破？', 'uploads/dialogue_voice/20260107/a6588e494a145b58acaaf34963368e00_695e38f686e97.mp3', NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (233, 1654, 369, 766, 1, 50, 'surprised', 0, 3000, 0, '真的假的？演的吧？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (234, 1654, 369, 767, 1, 50, 'neutral', 3500, 6000, 0, '看着挺年轻，不像骗子……', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (235, 1654, 369, 768, 1.2, 60, 'happy', 6500, 10000, 0, '小伙子，给我看看我儿子啥时候能找到对象？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (236, 1654, 370, 1, 1, 50, 'neutral', 0, 5000, 1, '此物方位属金，本无碍。但若在水中暗藏一缕头发或指甲，再以符咒催动，流水便成‘蚀财水’，反伤主人。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (237, 1654, 372, 5, 1, 50, 'sad', 0, 4000, 0, '陈伯，爷爷今天情况怎么样？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (238, 1654, 372, 765, 0.9, 50, 'sad', 4000, 10000, 0, '小姐，老爷还是昏昏沉沉的，医生说查不出具体病因，但身体机能衰退得很快……老宅那边，昨晚守夜的佣人又说听到地下室有奇怪的声音。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (239, 1654, 373, 5, 1, 0, 'neutral', 0, 0, 1, '那个人……难道真的不是巧合？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (240, 1654, 374, 764, 1, 50, 'neutral', 1000, 4000, 0, '柳总，您让我打听的，关于……嗯，比较灵验的师傅，有消息了。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (241, 1654, 374, 5, 1, 50, 'neutral', 4500, 5000, 0, '说。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (242, 1654, 374, 764, 1, 50, 'neutral', 5500, 9000, 0, '我托了几个信得过的老人问，都说最近天桥下来了个特别年轻的小道士，算卦看事很准，昨天好像还帮一个白领破了什么职场陷害。不过……', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (243, 1654, 374, 5, 1, 50, 'neutral', 9500, 10000, 0, '不过什么？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (244, 1654, 375, 5, 1, 50, 'neutral', 2000, 4000, 0, '地址发我。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (245, 1654, 375, 5, 1, 50, 'neutral', 4500, 6500, 0, '另外，安排一下，下午的会议取消。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (246, 1654, 375, 5, 1, 50, 'neutral', 7000, 9000, 0, '这件事，不要告诉任何人。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (247, 1654, 376, 1, 1, 50, 'neutral', 1000, 6000, 0, '您看这条线，主晚运。子女宫位饱满，说明子女孝顺，晚年有靠。只是近来水星丘略有晦暗，家中或子女处，近期可有与水相关的小麻烦？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (248, 1654, 376, 769, 0.9, 50, 'surprised', 6500, 8500, 0, '哎呀，对对对！我儿子家上个月水管漏了，修了好几天呢。小师傅，这要紧吗？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (249, 1654, 378, 5, 1, 50, 'neutral', 0, 0, 0, '……', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (250, 1654, 378, 5, 0.9, 50, 'surprised', 4000, 6000, 1, '……是他。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (251, 1654, 379, 5, 1, 50, 'neutral', 0, 2000, 0, '跟着他，慢一点，别被发现。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (252, 1654, 206, 1, 1, 50, 'surprised', 4000, 6000, 0, '这是哪里啊？', 'uploads/dialogue_voice/20260116/abf9198d41e9b5965f8b02475ddbe2f8_696a049242db5.mp3', NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (253, 1654, 399, 697, 1, 0, 'neutral', 0, 0, 1, '最后一个bug……马上就好……', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (254, 1654, 400, 697, 1, 0, 'neutral', 0, 0, 1, '……', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (255, 1654, 403, 775, 1, 50, 'sad', 0, 3000, 0, '豌豆啊！我的儿！你可别吓娘！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (256, 1654, 407, 702, 1, 50, 'happy', 0, 2000, 0, '醒了醒了！菩萨保佑！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (257, 1654, 407, 703, 0.9, 50, 'neutral', 2200, 4500, 0, '闺女，你……你还有哪儿不舒服？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (258, 1654, 408, 697, 1, 50, 'surprised', 0, 5000, 1, '我……穿越了？还是个快嫁不出去的村姑？！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (259, 1654, 411, 704, 1, 50, 'happy', 0, 2000, 0, '小妹坐！哥给你摘野果子去！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (260, 1654, 411, 705, 1, 50, 'happy', 2000, 3500, 0, '我去挑水！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (261, 1654, 413, 702, 1, 50, 'sad', 0, 5000, 0, '闺女，娘知道你不甘心。可……这十里八乡，听说你脾气的，都不敢上门啊。再过三月你就满十八了，按律法……', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (262, 1654, 414, 697, 1, 0, 'neutral', 0, 5000, 1, '原主这战斗力……难怪。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (263, 1654, 415, 697, 1, 50, 'neutral', 0, 2000, 1, '原主这战斗力……难怪。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (264, 1654, 417, 697, 1, 50, 'neutral', 1000, 4000, 0, '系统？金手指？老爷爷？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (265, 1654, 420, 776, 1, 50, 'neutral', 2000, 2200, 0, '（清脆的“叮”声，仿佛在脑海中直接响起）', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (266, 1654, 421, 697, 1, 0, 'neutral', 0, 0, 0, '……', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (267, 1654, 422, 697, 1, 50, 'surprised', 2000, 6000, 1, '这是……灵泉？空间？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (268, 1654, 424, 697, 1, 50, 'happy', 0, 5000, 1, '有救了！虽然开局地狱难度，但有这个，至少饿不死！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (269, 1654, 431, 697, 1, 0, 'neutral', 0, 10000, 1, '路边的男人不能捡！尤其是长得这么祸水的！电视剧里捡了都没好下场！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (270, 1654, 439, 778, 1, 50, 'neutral', 3000, 8000, 0, '这次大版本更新，西游副本的AI逻辑总算优化完了...', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (271, 1654, 440, 778, 1, 0, 'neutral', 0, 0, 0, '……', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (272, 1654, 441, 778, 1, 50, 'surprised', 0, 1000, 0, '过载了？！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (273, 1654, 443, 778, 1, 50, 'surprised', 1500, 3500, 0, '这是...哪儿？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (274, 1654, 444, 778, 1, 0, 'neutral', 0, 0, 1, '……', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (275, 1654, 445, 779, 1.2, 60, 'surprised', 1500, 3500, 0, '师父！你醒了？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (276, 1654, 445, 779, 1, 55, 'neutral', 3800, 4800, 0, '师父，你可吓坏俺老孙了！那阵妖风来得古怪...', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (277, 1654, 446, 779, 1, 50, 'surprised', 0, 3000, 0, '咦？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (278, 1654, 447, 779, 1, 50, 'surprised', 0, 3000, 0, '师父，你身上怎无半点佛光，反倒有股...铁盒子味？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (279, 1654, 448, 778, 1, 50, 'fearful', 500, 3500, 0, '悟...悟空，为师只是...受了些惊吓。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (280, 1654, 449, 779, 1, 50, 'neutral', 0, 4000, 0, '嘿嘿，许是俺老孙多心了。师父既已醒来，咱们该上路了。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (281, 1654, 450, 778, 1, 0, 'neutral', 0, 0, 1, '不是梦...我真的被困在游戏里了。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (282, 1654, 456, 5, 1, 50, 'neutral', 2000, 5000, 0, '公园里，用银针救人的，是你吗？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (283, 1654, 456, 1, 1, 50, 'neutral', 5500, 8000, 0, '是你。那日你被‘惊魂煞’冲撞，三魂不稳。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (284, 1654, 457, 5, 1, 50, 'neutral', 0, 3500, 0, '我叫柳如烟。那天谢谢你。我的保镖当时误会了，我代他们道歉。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (285, 1654, 457, 1, 1, 50, 'neutral', 4000, 4500, 0, '不必。职责所在。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (286, 1654, 457, 5, 1, 50, 'neutral', 5000, 7000, 0, '我听人说，天桥下有位年轻道长，看事很准。我遇到一些……用你的话说，可能是‘非自然’的麻烦。想请你看看。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (287, 1654, 457, 1, 1, 50, 'neutral', 7500, 8000, 0, '今日卦象已满。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (288, 1654, 457, 5, 1, 50, 'neutral', 8500, 9000, 0, '这是我的联系方式。报酬不是问题。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (289, 1654, 458, 1, 1, 50, 'neutral', 0, 5000, 0, '若信得过，明日此时，可带我去那宅子一看。无需定金，事成再议。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (290, 1654, 458, 1, 1, 50, 'neutral', 5000, 10000, 0, '柳小姐，近日亥时之后，莫要独自留在那宅中，尤其勿近水边。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (291, 1654, 468, 763, 1, 0, 'neutral', 0, 10000, 1, '这个月业绩又垫底，再这样下去肯定被裁……听说这里有个算命的挺准？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (292, 1654, 469, 763, 1, 50, 'anxious', 2000, 5000, 0, '道长，能……能算算工作吗？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (293, 1654, 460, 5, 1, 0, 'neutral', 0, 0, 1, '他真的能看出来？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (294, 1654, 461, 5, 1, 50, 'neutral', 1000, 2000, 0, '回家。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (295, 1654, 470, 1, 1, 50, 'neutral', 0, 4000, 0, '你近日是否常感心神不宁，做事多阻，尤其与人交接文书、钱财时易生纰漏？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (296, 1654, 470, 763, 1.1, 50, 'surprised', 4000, 6000, 0, '对对对！上周报错了一个数据，被主管骂得狗血淋头！这个月报销单也莫名其妙少了……', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (297, 1654, 470, 1, 1, 50, 'neutral', 6000, 8000, 0, '伸手。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (298, 1654, 462, 5, 1, 50, 'neutral', 2000, 6000, 0, '林薇，查一下这个人。要最基础的背景信息就行，注意方式，别惊动他。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (299, 1654, 462, 5, 1, 50, 'neutral', 6500, 9000, 0, '另外，明天下午所有安排取消，帮我准备一辆不起眼的车。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (300, 1654, 471, 1, 1, 50, 'neutral', 0, 4000, 0, '此乃‘穿堂煞’与‘青龙受压’之局。久居其中，易致心神涣散，判断失误，且易招小人背后中伤。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (301, 1654, 471, 763, 1.2, 50, 'surprised', 4000, 6000, 0, '那怎么办？能化解吗？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (302, 1654, 471, 1, 1, 50, 'neutral', 6000, 10000, 0, '将此符置于你办公桌右手抽屉内。三日内，若有人主动请你调换座位，或你左手边柜子有变动，便是转机。记住，莫要主动提起此事。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (304, 1654, 463, 1, 1, 0, 'neutral', 0, 0, 1, '师父命我下山历劫，劫起红尘，劫落何方？今日这位柳小姐，面相贵中带煞，牵连甚广。她口中老宅，恐非寻常。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (306, 1654, 464, 1, 1, 0, 'neutral', 0, 0, 1, '坎上艮下，水山蹇。前路艰难，贵人隐现……这贵人，莫非是她？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (307, 1654, 472, 763, 1, 50, 'grateful', 0, 1500, 0, '多少钱？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (308, 1654, 472, 1, 1, 50, 'neutral', 1800, 2200, 0, '随缘。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (309, 1654, 472, 763, 1, 50, 'grateful', 2500, 3500, 0, '谢谢道长，太谢谢您了！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (310, 1654, 473, 5, 1, 0, 'neutral', 0, 0, 1, '清微观……查不到更多信息。但他昨天一眼看出老宅有问题。是真有本事，还是另有所图？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (311, 1654, 474, 5, 1, 0, 'neutral', 0, 5000, 1, '秦彻，男，约22岁，自称终南山清微观弟子，半月前下山，无固定住所，无犯罪记录，社会关系简单。清微观……查不到更多信息。但他昨天一眼看出老宅有问题。是真有本事，还是另有所图？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (312, 1654, 475, 5, 1, 0, 'neutral', 0, 0, 1, '清微观……查不到更多信息。但他昨天一眼看出老宅有问题。是真有本事，还是另有所图？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (313, 1654, 476, 768, 1.2, 50, 'disgusted', 1000, 4000, 0, '现在的小年轻，不好好找工作，装神弄鬼骗钱。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (314, 1654, 478, 763, 1.2, 50, 'surprised', 0, 3000, 0, '道长！神了！我刚回公司，行政部就说空调管道要检修，让我们那片的人暂时搬到靠窗的备用工位！正好我左手边没柜子了！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (315, 1654, 479, 763, 1.3, 60, 'surprised', 0, 4000, 0, '而且……而且我听说，主管好像因为别的事被上面调查了，暂时顾不上我们！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (316, 1654, 479, 766, 1, 55, 'surprised', 4000, 5500, 0, '真的假的？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (317, 1654, 479, 763, 1.2, 65, 'happy', 5500, 8000, 0, '千真万确！道长，太谢谢您了！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (318, 1654, 480, 766, 1, 50, 'surprised', 0, 2000, 0, '真的假的？这么灵？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (319, 1654, 480, 767, 1, 50, 'neutral', 2000, 3500, 0, '看着挺年轻的，没想到有两下子。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (320, 1654, 480, 768, 1, 50, 'disgusted', 3500, 5000, 0, '哼，说不定是托儿呢。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (321, 1654, 481, 763, 1.2, 50, 'happy', 0, 3000, 0, '道长！太谢谢您了！', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (322, 1654, 482, 5, 1, 0, 'neutral', 0, 0, 1, '明天，带他去老宅。是人是鬼，一试便知。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (323, 1654, 483, 5, 1, 50, 'neutral', 1000, 4000, 0, '走吧，去公司。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (324, 1654, 484, 5, 1, 0, 'neutral', 0, 0, 1, '明天，带他去老宅。是人是鬼，一试便知。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (325, 1654, 486, 1, 1, 50, 'neutral', 0, 2000, 0, '你也来了。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (326, 1654, 488, 1, 1, 0, 'neutral', 0, 5000, 1, '山下人心，果然复杂。善意恶意，皆在一念。师父说的‘劫’，或许便是要在这纷繁念想中，找到自己的‘道’。 ', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (327, 1654, 489, 5, 1, 0, 'neutral', 0, 0, 1, '爸，妈……老宅里，到底藏着什么？爷爷守着的，又是什么？这个突然出现的秦彻，会是转机，还是另一个陷阱？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (328, 1654, 490, 764, 1, 50, 'neutral', 2000, 6000, 0, '柳总，您要的关于老宅近三年维修、改造的所有记录，还有进出人员登记（非家族成员部分），已经整理好了。另外，陈伯（老宅管家）说，老爷子今天精神似乎更差了些，一直在念叨……‘钥匙’、‘守不住’之类的呓语。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (329, 1654, 491, 764, 1, 50, 'neutral', 0, 4000, 0, '柳总，您要的关于老宅近三年维修、改造的所有记录，还有进出人员登记（非家族成员部分），已经整理好了。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (330, 1654, 491, 764, 0.9, 50, 'sad', 4000, 8000, 0, '另外，陈伯（老宅管家）说，老爷子今天精神似乎更差了些，一直在念叨……‘钥匙’、‘守不住’之类的呓语。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (331, 1654, 492, 5, 1, 50, 'neutral', 0, 4000, 0, '我知道了。明天下午的安排都空出来。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (332, 1654, 492, 5, 1, 50, 'neutral', 4500, 8000, 0, '还有，帮我准备些现金，用普通信封装好。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (333, 1654, 492, 764, 1, 50, 'neutral', 8500, 10000, 0, '是。柳总，那位秦先生……需要再深入查查吗？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (334, 1654, 493, 764, 1, 50, 'neutral', 0, 1000, 0, '是。', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (335, 1654, 494, 5, 1, 0, 'neutral', 0, 5000, 1, '爸，妈……老宅里，到底藏着什么？爷爷守着的，又是什么？这个突然出现的秦彻，会是转机，还是另一个陷阱？', NULL, NULL);
INSERT INTO `php_plugin_shortplay_drama_storyboard_dialogue` VALUES (336, 1654, 499, 843, 1, 50, 'neutral', 7000, 9000, 0, '下一站，幸福里站，请准备下车的乘客……', NULL, NULL);
